mapper.select:
/* SaleRegistKwuMapper.getMembrKwList */
SELECT  tmm.MEMBR_NO,
        SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM
FROM    TB_MB_MEMBER tmm



        WHERE   tmm.MEMBR_ORGN_CD = #{hqOfficeCd}




            WHERE   tmm.MEMBR_ORGN_CD = #{storeCd}



            WHERE   tmm.MEMBR_ORGN_CD = #{hqOfficeCd}




    AND tmm.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'


    AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'

ORDER BY tmm.MEMBR_NO

/* SaleRegistKwuMapper.getBillNo */
SELECT  LPAD(NVL(MAX(BILL_NO), 9000) + 1, 4, '0')
FROM    TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '99'
AND     BILL_NO > '9000'

/* SaleRegistKwuMapper.getCashAmt */
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '01'
UNION  ALL
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '02'

/* SaleRegistKwuMapper.getHdrInfo */ SELECT NVL(MAX(MEMBR_NO),'') AS MEMBR_NO,
NVL(MAX((SELECT '[' || MEMBR_NO || '] ' || SBPENC.D(tmm.MEMBR_NM) FROM TB_MB_MEMBER tmm WHERE tmm.MEMBR_ORGN_CD = #{storeCd} AND tmm.MEMBR_NO = tsshi.MEMBR_NO)), '') AS MEMBR_NM,
NVL(MAX((SELECT '[' || MEMBR_NO || '] ' || SBPENC.D(tmm.MEMBR_NM) FROM TB_MB_MEMBER tmm WHERE tmm.MEMBR_ORGN_CD = #{hqOfficeCd} AND tmm.MEMBR_NO = tsshi.MEMBR_NO)), '') AS MEMBR_NM,
NVL(MAX(DEPOSIT_AMT), 0) AS DEPOSIT_AMT, NVL(MAX(POSTPAID_AMT), 0) AS POSTPAID_AMT, NVL(MAX(BUSINESS_AMT), 0) AS BUSINESS_AMT, NVL(MAX(TEACHER_AMT), 0) AS TEACHER_AMT, NVL(MAX(TEACHER_CNT), 0) AS TEACHER_CNT, NVL(MAX(TRANSPORT_AMT), 0) AS TRANSPORT_AMT, NVL(MAX(QTY), 0) AS QTY, NVL(MAX(REMAIN_AMT), 0) AS REMAIN_AMT, NVL(MAX(REMARK), '') AS REMARK FROM TB_SL_SALE_HDR_INFO tsshi WHERE STORE_CD = #{storeCd} AND SALE_DATE = #{saleDate} AND POS_NO = #{posNo} AND BILL_NO = #{billNo}

/* SaleRegistKwuMapper.getSaleFg */
SELECT 	SALE_FG
FROM 	TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}

mapper.delete:
/* SaleRegistKwuMapper.delSaleHdr */
DELETE  TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '99'
AND     BILL_NO = #{billNo}
AND     REG_ID like 'WEB_REG_%'

mapper.insert:
/* SaleRegistKwuMapper.getSalePay */ INSERT INTO TB_SL_SALE_PAY ( HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , PAY_CD , PAY_AMT , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , REG_DT , REG_ID , MOD_DT , MOD_ID , REG_SEQ , SALE_YN , RECV_AMT , RTN_AMT ) SELECT HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , #{payCd} , #{payAmt} * #{saleFg} , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , #{regDt} , #{regId} , #{modDt} , #{modId} , REG_SEQ , SALE_YN
, '0' , '0' FROM TB_SL_SALE_PAY_CARD
, RECV_AMT , RTN_AMT FROM TB_SL_SALE_PAY_CASH
WHERE STORE_CD = #{storeCd} AND SALE_DATE = #{saleDate} AND POS_NO = '99' AND BILL_NO = #{billNo}
