mapper.select:
/* DisuseInfoBonavieMapper.getDisuseInfoBonavieList */
SELECT TO_CHAR(TO_DATE(A.DT_IO, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS DT_IO, B.STORE_CD, C.STORE_NM, (CASE WHEN A.CD_COMPANY = 'TEST01' THEN '테스트' WHEN A.CD_COMPANY = '15' THEN '실가동' ELSE A.CD_COMPANY END) AS CD_COMPANY, A.CD_PLANT, A.NO_EGR, A.NO_EGRLINE, A.CD_PJT, A.CD_PARTNER, A.CD_ITEM AS PROD_CD, D.PROD_NM, A.QT_IO, A.UM, A.AM, A.VAT, A.NO_IO, A.NO_IOLINE, (CASE WHEN A.FG_TPIO = '003' THEN '폐기' WHEN A.FG_TPIO = '004' THEN '재고실사(출고)' WHEN A.FG_TPIO = '005' THEN '무상출고' ELSE A.FG_TPIO END) AS FG_TPIO, A.CD_QTIOTP, (CASE WHEN A.FG_STATUS = '0' THEN '정상' WHEN A.FG_STATUS = '1' THEN '삭제' ELSE A.FG_STATUS END) AS FG_STATUS, TO_CHAR(TO_DATE(A.DTS_INSERT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS DTS_INSERT, A.ID_INSERT, TO_CHAR(TO_DATE(A.DTS_UPDATE, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS DTS_UPDATE, A.ID_UPDATE, A.SEND_YN
FROM TB_NEOE_SPOS_EGR A, TB_NEOE_SPOS_STR B, TB_MS_STORE C, TB_MS_PRODUCT D WHERE 1=1 AND C.HQ_OFFICE_CD = #{hqOfficeCd} AND A.DT_IO BETWEEN #{startDate} AND #{endDate}
AND B.STORE_CD = #{storeCd}
AND A.CD_ITEM LIKE '%'||#{prodCd}||'%'
AND D.PROD_NM LIKE '%'||#{prodNm}||'%'
AND B.CD_PARTNER = A.CD_PARTNER AND B.CD_COMPANY = A.CD_COMPANY AND C.STORE_CD = B.STORE_CD AND D.STORE_CD = B.STORE_CD AND D.PROD_CD = A.CD_ITEM ORDER BY A.DT_IO, B.STORE_CD

/* DisuseInfoBonavieMapper.getDisuseInfoBonavieExcelList */ SELECT TO_CHAR(TO_DATE(A.DT_IO, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS DT_IO, B.STORE_CD, C.STORE_NM, (CASE WHEN A.CD_COMPANY = 'TEST01' THEN '테스트' WHEN A.CD_COMPANY = '15' THEN '실가동' ELSE A.CD_COMPANY END) AS CD_COMPANY, A.CD_PLANT, A.NO_EGR, A.NO_EGRLINE, A.CD_PJT, A.CD_PARTNER, A.CD_ITEM AS PROD_CD, D.PROD_NM, A.QT_IO, A.UM, A.AM, A.VAT, A.NO_IO, A.NO_IOLINE, (CASE WHEN A.FG_TPIO = '003' THEN '폐기' WHEN A.FG_TPIO = '004' THEN '재고실사(출고)' WHEN A.FG_TPIO = '005' THEN '무상출고' ELSE A.FG_TPIO END) AS FG_TPIO, A.CD_QTIOTP, (CASE WHEN A.FG_STATUS = '0' THEN '정상' WHEN A.FG_STATUS = '1' THEN '삭제' ELSE A.FG_STATUS END) AS FG_STATUS, TO_CHAR(TO_DATE(A.DTS_INSERT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS DTS_INSERT, A.ID_INSERT, TO_CHAR(TO_DATE(A.DTS_UPDATE, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS DTS_UPDATE, A.ID_UPDATE, A.SEND_YN FROM TB_NEOE_SPOS_EGR A, TB_NEOE_SPOS_STR B, TB_MS_STORE C, TB_MS_PRODUCT D WHERE 1=1 AND C.HQ_OFFICE_CD = #{hqOfficeCd} AND A.DT_IO BETWEEN #{startDate} AND #{endDate}
AND B.STORE_CD = #{storeCd}
AND A.CD_ITEM LIKE '%'||#{prodCd}||'%'
AND D.PROD_NM LIKE '%'||#{prodNm}||'%'
AND B.CD_PARTNER = A.CD_PARTNER AND B.CD_COMPANY = A.CD_COMPANY AND C.STORE_CD = B.STORE_CD AND D.STORE_CD = B.STORE_CD AND D.PROD_CD = A.CD_ITEM ORDER BY A.DT_IO, B.STORE_CD
