mapper.select:
/* USE : IostockCmmMapper.selectStoreList */
SELECT tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tms.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
ORDER BY tms.STORE_CD

/* USE : IostockCmmMapper.selectStoreMomsList */

    SELECT  tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM
    FROM    TB_MS_STORE tms,
             TB_MS_STORE_INFO tmsi
    WHERE   tms.HQ_OFFICE_CD =   #{hqOfficeCd}


        AND (
                'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
            OR   tms.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
            )


        AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'


        AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'


        AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



            -- 매장브랜드 전체일때
            AND tms.HQ_BRAND_CD IN

                #{item}




        AND tmsi.MOMS_TEAM = #{momsTeam}


        AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


        AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


        AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


        AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


        AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


        AND tms.BRANCH_CD = #{branchCd}

    AND tmsi.STORE_CD (+)= tms.STORE_CD
    ORDER BY tms.STORE_CD

/* USE : IostockCmmMapper.selectBrandMomsList */
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT HQ_BRAND_CD AS VALUE, HQ_BRAND_NM AS NAME FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y'
AND HQ_BRAND_CD IN
#{item}
ORDER BY VALUE NULLS FIRST
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT MS_BRAND_CD AS VALUE, MS_BRAND_NM AS NAME FROM TB_MS_BRAND WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ORDER BY VALUE NULLS FIRST

/* USE : VendrOrderMapper.getVendrList */
SELECT thv.VENDR_CD, thv.VENDR_NM , thv.SHIP_FG, thv.VAT_INCLD_YN , thv.USE_YN FROM TB_HQ_VENDOR thv WHERE thv.HQ_OFFICE_CD = #{hqOfficeCd}
SELECT tmv.VENDR_CD, tmv.VENDR_NM , tmv.SHIP_FG, tmv.VAT_INCLD_YN , tmv.USE_YN FROM TB_MS_VENDOR tmv WHERE tmv.STORE_CD = #{storeCd}

/* USE : IostockCmmMapper.selectStorageList */ SELECT STORAGE_CD, STORAGE_NM
FROM TB_HQ_STORAGE WHERE 1 = 1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y' ORDER BY STORAGE_CD
FROM TB_MS_STORAGE WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ORDER BY STORE_CD

/* USE : VolmErrMapper.selectCmmCodeList */
SELECT tcn.NMCODE_GRP_CD , tcn.NMCODE_CD , tcn.PARENT_CD , tcn.NMCODE_NM , tcn.NMCODE_ITEM_1 , tcn.NMCODE_ITEM_2 , tcn.USE_COL_NM , tcn.USE_TARGET_FG , tcn.USE_SYSTEM_FG FROM TB_CM_NMCODE tcn WHERE tcn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND tcn.NMCODE_ITEM_1 = #{nmcodeItem1}
ORDER BY tcn.NMCODE_CD

/* USE : VolmErrMapper.selectHqCodeList */
SELECT thn.HQ_OFFICE_CD , thn.NMCODE_GRP_CD , thn.NMCODE_CD , thn.PARENT_CD , thn.NMCODE_NM , thn.NMCODE_ITEM_1 , thn.NMCODE_ITEM_2 , thn.USE_YN FROM TB_HQ_NMCODE thn WHERE thn.HQ_OFFICE_CD = #{hqOfficeCd} AND thn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND thn.NMCODE_ITEM_1 = #{nmcodeItem1}
ORDER BY thn.NMCODE_CD

/* USE : VolmErrMapper.selectStoreCodeList */
SELECT tmsn.STORE_CD , tmsn.NMCODE_GRP_CD , tmsn.NMCODE_CD , tmsn.PARENT_CD , tmsn.NMCODE_NM , tmsn.NMCODE_ITEM_1 , tmsn.NMCODE_ITEM_2 , tmsn.USE_YN FROM TB_MS_STORE_NMCODE tmsn WHERE tmsn.STORE_CD = #{storeCd} AND tmsn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND tmsn.NMCODE_ITEM_1 = #{nmcodeItem1}
ORDER BY tmsn.NMCODE_CD

/* USE : VolmErrMapper.selectDynamicCodeList */

SELECT  ${selectCd} AS NMCODE_CD
,       ${selectNm} AS NMCODE_NM
FROM    ${selectTable}
WHERE   1 = 1


    AND ${selectWhere}


ORDER BY ${selectCd}

/* IostockCmmMapper.getUserHqNmcodeCdList */
SELECT the.MOMS_TEAM FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_AC_SHOP FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_AREA_FG FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_COMMERCIAL FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_SHOP_TYPE FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_STORE_MANAGE_TYPE FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}

/* IostockCmmMapper.selectHqNmcodeMomsList */
SELECT thn.NMCODE_CD AS VALUE, thn.NMCODE_NM AS NAME FROM TB_HQ_NMCODE thn WHERE 1=1 AND thn.HQ_OFFICE_CD = #{hqOfficeCd} AND thn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND thn.NMCODE_CD = #{userHqNmcodeCd} ORDER BY thn.NMCODE_CD
UNION SELECT '' AS VALUE, '전체' AS NAME FROM DUAL ORDER BY VALUE NULLS FIRST

/* IostockCmmMapper.getUserBranchCdList */
SELECT
the.BRANCH_CD
FROM TB_HQ_EMPLOYEE the
WHERE 1=1
AND the.USER_ID = #{userId}

/* IostockCmmMapper.selectBranchMomsList */
SELECT thbm.BRANCH_CD AS VALUE, thbm.BRANCH_NM AS NAME FROM TB_HQ_BRANCH_MOMS thbm WHERE 1=1 AND thbm.HQ_OFFICE_CD = #{hqOfficeCd}
AND thbm.BRANCH_CD = #{userHqNmcodeCd} ORDER BY thbm.BRANCH_CD
UNION SELECT '' AS VALUE, '전체' AS NAME FROM DUAL ORDER BY VALUE NULLS FIRST

/* USE : IostockCmmMapper.selectPromotionList */
SELECT  h.PROMOTION_CD
,       h.PROMOTION_NM
,       TO_CHAR(TO_DATE(c.DEPOSIT_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS DEPOSIT_YMD



    FROM    TB_HQ_PROMO_H h
    ,       TB_HQ_PROMO_CONDI c
    WHERE   h.HQ_OFFICE_CD =   #{hqOfficeCd}
    AND     h.HQ_OFFICE_CD = c.HQ_OFFICE_CD



    FROM    TB_MS_PROMO_H h
    ,       TB_MS_PROMO_CONDI c
    WHERE   h.STORE_CD =   #{storeCd}
    AND     h.STORE_CD = c.STORE_CD


AND     h.PROMOTION_CD = c.PROMOTION_CD

    AND h.PROMOTION_CD = #{promotionCd}


    AND h.PROMOTION_NM = #{promotionNm}

ORDER BY h.PROMOTION_CD
