mapper.update:
/* USE : ExcelUploadMapper.updateExcelUploadProdCd */



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.PROD_CD  = ( SELECT  thp.PROD_CD
                                  FROM    TB_HQ_PRODUCT thp
                                  WHERE   thp.HQ_OFFICE_CD  = #{hqOfficeCd}
                                  AND     thp.PROD_CD       = tpte.PROD_BARCD_CD
                                )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NULL



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.PROD_CD  = ( SELECT  tmp.PROD_CD
                                  FROM    TB_MS_PRODUCT tmp
                                  WHERE   tmp.STORE_CD  = #{storeCd}
                                  AND     tmp.PROD_CD   = tpte.PROD_BARCD_CD
                                )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NULL

/* USE : ExcelUploadMapper.updateExcelUploadBarcdCd */



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.PROD_CD  = ( SELECT  thpb.PROD_CD
                                  FROM    TB_HQ_PRODUCT_BARCD thpb
                                  WHERE   thpb.HQ_OFFICE_CD  = #{hqOfficeCd}
                                  AND     thpb.BARCD_CD      = tpte.PROD_BARCD_CD
                                )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NULL



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.PROD_CD  = ( SELECT  tmpb.PROD_CD
                                  FROM    TB_MS_PRODUCT_BARCD tmpb
                                  WHERE   tmpb.STORE_CD = #{storeCd}
                                  AND     tmpb.BARCD_CD = tpte.PROD_BARCD_CD
                                )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NULL

/* USE : ExcelUploadMapper.updateExcelUploadUprc */



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.UPRC  = ( SELECT  thp.LAST_COST_UPRC
                               FROM    TB_HQ_PRODUCT thp
                               WHERE   thp.HQ_OFFICE_CD = #{hqOfficeCd}
                               AND     thp.PROD_CD      = tpte.PROD_BARCD_CD
                             )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NOT NULL
        AND     tpte.UPRC       IS NULL



        UPDATE  TB_PO_TEMP_EXCEL tpte
        SET     tpte.UPRC  =  ( SELECT  tmp.LAST_COST_UPRC
                                FROM    TB_MS_PRODUCT tmp
                                WHERE   tmp.STORE_CD  = #{storeCd}
                                AND     tmp.PROD_CD   = tpte.PROD_BARCD_CD
                              )
        WHERE   tpte.SESSION_ID = #{sessionId}
        AND     tpte.PROD_CD    IS NOT NULL
        AND     tpte.UPRC       IS NULL

mapper.delete:
/* USE : ExcelUploadMapper.deleteExcelUpload */
DELETE  TB_PO_TEMP_EXCEL
WHERE   SESSION_ID    = #{sessionId}

mapper.select:
/* USE : ExcelUploadMapper.getExcelUploadErrInfoList */
SELECT DECODE(tpte.PROD_CD, NULL, '0', '1') AS PROD_STATUS , tpte.PROD_BARCD_CD , tpte.PROD_CD , tpte.BARCD_CD , tpte.UPRC , tpte.UNIT_QTY , tpte.ETC_QTY , tpte.QTY , tpte.REMARK , thp.STOCK_PROD_YN , thp.PO_PROD_FG , thp.USE_YN FROM TB_PO_TEMP_EXCEL tpte , TB_HQ_PRODUCT thp WHERE tpte.SESSION_ID = #{sessionId} AND tpte.HQ_OFFICE_CD = #{hqOfficeCd} AND tpte.PROD_BARCD_CD IS NOT NULL AND thp.HQ_OFFICE_CD(+)= tpte.HQ_OFFICE_CD AND thp.PROD_CD (+)= tpte.PROD_CD ORDER BY tpte.PROD_BARCD_CD, tpte.PROD_CD
SELECT DECODE(tpte.PROD_CD, NULL, '0', '1') AS PROD_STATUS , tpte.PROD_BARCD_CD , tpte.PROD_CD , tpte.BARCD_CD , tpte.UPRC , tpte.UNIT_QTY , tpte.ETC_QTY , tpte.QTY , tpte.REMARK , tmp.STOCK_PROD_YN , tmp.PO_PROD_FG , tmp.USE_YN FROM TB_PO_TEMP_EXCEL tpte , TB_MS_PRODUCT tmp WHERE tpte.SESSION_ID = #{sessionId} AND tpte.STORE_CD = #{storeCd} AND tpte.PROD_BARCD_CD IS NOT NULL AND tmp.STORE_CD (+)= tpte.STORE_CD AND tmp.PROD_CD (+)= tpte.PROD_CD ORDER BY tpte.PROD_BARCD_CD, tpte.PROD_CD
