mapper.select:
/* USE : ProdFrnchsMapper.getProdFrnchsList */
SELECT A.PROD_CD , Z.PROD_NM
, (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE NMCODE_GRP_CD = '093' AND HQ_OFFICE_CD = A.HQ_OFFICE_CD AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM
, (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE NMCODE_GRP_CD = '093' AND STORE_CD = A.STORE_CD AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM
, A.PO_UNIT_QTY , COUNT(*) AS OUT_CNT , SUM(A.OUT_TOT_QTY) AS OUT_TOT_QTY , SUM(A.OUT_TOT) AS OUT_TOT , SUM(A.IN_TOT_QTY) AS IN_TOT_QTY , SUM(A.IN_TOT) AS IN_TOT , SUM(A.PENALTY_AMT) AS PENALTY_AMT , NVL((SELECT VENDR_NM FROM TB_HQ_VENDOR thv WHERE thv.HQ_OFFICE_CD = #{hqOfficeCd} AND thv.VENDR_CD = B.VENDR_CD ), '본사') AS VENDR
FROM TB_PO_HQ_STORE_OUTSTOCK_DTL A , TB_MS_PRODUCT Z , TB_PO_HQ_STORE_OUTSTOCK B WHERE A.STORE_CD = Z.STORE_CD AND A.PROD_CD = Z.PROD_CD AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.STORE_CD = A.STORE_CD AND B.OUT_DATE = A.REQ_DATE AND B.SLIP_NO = A.SLIP_NO
AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD = #{storeCd}
AND A.STORE_CD IN
#{item}
AND Z.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND Z.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND Z.PROD_CD = #{prodCd}
AND Z.PROD_NM = #{prodNm}
AND B.VENDR_CD = #{vendrCd}
AND B.VENDR_CD IS NULL
AND EXISTS (SELECT * FROM TB_PO_HQ_STORE_OUTSTOCK M WHERE M.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND M.SLIP_NO = A.SLIP_NO
AND M.OUT_DATE >= #{startDate}
AND M.OUT_DATE <= #{endDate}
) GROUP BY A.PROD_CD , Z.PROD_NM , A.PO_UNIT_FG , A.PO_UNIT_QTY , B.VENDR_CD
, A.HQ_OFFICE_CD
, A.STORE_CD
ORDER BY A.PROD_CD

/* USE : ProdFrnchsMapper.getProdInOutstockInfoList */ SELECT TO_CHAR(TO_DATE(TPHSO.OUT_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS OUT_DT , TO_CHAR(TO_DATE(TPHSO.IN_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS IN_DT , TMS.STORE_NM , DECODE(TPHSOD.SLIP_FG,1,'출고',-1,'반품') AS SLIP_FG_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '114' AND NMCODE_CD = TPHSO.SLIP_KIND) AS SLIP_KIND_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '113' AND NMCODE_CD = TPHSOD.PROC_FG) AS PROC_FG_NM , TMP.SPLY_UPRC , TPHSOD.OUT_TOT_QTY , TPHSOD.OUT_AMT , TPHSOD.OUT_VAT , TPHSOD.OUT_TOT , TPHSOD.IN_TOT_QTY , TPHSOD.IN_AMT , TPHSOD.IN_VAT , TPHSOD.IN_TOT , TPHSOD.PENALTY_AMT , TPHSOD.REMARK FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_MS_PRODUCT TMP , TB_MS_STORE TMS WHERE TPHSOD.PROD_CD = #{prodCd}
AND TPHSOD.HQ_OFFICE_CD = #{hqOfficeCd}
AND TPHSOD.STORE_CD IN
#{item}
AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO AND TPHSOD.STORE_CD = TMP.STORE_CD AND TPHSOD.PROD_CD = TMP.PROD_CD AND TPHSOD.STORE_CD = TMS.STORE_CD
AND TPHSO.OUT_DATE >= #{startDate}
AND TPHSO.OUT_DATE <= #{endDate}
AND TPHSO.VENDR_CD = #{vendrCd}
AND TPHSO.VENDR_CD IS NULL
ORDER BY TPHSO.OUT_DT, TPHSOD.STORE_CD

/* USE : ProdFrnchsMapper.getProdFrnchsExcelList */ SELECT A.PROD_CD , Z.PROD_NM
, (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE NMCODE_GRP_CD = '093' AND HQ_OFFICE_CD = A.HQ_OFFICE_CD AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM
, (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE NMCODE_GRP_CD = '093' AND STORE_CD = A.STORE_CD AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM
, A.PO_UNIT_QTY , COUNT(*) AS OUT_CNT , SUM(A.OUT_TOT_QTY) AS OUT_TOT_QTY , SUM(A.OUT_TOT) AS OUT_TOT , SUM(A.IN_TOT_QTY) AS IN_TOT_QTY , SUM(A.IN_TOT) AS IN_TOT , SUM(A.PENALTY_AMT) AS PENALTY_AMT , NVL((SELECT VENDR_NM FROM TB_HQ_VENDOR thv WHERE thv.HQ_OFFICE_CD = #{hqOfficeCd} AND thv.VENDR_CD = B.VENDR_CD ), '본사') AS VENDR FROM TB_PO_HQ_STORE_OUTSTOCK_DTL A , TB_MS_PRODUCT Z , TB_PO_HQ_STORE_OUTSTOCK B WHERE A.STORE_CD = Z.STORE_CD AND A.PROD_CD = Z.PROD_CD AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.STORE_CD = A.STORE_CD AND B.OUT_DATE = A.REQ_DATE AND B.SLIP_NO = A.SLIP_NO
AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD = #{storeCd}
AND A.STORE_CD IN
#{item}
AND Z.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND Z.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND Z.PROD_CD = #{prodCd}
AND Z.PROD_NM = #{prodNm}
AND B.VENDR_CD = #{vendrCd}
AND B.VENDR_CD IS NULL
AND EXISTS (SELECT * FROM TB_PO_HQ_STORE_OUTSTOCK M WHERE M.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND M.SLIP_NO = A.SLIP_NO
AND M.OUT_DATE >= #{startDate}
AND M.OUT_DATE <= #{endDate}
) GROUP BY A.PROD_CD , Z.PROD_NM , A.PO_UNIT_FG , A.PO_UNIT_QTY , B.VENDR_CD
, A.HQ_OFFICE_CD
, A.STORE_CD
ORDER BY A.PROD_CD
