mapper.select:
SELECT A.SLIP_NO , A.STORE_CD , B.STORE_NM , DECODE(A.SLIP_FG,1,'출고',-1,'반품') AS SLIP_FG_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '114' AND NMCODE_CD = A.SLIP_KIND) AS SLIP_KIND_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '113' AND NMCODE_CD = A.PROC_FG) AS PROC_FG_NM , TO_CHAR(TO_DATE(A.OUT_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS OUT_DT , TO_CHAR(TO_DATE(A.IN_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS IN_DT , A.DTL_CNT , A.MD_TOT_QTY , A.MD_TOT , A.OUT_TOT_QTY , A.OUT_TOT , A.IN_TOT_QTY , A.IN_TOT , (SELECT SUM(PENALTY_AMT) FROM TB_PO_HQ_STORE_OUTSTOCK_DTL Z WHERE Z.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND Z.SLIP_NO = A.SLIP_NO) AS PENALTY_AMT
FROM TB_PO_HQ_STORE_OUTSTOCK A , TB_MS_STORE B WHERE A.STORE_CD = B.STORE_CD
AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.OUT_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN
#{item}
AND A.SLIP_FG = #{slipFg}
AND A.SLIP_KIND = #{slipKind}
AND A.PROC_FG = #{procFg}
AND EXISTS (SELECT * FROM TB_PO_HQ_STORE_OUTSTOCK_DTL M , TB_MS_PRODUCT Z WHERE M.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND M.SLIP_NO = A.SLIP_NO AND M.STORE_CD = Z.STORE_CD AND M.PROD_CD = Z.PROD_CD AND Z.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} ) )
AND EXISTS (SELECT * FROM TB_PO_HQ_STORE_OUTSTOCK_DTL M WHERE M.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND M.SLIP_NO = A.SLIP_NO AND M.PROD_CD = #{prodCd} )
AND EXISTS (SELECT * FROM TB_PO_HQ_STORE_OUTSTOCK_DTL M , TB_MS_PRODUCT Z WHERE M.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND M.SLIP_NO = A.SLIP_NO AND M.STORE_CD = Z.STORE_CD AND M.PROD_CD = Z.PROD_CD AND Z.PROD_NM LIKE '%' || #{prodNm} || '%' )
