mapper.select:
/* USE : FrnchsStoreMapper.getFrnchsStoreList */
SELECT TPHSO.STORE_CD , TMS.STORE_NM , COUNT(*) SLIP_CNT , SUM(TPHSO.ORDER_TOT) AS ORDER_TOT , SUM(TPHSO.MD_TOT) AS MD_TOT , SUM(TPHSO.OUT_TOT) AS OUT_TOT , SUM(TPHSO.IN_TOT) AS IN_TOT , SUM(TPHSO.OUT_AMT) AS OUT_AMT , SUM(TPHSO.OUT_VAT) AS OUT_VAT , TMS.BIZ_NO , TMS.BIZ_STORE_NM , TMS.POST_NO , TMS.ADDR || TMS.ADDR_DTL AS ADDR , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '057' AND NMCODE_CD = TMS.STORE_TYPE) AS STORE_TYPE_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '001' AND NMCODE_CD = TMS.CLS_FG) AS CLS_FG_NM , TMS.EMAIL_ADDR
FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_MS_STORE TMS WHERE TPHSO.STORE_CD = TMS.STORE_CD AND TPHSO.HQ_OFFICE_CD = #{hqOfficeCd}
AND TPHSO.OUT_DATE >= #{startDate}
AND TPHSO.OUT_DATE <= #{endDate}
AND TPHSO.SLIP_FG = #{slipFg}
AND TPHSO.STORE_CD IN
#{item}
AND TMS.STORE_NM LIKE '%'||#{storeNm}||'%'
GROUP BY TPHSO.STORE_CD , TMS.STORE_NM , TMS.BIZ_NO , TMS.BIZ_STORE_NM , TMS.POST_NO , TMS.ADDR || TMS.ADDR_DTL , TMS.STORE_TYPE , TMS.CLS_FG , TMS.EMAIL_ADDR ORDER BY TPHSO.STORE_CD
