mapper.select:
/* USE : UnusualMapper.getUnusualList */
SELECT TPHSOD.SLIP_NO , TPHSOD.STORE_CD , TPHSOD.SLIP_FG , DECODE(TPHSOD.SLIP_FG,1,'주문',-1,'반품') AS SLIP_FG_NM /* 구분 */ , (SELECT STORE_NM FROM TB_MS_STORE WHERE TPHSOD.STORE_CD = TB_MS_STORE.STORE_CD) AS STORE_NM , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '113' AND NMCODE_CD = TPHSOD.PROC_FG) AS PROC_FG_NM , TO_CHAR(TO_DATE(TPHSO.OUT_DT, 'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS OUT_OT , TO_CHAR(TO_DATE(TPHSO.IN_DT, 'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS IN_OT , TPHSOD.PROD_CD , TMP.PROD_NM , TPHSOD.OUT_TOT_QTY , TPHSOD.OUT_TOT , TPHSOD.IN_TOT_QTY , TPHSOD.IN_TOT , TPHSOD.REMARK
FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_MS_PRODUCT TMP WHERE TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO AND TPHSOD.STORE_CD = TMP.STORE_CD AND TPHSOD.PROD_CD = TMP.PROD_CD AND TPHSO.HQ_OFFICE_CD = #{hqOfficeCd}
AND TPHSO.SLIP_NO = #{slipNo}
AND TPHSO.OUT_DATE >= #{startDate}
AND TPHSO.OUT_DATE <= #{endDate}
AND TPHSO.IN_DATE >= #{startDate}
AND TPHSO.IN_DATE <= #{endDate}
AND TPHSO.STORE_CD IN
#{item}
AND TPHSO.STORE_CD = #{storeCd}
AND TPHSOD.SLIP_FG = #{slipFg}*1

