/* USE : StoreLoanInfoMapper.getStoreLoanInfoList */
SELECT B.STORE_CD, tms.STORE_NM, B.LOAN_DATE, B.SLIP_NO, B.OUT_AMT, B.IN_AMT, B.REMARK, tphsl.LIMIT_LOAN_AMT, tphsl.CURR_LOAN_AMT FROM TB_PO_HQ_STORE_LOAN tphsl , ( SELECT STORE_CD, '조회기간 후' AS LOAN_DATE, '' AS SLIP_NO, SUM(OUT_AMT) AS OUT_AMT, SUM(IN_AMT) AS IN_AMT, '' AS REMARK, '1' AS ORDER_RANK FROM ( SELECT STORE_CD, OUT_DATE LOAN_DATE, SLIP_NO, OUT_TOT AS OUT_AMT, 0 AS IN_AMT, REMARK FROM TB_PO_HQ_STORE_OUTSTOCK WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND OUT_DATE > #{endDate} AND SLIP_FG = 1 AND PROC_FG IN ('20', '30') UNION ALL SELECT tphso.STORE_CD, tphso.IN_DATE LOAN_DATE, tphso.SLIP_NO, (tphso.IN_TOT + SUM(tphsod.PENALTY_AMT)) AS OUT_AMT, 0 AS IN_AMT, tphso.REMARK FROM TB_PO_HQ_STORE_OUTSTOCK tphso , TB_PO_HQ_STORE_OUTSTOCK_DTL tphsod WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd} AND tphso.IN_DATE > #{startDate} AND tphso.SLIP_FG = -1 AND tphso.PROC_FG = '30' AND tphsod.HQ_OFFICE_CD = tphso.HQ_OFFICE_CD AND tphsod.SLIP_NO = tphso.SLIP_NO GROUP BY tphso.STORE_CD, tphso.IN_DATE, tphso.SLIP_NO, tphso.IN_TOT, tphso.REMARK ) GROUP BY STORE_CD HAVING COUNT(*) > 0 UNION ALL SELECT STORE_CD, LOAN_DATE, SLIP_NO, OUT_AMT, IN_AMT, REMARK, '2' AS ORDER_RANK FROM ( SELECT STORE_CD, OUT_DATE AS LOAN_DATE, SLIP_NO, OUT_TOT AS OUT_AMT, 0 AS IN_AMT, REMARK FROM TB_PO_HQ_STORE_OUTSTOCK WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND OUT_DATE BETWEEN #{startDate} AND #{endDate} AND SLIP_FG = 1 AND PROC_FG IN ('20', '30') UNION ALL SELECT tphso.STORE_CD, tphso.IN_DATE AS LOAN_DATE, tphso.SLIP_NO, (tphso.IN_TOT + SUM(tphsod.PENALTY_AMT)) AS OUT_AMT, 0 AS IN_AMT, tphso.REMARK FROM TB_PO_HQ_STORE_OUTSTOCK tphso , TB_PO_HQ_STORE_OUTSTOCK_DTL tphsod WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd} AND tphso.IN_DATE BETWEEN #{startDate} AND #{endDate} AND tphso.SLIP_FG = -1 AND tphso.PROC_FG = '30' AND tphsod.HQ_OFFICE_CD = tphso.HQ_OFFICE_CD AND tphsod.SLIP_NO = tphso.SLIP_NO GROUP BY tphso.STORE_CD, tphso.IN_DATE, tphso.SLIP_NO, tphso.IN_TOT, tphso.REMARK ) UNION ALL SELECT STORE_CD, '조회기간 전' AS LOAN_DATE, '' AS SLIP_NO, SUM(OUT_AMT) AS OUT_AMT, SUM(IN_AMT) AS IN_AMT, '' AS REMARK, '3' AS ORDER_RANK FROM ( SELECT STORE_CD, OUT_DATE AS LOAN_DATE, SLIP_NO, OUT_TOT AS OUT_AMT, 0 AS IN_AMT, REMARK FROM TB_PO_HQ_STORE_OUTSTOCK WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND OUT_DATE < #{startDate} AND SLIP_FG = 1 AND PROC_FG IN ('20', '30') UNION ALL SELECT tphso.STORE_CD, tphso.IN_DATE AS LOAN_DATE, tphso.SLIP_NO, (tphso.IN_TOT + SUM(tphsod.PENALTY_AMT)) AS OUT_AMT, 0 AS IN_AMT, tphso.REMARK FROM TB_PO_HQ_STORE_OUTSTOCK tphso , TB_PO_HQ_STORE_OUTSTOCK_DTL tphsod WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd} AND tphso.IN_DATE < #{startDate} AND tphso.SLIP_FG = -1 AND tphso.PROC_FG = '30' AND tphsod.HQ_OFFICE_CD = tphso.HQ_OFFICE_CD AND tphsod.SLIP_NO = tphso.SLIP_NO GROUP BY tphso.STORE_CD, tphso.IN_DATE, tphso.SLIP_NO, tphso.IN_TOT, tphso.REMARK ) GROUP BY STORE_CD HAVING COUNT(*) > 0 ) B , TB_MS_STORE tms WHERE B.STORE_CD = tphsl.STORE_CD AND tms.STORE_CD = tphsl.STORE_CD
AND tms.STORE_CD IN
#{item}
ORDER BY B.STORE_CD, B.ORDER_RANK, B.LOAN_DATE DESC, B.SLIP_NO DESC
