mapper.select:
/* USE : StoreLoanManageMapper.getStoreLoanList */
SELECT tms.STORE_CD, tms.STORE_NM , NVL(tphsl.LIMIT_LOAN_AMT, '') AS LIMIT_LOAN_AMT, tphsl.USE_LOAN_AMT, tphsl.CURR_LOAN_AMT , NVL(tphsl.MAX_ORDER_AMT, '') AS MAX_ORDER_AMT, NVL(tphsl.ORDER_FG, '1') AS ORDER_FG, NVL(tphsl.NO_OUTSTOCK_AMT_FG, 'N') AS NO_OUTSTOCK_AMT_FG , NVL(tphsl.ORDER_CLOSE_YN, 'N') AS ORDER_CLOSE_YN , DECODE(tphsl.ORDER_FG ,'1' ,DECODE(SIGN(tphsl.CURR_LOAN_AMT) ,1 ,DECODE(tphsl.MAX_ORDER_AMT ,NULL ,tphsl.CURR_LOAN_AMT ,DECODE(SIGN(tphsl.CURR_LOAN_AMT-tphsl.MAX_ORDER_AMT) ,1 ,tphsl.MAX_ORDER_AMT ,tphsl.CURR_LOAN_AMT ) ) ,0 ) ,'2' ,DECODE(SIGN(tphsl.CURR_LOAN_AMT) ,1 ,DECODE(tphsl.MAX_ORDER_AMT ,NULL ,tphsl.LIMIT_LOAN_AMT+tphsl.CURR_LOAN_AMT ,DECODE(SIGN(tphsl.LIMIT_LOAN_AMT+tphsl.CURR_LOAN_AMT-tphsl.MAX_ORDER_AMT) ,1 ,tphsl.MAX_ORDER_AMT ,tphsl.LIMIT_LOAN_AMT+tphsl.CURR_LOAN_AMT ) ) ,0 ) ,'3' ,DECODE(SIGN(tphsl.CURR_LOAN_AMT) ,1 ,tphsl.MAX_ORDER_AMT ,0 ) ) AVAILABLE_ORDER_AMT , NVL(tphsl.REMARK, '') AS REMARK
FROM TB_MS_STORE tms , TB_PO_HQ_STORE_LOAN tphsl WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsl.STORE_CD (+) = tms.STORE_CD
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tms.STORE_CD

mapper.update:
/* USE : StoreLoanManageMapper.updateStoreLoanManage */
UPDATE  TB_PO_HQ_STORE_LOAN
SET     LIMIT_LOAN_AMT        = #{limitLoanAmt},
        CURR_LOAN_AMT         = #{limitLoanAmt} - USE_LOAN_AMT,
        MAX_ORDER_AMT         = #{maxOrderAmt},
        ORDER_FG              = #{orderFg},
        NO_OUTSTOCK_AMT_FG    = #{noOutstockAmtFg},
        ORDER_CLOSE_YN        = #{orderCloseYn},
        REMARK                = #{remark},
        MOD_DT                = #{modDt},
        MOD_ID                = #{modId}
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND     STORE_CD     = #{storeCd}
