mapper.select:
/* VirtualAccountMapper.getVirtualAccountList */ SELECT thvd.STORE_CD, tms.STORE_NM, thvd.REQ_DATE, thvd.REQ_SEQ, thvd.REQ_VA_MNY AS VA_MNY, thvd.REQ_ORDR_IDXX AS ORDR_IDXX, thvd.REQ_GOOD_NAME AS GOOD_NAME, (CASE WHEN thvd.REQ_VA_RECEIPT_GUBN IS NULL THEN 'N' ELSE 'Y' END) AS RECEIPT_YN, thvd.REQ_VA_TAXNO AS VA_TAXNO, thvd.RES_BANKNAME AS BANKNAME, thvd.RES_DEPOSITOR AS DEPOSITOR, thvd.RES_ACCOUNT AS ACCOUNT, thvd.DEPOSIT_FG, thvd.RES_ORDER_NO AS ORDER_NO FROM TB_HQ_VACCNT_DEPOSIT thvd, TB_MS_STORE tms WHERE 1=1 AND thvd.HQ_OFFICE_CD = #{hqOfficeCd} AND thvd.REQ_DATE BETWEEN #{startDate} AND #{endDate}
AND thvd.DEPOSIT_FG = #{depositFg}
AND thvd.STORE_CD LIKE '%' || #{storeCd} || '%'
AND tms.STORE_NM LIKE '%' || #{storeNm} || '%'
AND tms.STORE_CD = thvd.STORE_CD ORDER BY thvd.STORE_CD, tms.STORE_NM, thvd.REQ_DATE, thvd.REQ_SEQ

/* VirtualAccountMapper.getVirtualAccountReqSeq */
SELECT (NVL(MAX(REQ_SEQ), 0) +1) AS REQ_SEQ
FROM TB_HQ_VACCNT_DEPOSIT
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND REQ_DATE = #{reqDate}
