mapper.select:
/* USE : DstbCloseStoreMapper.getDstbCloseStoreList */
SELECT tphsd.REQ_DATE, tphsd.SLIP_FG, tphsd.PROC_FG, tphsd.STORE_CD, tms.STORE_NM , SUM(DECODE(tphsd.ORDER_TOT_QTY,0,0,1)) AS DTL_CNT_ORDER , SUM(DECODE(tphsd.ORDER_TOT_QTY,0,1,0)) AS DTL_CNT_ADD , COUNT(*) AS DTL_CNT_TOT , SUM(tphsd.ORDER_AMT*tphsd.SLIP_FG) AS ORDER_AMT , SUM(tphsd.ORDER_VAT*tphsd.SLIP_FG) AS ORDER_VAT , SUM(tphsd.ORDER_TOT*tphsd.SLIP_FG) AS ORDER_TOT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_UNIT_QTY,tphsd.MGR_UNIT_QTY)*tphsd.SLIP_FG) AS MGR_UNIT_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_ETC_QTY ,tphsd.MGR_ETC_QTY) *tphsd.SLIP_FG) AS MGR_ETC_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_TOT_QTY ,tphsd.MGR_TOT_QTY) *tphsd.SLIP_FG) AS MGR_TOT_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_AMT ,tphsd.MGR_AMT) *tphsd.SLIP_FG) AS MGR_AMT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_VAT ,tphsd.MGR_VAT) *tphsd.SLIP_FG) AS MGR_VAT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_TOT ,tphsd.MGR_TOT) *tphsd.SLIP_FG) AS MGR_TOT , MAX(tphsd.MOD_DT) AS MOD_DT FROM TB_PO_HQ_STORE_DISTRIBUTE tphsd , TB_MS_STORE tms WHERE tphsd.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsd.SLIP_FG = #{slipFg} AND tms.STORE_CD = tphsd.STORE_CD
AND tphsd.REQ_DATE BETWEEN #{startDate} AND #{endDate}
AND tphsd.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tphsd.MOD_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tphsd.PROC_FG = #{procFg}
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
GROUP BY tphsd.REQ_DATE, tphsd.SLIP_FG, tphsd.PROC_FG , tphsd.STORE_CD, tms.STORE_NM ORDER BY tphsd.REQ_DATE, tphsd.STORE_CD, tphsd.PROC_FG

mapper.update:
/* USE : DstbCloseStoreMapper.updateDstbCloseConfirm */
