SET     PROC_FG         = #{updateProcFg},
        MGR_DT          = #{modDt},
        MGR_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     REQ_DATE      = #{reqDate}
AND     SLIP_FG       = #{slipFg}
AND     STORE_CD      = #{storeCd}
AND     PROC_FG       = #{procFg}

/* USE : DstbCloseStoreMapper.updateDstbCloseDtl */
