INSERT INTO TB_PO_HQ_STORE_DISTRIBUTE ( HQ_OFFICE_CD, REQ_DATE, SEQ, STORE_CD, EMP_NO, SLIP_FG, DSTB_FG, STORAGE_CD, HQ_BRAND_CD, PROD_CD, PO_UNIT_FG, PO_UNIT_QTY, MGR_SPLY_UPRC, MGR_UNIT_QTY, MGR_ETC_QTY, MGR_TOT_QTY, MGR_AMT, MGR_VAT, MGR_TOT, PROC_FG, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{reqDate}, #{seq}, #{storeCd}, #{empNo}, #{slipFg}, #{dstbFg}, #{storageCd}, #{hqBrandCd}, #{prodCd}, #{poUnitFg}, #{poUnitQty}, #{mgrSplyUprc}, #{mgrUnitQty}, #{mgrEtcQty}, #{mgrTotQty}, #{mgrAmt}, #{mgrVat}, #{mgrTot}, #{procFg}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )

