mapper.select:
/* USE : InstockConfmMapper.getInstockConfmList */
SELECT tphso.SLIP_NO, tphso.VENDR_CD, tphso.SLIP_FG, tphso.SLIP_KIND, tphso.PROC_FG, tphso.STORE_CD, tms.STORE_NM , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_AMT*tphso.SLIP_FG AS ORDER_AMT , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_VAT*tphso.SLIP_FG AS ORDER_VAT , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_TOT*tphso.SLIP_FG AS ORDER_TOT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_AMT *tphso.SLIP_FG AS MGR_AMT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_VAT *tphso.SLIP_FG AS MGR_VAT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_TOT *tphso.SLIP_FG AS MGR_TOT , (CASE WHEN tphso.PROC_FG < '20' THEN tphso.OUT_DATE ELSE NULL END) AS REQ_DATE , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_DATE ELSE NULL END) AS OUT_DATE , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_TOT_QTY*tphso.SLIP_FG ELSE NULL END) AS OUT_TOT_QTY , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_AMT*tphso.SLIP_FG ELSE NULL END) AS OUT_AMT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_VAT*tphso.SLIP_FG ELSE NULL END) AS OUT_VAT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_TOT*tphso.SLIP_FG ELSE NULL END) AS OUT_TOT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_DATE ELSE NULL END) AS IN_DATE , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_TOT_QTY *tphso.SLIP_FG ELSE NULL END) AS IN_TOT_QTY , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_AMT *tphso.SLIP_FG ELSE NULL END) AS IN_AMT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_VAT *tphso.SLIP_FG ELSE NULL END) AS IN_VAT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_TOT *tphso.SLIP_FG ELSE NULL END) AS IN_TOT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_DT ELSE NULL END) AS OUT_DT , (CASE WHEN tphso.PROC_FG >= '20' THEN userInfo.EMP_NM ELSE NULL END) AS OUT_NM , tphso.REMARK, tphso.HQ_REMARK, tphso.DLVR_CD
FROM TB_PO_HQ_STORE_OUTSTOCK tphso , TB_MS_STORE tms , ( SELECT tme.USER_ID, tme.EMP_NM FROM TB_MS_STORE tms , TB_MS_EMPLOYEE tme WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tme.STORE_CD = tms.STORE_CD UNION SELECT the.USER_ID, the.EMP_NM FROM TB_HQ_EMPLOYEE the WHERE the.HQ_OFFICE_CD = #{hqOfficeCd} ) userInfo WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd}
AND tphso.STORE_CD = #{storeCd}
AND tphso.OUT_DATE BETWEEN #{startDate} AND #{endDate} AND tms.STORE_CD = tphso.STORE_CD AND userInfo.USER_ID (+) = tphso.MOD_ID
AND tphso.SLIP_FG = #{slipFg}
AND tphso.SLIP_KIND = #{slipKind}
AND tphso.PROC_FG = #{procFg}
AND tphso.VENDR_CD = #{vendrCd}
AND tphso.DLVR_CD = #{dlvrCd}
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tphso.SLIP_NO

/* USE : InstockConfmMapper.getInstockConfmDtlList */
SELECT A.SLIP_NO, A.SLIP_FG, A.SLIP_KIND, A.PROD_CD, A.PROD_NM, A.SEQ, A.PO_UNIT_FG, A.PO_UNIT_QTY, A.PO_UNIT_ALLOW_FG, A.VAT_FG01, A.ORDER_SPLY_UPRC, A.ORDER_UNIT_QTY, A.ORDER_ETC_QTY, A.ORDER_AMT, A.ORDER_VAT, A.ORDER_TOT, A.MGR_SPLY_UPRC, A.MGR_UNIT_QTY, A.MGR_ETC_QTY, A.MGR_AMT, A.MGR_VAT, A.MGR_TOT, A.OUT_SPLY_UPRC, A.OUT_UNIT_QTY, A.OUT_ETC_QTY, A.OUT_TOT_QTY, A.OUT_AMT, A.OUT_VAT, A.OUT_TOT, A.IN_SPLY_UPRC, A.IN_UNIT_QTY, A.IN_ETC_QTY, A.IN_AMT, A.IN_VAT, A.IN_TOT, A.REMARK, A.ENVST0011, A.BARCD_CD, LISTAGG( A.A_STORAGE_CD, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_STORAGE_CD, LISTAGG( A.A_STORAGE_NM, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_STORAGE_NM, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_UNIT_QTY ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_UNIT_QTY, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_ETC_QTY ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_ETC_QTY, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_TOT_QTY ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_TOT_QTY, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_AMT ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_AMT, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_VAT ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_VAT, LISTAGG(CASE A.PROD_CD WHEN A.A_PROD_CD THEN A.A_IN_TOT ELSE 0 END, '^') WITHIN GROUP(ORDER BY A.A_STORAGE_CD) AS ARR_IN_TOT FROM ( SELECT A.SLIP_NO, A.SLIP_FG, A.SLIP_KIND, A.PROD_CD, A.PROD_NM, A.SEQ, A.PO_UNIT_FG, A.PO_UNIT_QTY, A.PO_UNIT_ALLOW_FG, A.VAT_FG01, A.ORDER_SPLY_UPRC, A.ORDER_UNIT_QTY, A.ORDER_ETC_QTY, A.ORDER_AMT, A.ORDER_VAT, A.ORDER_TOT, A.MGR_SPLY_UPRC, A.MGR_UNIT_QTY, A.MGR_ETC_QTY, A.MGR_AMT, A.MGR_VAT, A.MGR_TOT, A.OUT_SPLY_UPRC, A.OUT_UNIT_QTY, A.OUT_ETC_QTY, A.OUT_TOT_QTY, A.OUT_AMT, A.OUT_VAT, A.OUT_TOT, A.IN_SPLY_UPRC, A.IN_UNIT_QTY, A.IN_ETC_QTY, A.IN_AMT, A.IN_VAT, A.IN_TOT, A.REMARK, A.ENVST0011, A.BARCD_CD, B.PROD_CD AS A_PROD_CD, B.STORAGE_CD AS A_STORAGE_CD, B.STORAGE_NM AS A_STORAGE_NM, B.IN_UNIT_QTY AS A_IN_UNIT_QTY, B.IN_ETC_QTY AS A_IN_ETC_QTY, B.IN_TOT_QTY AS A_IN_TOT_QTY, B.IN_AMT AS A_IN_AMT, B.IN_VAT AS A_IN_VAT, B.IN_TOT AS A_IN_TOT FROM ( SELECT A.HQ_OFFICE_CD, A.SLIP_NO, E.SLIP_KIND, A.SLIP_FG, A.PROD_CD, B.PROD_NM, A.SEQ, A.PO_UNIT_FG, A.PO_UNIT_QTY, B.PO_UNIT_ALLOW_FG, DECODE(B.VAT_FG, '1', 1, 0) AS VAT_FG01, A.ORDER_SPLY_UPRC AS ORDER_SPLY_UPRC, TO_NUMBER(DECODE(A.ORDER_UNIT_QTY, 0,NULL, A.ORDER_UNIT_QTY * A.SLIP_FG)) AS ORDER_UNIT_QTY, TO_NUMBER(DECODE(A.ORDER_ETC_QTY, 0,NULL, A.ORDER_ETC_QTY * A.SLIP_FG)) AS ORDER_ETC_QTY, A.ORDER_AMT * A.SLIP_FG AS ORDER_AMT, A.ORDER_VAT * A.SLIP_FG AS ORDER_VAT, A.ORDER_TOT * A.SLIP_FG AS ORDER_TOT, A.MGR_SPLY_UPRC AS MGR_SPLY_UPRC, TO_NUMBER(DECODE(A.MGR_UNIT_QTY, 0,NULL, A.MGR_UNIT_QTY * A.SLIP_FG)) AS MGR_UNIT_QTY, TO_NUMBER(DECODE(A.MGR_ETC_QTY , 0,NULL, A.MGR_ETC_QTY * A.SLIP_FG)) AS MGR_ETC_QTY, A.MGR_AMT * A.SLIP_FG AS MGR_AMT, A.MGR_VAT * A.SLIP_FG AS MGR_VAT, A.MGR_TOT * A.SLIP_FG AS MGR_TOT, A.OUT_SPLY_UPRC AS OUT_SPLY_UPRC, TO_NUMBER(DECODE(A.OUT_UNIT_QTY, 0,NULL, A.OUT_UNIT_QTY * A.SLIP_FG)) AS OUT_UNIT_QTY, TO_NUMBER(DECODE(A.OUT_ETC_QTY, 0,NULL, A.OUT_ETC_QTY * A.SLIP_FG)) AS OUT_ETC_QTY, TO_NUMBER(DECODE(A.OUT_TOT_QTY, 0,NULL, A.OUT_TOT_QTY * A.SLIP_FG)) AS OUT_TOT_QTY, A.OUT_AMT * A.SLIP_FG AS OUT_AMT, A.OUT_VAT * A.SLIP_FG AS OUT_VAT, A.OUT_TOT * A.SLIP_FG AS OUT_TOT, A.IN_SPLY_UPRC AS IN_SPLY_UPRC, TO_NUMBER(DECODE(A.IN_UNIT_QTY, 0,NULL, A.IN_UNIT_QTY * A.SLIP_FG)) AS IN_UNIT_QTY, TO_NUMBER(DECODE(A.IN_ETC_QTY, 0,NULL, A.IN_ETC_QTY * A.SLIP_FG)) AS IN_ETC_QTY, A.IN_AMT* A.SLIP_FG AS IN_AMT, A.IN_VAT* A.SLIP_FG AS IN_VAT, A.IN_TOT* A.SLIP_FG AS IN_TOT, A.REMARK, D.ENVST_VAL AS ENVST0011, C.BARCD_CD FROM TB_PO_HQ_STORE_OUTSTOCK E, TB_PO_HQ_STORE_OUTSTOCK_DTL A, TB_HQ_PRODUCT B, TB_HQ_PRODUCT_BARCD C, TB_HQ_ENVST D WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SLIP_NO = #{slipNo} AND E.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND E.SLIP_NO = A.SLIP_NO AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.PROD_CD = A.PROD_CD AND C.HQ_OFFICE_CD (+) = A.HQ_OFFICE_CD AND C.PROD_CD (+) = A.PROD_CD AND D.HQ_OFFICE_CD = #{hqOfficeCd} AND D.ENVST_CD = '0011' ) A LEFT OUTER JOIN ( SELECT X.STORE_CD, X.STORAGE_CD, X.STORAGE_NM, --Y.HQ_OFFICE_CD, #{hqOfficeCd} AS HQ_OFFICE_CD, #{slipNo} AS SLIP_NO, Y.PROD_CD, --Y.STORAGE_CD, NVL(Y.IN_UNIT_QTY, 0) AS IN_UNIT_QTY, NVL(Y.IN_ETC_QTY, 0) AS IN_ETC_QTY, NVL(Y.IN_TOT_QTY, 0) AS IN_TOT_QTY, NVL(Y.IN_AMT, 0) AS IN_AMT, NVL(Y.IN_VAT, 0) AS IN_VAT, NVL(Y.IN_TOT, 0) AS IN_TOT FROM TB_MS_STORAGE X LEFT OUTER JOIN TB_PO_HQ_STORE_OUTSTOCK_PROD Y ON X.STORE_CD = Y.STORE_CD AND X.STORAGE_CD = Y.STORAGE_CD AND Y.HQ_OFFICE_CD = #{hqOfficeCd} AND Y.SLIP_NO = #{slipNo} AND Y.SLIP_FG = '1' WHERE X.STORE_CD = #{storeCd} AND X.USE_YN = 'Y' AND X.STORAGE_CD >= '001' AND X.STORAGE_CD < '999' ) B ON A.HQ_OFFICE_CD = B.HQ_OFFICE_CD AND A.SLIP_NO = B.SLIP_NO
AND A.PROD_CD = (CASE NVL(B.PROD_CD,'X') WHEN 'X' THEN A.PROD_CD ELSE B.PROD_CD END) ) A GROUP BY A.SLIP_NO, A.SLIP_FG, A.SLIP_KIND, A.PROD_CD, A.PROD_NM, A.SEQ, A.PO_UNIT_FG, A.PO_UNIT_QTY, A.PO_UNIT_ALLOW_FG, A.VAT_FG01, A.ORDER_SPLY_UPRC, A.ORDER_UNIT_QTY, A.ORDER_ETC_QTY, A.ORDER_AMT, A.ORDER_VAT, A.ORDER_TOT, A.MGR_SPLY_UPRC, A.MGR_UNIT_QTY, A.MGR_ETC_QTY, A.MGR_AMT, A.MGR_VAT, A.MGR_TOT, A.OUT_SPLY_UPRC, A.OUT_UNIT_QTY, A.OUT_ETC_QTY, A.OUT_TOT_QTY, A.OUT_AMT, A.OUT_VAT, A.OUT_TOT, A.IN_SPLY_UPRC, A.IN_UNIT_QTY, A.IN_ETC_QTY, A.IN_AMT, A.IN_VAT, A.IN_TOT, A.REMARK, A.ENVST0011, A.BARCD_CD ORDER BY A.PROD_CD

mapper.update:
/* USE : InstockConfmMapper.updateInstockConfmDtl */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK_DTL

SET     IN_SPLY_UPRC    = #{outSplyUprc},
        IN_UNIT_QTY     = #{inUnitQty},
        IN_ETC_QTY      = #{inEtcQty},
        IN_TOT_QTY      = #{inTotQty},
        IN_AMT          = #{inAmt},
        IN_VAT          = #{inVat},
        IN_TOT          = #{inTot},
        REMARK          = #{remark},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD	= #{hqOfficeCd}
AND     SLIP_NO       	= #{slipNo}
AND     SEQ           	= #{seq}

/* USE : InstockConfmMapper.updateInstockConfmHd */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK
SET     (   IN_UNIT_QTY, IN_ETC_QTY, IN_TOT_QTY, IN_AMT, IN_VAT, IN_TOT)
    =   (   SELECT  SUM(IN_UNIT_QTY), SUM(IN_ETC_QTY), SUM(IN_TOT_QTY), SUM(IN_AMT), SUM(IN_VAT), SUM(IN_TOT)
            FROM    TB_PO_HQ_STORE_OUTSTOCK_DTL
            WHERE   HQ_OFFICE_CD    =   #{hqOfficeCd}
            AND     SLIP_NO         =   #{slipNo}
        ),
        REMARK      = #{hdRemark},
        MOD_DT      = #{modDt},
        MOD_ID      = #{modId}
WHERE   HQ_OFFICE_CD= #{hqOfficeCd}
AND     SLIP_NO		= #{slipNo}

/* USE : InstockConfmMapper.updateInstockConfirm */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK_DTL
SET     PROC_FG     = #{updateProcFg},
        MOD_DT      = #{modDt},
        MOD_ID      = #{modId}
WHERE   HQ_OFFICE_CD= #{hqOfficeCd}
AND     SLIP_NO		= #{slipNo}

/* USE : InstockConfmMapper.updateInstockConfirm */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK
SET     PROC_FG         = #{updateProcFg},
        IN_DATE         = #{inDate},
        IN_DT           = #{modDt},
        IN_ID           = #{modId},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  	= #{hqOfficeCd}
AND     SLIP_NO			= #{slipNo}

mapper.insert:
/* USE : InstockConfmMapper.insertInstockErrDtl */

INSERT INTO TB_PO_STORE_INSTOCK_ERROR_DTL
(       HQ_OFFICE_CD    ,       SLIP_NO         ,       SEQ             ,       SLIP_FG
,       DSTB_FG         ,       STORAGE_CD      ,       HQ_BRAND_CD     ,       PROD_CD         ,       PROD_SEQ        ,       PO_UNIT_FG      ,       PO_UNIT_QTY
,       OUT_SPLY_UPRC   ,       OUT_UNIT_QTY    ,       OUT_ETC_QTY     ,       OUT_TOT_QTY     ,       OUT_AMT         ,       OUT_VAT         ,       OUT_TOT
,       IN_SPLY_UPRC    ,       IN_UNIT_QTY     ,       IN_ETC_QTY      ,       IN_TOT_QTY      ,       IN_AMT          ,       IN_VAT          ,       IN_TOT
,       REG_DT
,       REG_ID
,       MOD_DT
,       MOD_ID
)
SELECT  HQ_OFFICE_CD    ,       SLIP_NO         ,       SEQ             ,       SLIP_FG
,       DSTB_FG         ,       STORAGE_CD      ,       HQ_BRAND_CD     ,       PROD_CD         ,       ROWNUM          ,       PO_UNIT_FG      ,       PO_UNIT_QTY
,       OUT_SPLY_UPRC   ,       OUT_UNIT_QTY    ,       OUT_ETC_QTY     ,       OUT_TOT_QTY     ,       OUT_AMT         ,       OUT_VAT         ,       OUT_TOT
,       IN_SPLY_UPRC    ,       IN_UNIT_QTY     ,       IN_ETC_QTY      ,       IN_TOT_QTY      ,       IN_AMT          ,       IN_VAT          ,       IN_TOT
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}
FROM    TB_PO_HQ_STORE_OUTSTOCK_DTL
WHERE   HQ_OFFICE_CD =   #{hqOfficeCd}
AND     SLIP_NO      =   #{slipNo}
AND     (OUT_UNIT_QTY <> IN_UNIT_QTY OR OUT_ETC_QTY <> IN_ETC_QTY)
