mapper.select:
/* USE : outstockReqDateMapper.getDaysList */
SELECT tms.STORE_CD, tms.STORE_NM , tms.OWNER_NM, tms.SYS_STAT_FG , NVL(tphsl.ORDER_CLOSE_YN, 'N') AS ORDER_CLOSE_YN , DECODE(INSTR(tphsod.REQ_NO_DAY, '1'), 0, 'Y', NULL, 'Y', 'N') AS SUN , DECODE(INSTR(tphsod.REQ_NO_DAY, '2'), 0, 'Y', NULL, 'Y', 'N') AS MON , DECODE(INSTR(tphsod.REQ_NO_DAY, '3'), 0, 'Y', NULL, 'Y', 'N') AS TUE , DECODE(INSTR(tphsod.REQ_NO_DAY, '4'), 0, 'Y', NULL, 'Y', 'N') AS WED , DECODE(INSTR(tphsod.REQ_NO_DAY, '5'), 0, 'Y', NULL, 'Y', 'N') AS THU , DECODE(INSTR(tphsod.REQ_NO_DAY, '6'), 0, 'Y', NULL, 'Y', 'N') AS FRI , DECODE(INSTR(tphsod.REQ_NO_DAY, '7'), 0, 'Y', NULL, 'Y', 'N') AS SAT , tphsod.REMARK AS DAYS_REMARK
FROM TB_MS_STORE tms , TB_PO_HQ_STORE_ORDER_DAYS tphsod , TB_PO_HQ_STORE_LOAN tphsl WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsod.STORE_CD (+) = tms.STORE_CD AND tphsl.STORE_CD (+) = tms.STORE_CD
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tms.STORE_CD

mapper.update:
/* USE : outstockReqDateMapper.updateReqDateDays */
UPDATE  TB_PO_HQ_STORE_ORDER_DAYS
SET     REQ_NO_DAY      = #{reqNoDay},
        REMARK          = #{daysRemark},
        REG_DT          = #{regDt},
        REG_ID          = #{regId},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND     STORE_CD     = #{storeCd}

mapper.select:
/* USE : outstockReqDateMapper.getSpecificDateList */
SELECT tms.STORE_CD, tms.STORE_NM , tms.OWNER_NM, tms.SYS_STAT_FG , tphsos.SPECIFIC_DATE , tphsos.OUTSTOCK_REQ_YN , tphsos.REMARK AS SPECIFIC_DATE_REMARK
FROM TB_PO_HQ_STORE_ORDER_SPECIFIC tphsos , TB_MS_STORE tms WHERE tphsos.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tphsos.STORE_CD
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tphsos.STORE_CD

mapper.update:
/* USE : outstockReqDateMapper.updateSpecificDate */
UPDATE  TB_PO_HQ_STORE_ORDER_SPECIFIC
SET     OUTSTOCK_REQ_YN = #{outstockReqYn},
        REMARK          = #{specificDateRemark},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     STORE_CD      = #{storeCd}
AND     SPECIFIC_DATE = #{specificDate}

mapper.delete:
/* USE : outstockReqDateMapper.deleteSpecificDate */
DELETE  TB_PO_HQ_STORE_ORDER_SPECIFIC
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     STORE_CD      = #{storeCd}
AND     SPECIFIC_DATE = #{specificDate}

/* USE : outstockReqDateMapper.deleteCopySpecificDate */
DELETE  TB_PO_HQ_STORE_ORDER_SPECIFIC
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     STORE_CD      = #{copyStoreCd}
AND     SPECIFIC_DATE = #{specificDate}

/* USE : outstockReqDateMapper.deleteAllCopyDays */
DELETE  TB_PO_HQ_STORE_ORDER_DAYS
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     STORE_CD      = #{copyStoreCd}
