mapper.select:
/* USE : RtnDstbCloseProdMapper.getRtnDstbCloseProdList */
SELECT tphsd.REQ_DATE, tphsd.SLIP_FG, tphsd.PROC_FG, tphsd.PROD_CD, tmp.PROD_NM , tmp.PO_UNIT_FG , tmp.PO_UNIT_QTY , COUNT(*) AS DTL_CNT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_UNIT_QTY,tphsd.MGR_UNIT_QTY)*tphsd.SLIP_FG) AS MGR_UNIT_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_ETC_QTY ,tphsd.MGR_ETC_QTY) *tphsd.SLIP_FG) AS MGR_ETC_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_TOT_QTY ,tphsd.MGR_TOT_QTY) *tphsd.SLIP_FG) AS MGR_TOT_QTY , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_AMT ,tphsd.MGR_AMT) *tphsd.SLIP_FG) AS MGR_AMT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_VAT ,tphsd.MGR_VAT) *tphsd.SLIP_FG) AS MGR_VAT , SUM(DECODE(tphsd.PROC_FG,'00',tphsd.MD_TOT ,tphsd.MGR_TOT) *tphsd.SLIP_FG) AS MGR_TOT FROM TB_PO_HQ_STORE_DISTRIBUTE tphsd , TB_MS_PRODUCT tmp WHERE tphsd.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsd.SLIP_FG = #{slipFg} AND tmp.STORE_CD = tphsd.STORE_CD AND tmp.PROD_CD = tphsd.PROD_CD
AND tphsd.REQ_DATE BETWEEN #{startDate} AND #{endDate}
AND tphsd.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tphsd.MOD_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tphsd.PROC_FG = #{procFg}
GROUP BY tphsd.REQ_DATE, tphsd.SLIP_FG, tphsd.PROC_FG , tphsd.PROD_CD, tmp.PROD_NM , tmp.PO_UNIT_FG , tmp.PO_UNIT_QTY ORDER BY tphsd.REQ_DATE, tphsd.PROD_CD

mapper.update:
