WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     REQ_DATE      = #{reqDate}
AND     SEQ           = #{seq}

mapper.select:
/* USE : RtnDstbCloseProdMapper.getRtnDstbCloseProdAddProdList */
SELECT NVL((SELECT SUBSTR(MAX(SYS_CONNECT_BY_PATH( PROD_CLASS_NM, '>')), 2) FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD START WITH PROD_CLASS_CD = thp.PROD_CLASS_CD CONNECT BY PRIOR P_PROD_CLASS_CD = PROD_CLASS_CD GROUP BY thp.PROD_CLASS_CD),' ') AS PROD_CLASS_NM , thp.PROD_CD , thp.PROD_NM , thp.SPLY_UPRC , thp.PO_UNIT_FG , thp.PO_UNIT_QTY
FROM TB_HQ_PRODUCT thp , TB_HQ_PRODUCT_BARCD thpb WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thpb.HQ_OFFICE_CD(+)= thp.HQ_OFFICE_CD AND thpb.PROD_CD (+)= thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thpb.BARCD_CD = #{barcdCd}
ORDER BY thp.PROD_CD

/* USE : RtnDstbCloseProdMapper.getRtnDstbCloseProdAddRegistList */
SELECT tms.STORE_CD, tms.STORE_NM , tmp.PROD_CD, tmp.PROD_NM , DECODE(tmp.VAT_FG,'Y',1,0) AS VAT_FG01 , tmp.SPLY_UPRC , tmp.PO_UNIT_ALLOW_FG , tmp.PO_PROD_FG , tmp.PO_UNIT_FG , tmp.PO_UNIT_QTY , 0 AS ENVST0011 , tmp.SPLY_UPRC AS MGR_SPLY_UPRC , TRUNC(tphsd.PREV_MGR_UNIT_QTY / tphsd.PO_UNIT_QTY) * tphsd.SLIP_FG AS PREV_MGR_UNIT_QTY , MOD (tphsd.PREV_MGR_ETC_QTY , tphsd.PO_UNIT_QTY) * tphsd.SLIP_FG AS PREV_MGR_ETC_QTY , TRUNC(DECODE(SIGN(tshsc.CURR_QTY),-1,0,tshsc.CURR_QTY) / tmp.PO_UNIT_QTY) AS HD_CUR_UNIT_QTY , MOD (DECODE(SIGN(tshsc.CURR_QTY),-1,0,tshsc.CURR_QTY) , tmp.PO_UNIT_QTY) AS HD_CUR_ETC_QTY , TRUNC(DECODE(SIGN(tsssc.CURR_QTY),-1,0,tsssc.CURR_QTY) / tmp.PO_UNIT_QTY) AS STORE_CUR_UNIT_QTY , MOD (DECODE(SIGN(tsssc.CURR_QTY),-1,0,tsssc.CURR_QTY) , tmp.PO_UNIT_QTY) AS STORE_CUR_ETC_QTY , DECODE(NVL(suborder.ORDER_FG, 0), 0, 'Y', 'N') AS ORDER_FG , 'N' AS CONFIRM_YN FROM TB_MS_STORE tms , TB_MS_PRODUCT tmp , ( SELECT tphsd.STORE_CD, tphsd.PROD_CD, tphsd.SLIP_FG, tphsd.PO_UNIT_QTY , SUM(DECODE(tphsd.MGR_TOT_QTY, 0, tphsd.MD_TOT_QTY, tphsd.MGR_TOT_QTY)) AS PREV_MGR_UNIT_QTY , SUM(DECODE(tphsd.MGR_TOT_QTY, 0, tphsd.MD_TOT_QTY, tphsd.MGR_TOT_QTY)) AS PREV_MGR_ETC_QTY FROM TB_PO_HQ_STORE_DISTRIBUTE tphsd WHERE tphsd.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsd.REQ_DATE = #{reqDate} AND tphsd.SLIP_FG = #{slipFg} GROUP BY tphsd.STORE_CD, tphsd.PROD_CD, tphsd.SLIP_FG, tphsd.PO_UNIT_QTY ) tphsd , ( SELECT PROD_CD, CURR_QTY FROM TB_ST_HQ_STOCK_CUR WHERE HQ_OFFICE_CD = #{hqOfficeCd} ) tshsc , TB_ST_STORE_STOCK_CUR tsssc , ( SELECT STORE_CD, SUM(ORDER_FG) AS ORDER_FG FROM ( SELECT tphsos.STORE_CD, DECODE(NVL(tphsos.OUTSTOCK_REQ_YN, 'N'), 'Y', 1, 0) AS ORDER_FG FROM TB_PO_HQ_STORE_ORDER_SPECIFIC tphsos WHERE tphsos.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsos.SPECIFIC_DATE = #{reqDate} GROUP BY tphsos.STORE_CD, tphsos.OUTSTOCK_REQ_YN UNION SELECT tphsod.STORE_CD, COUNT(STORE_CD) AS ORDER_FG FROM TB_PO_HQ_STORE_ORDER_DAYS tphsod WHERE tphsod.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsod.REQ_NO_DAY LIKE '%'||(SELECT TO_CHAR(TO_DATE(#{reqDate}, 'YYYYMMDD'), 'D') FROM DUAL)||'%' GROUP BY tphsod.STORE_CD UNION SELECT tms.STORE_CD, DECODE(NVL(tphsc.ORDER_CLOSE_FG, 'N'), 'Y', 1, 0) AS ORDER_FG FROM TB_PO_HQ_STORE_CLOSE tphsc , TB_MS_STORE tms WHERE tphsc.HQ_OFFICE_CD = #{hqOfficeCd} AND tphsc.CLOSE_DATE = #{reqDate} AND tms.HQ_OFFICE_CD = tphsc.HQ_OFFICE_CD UNION SELECT tphsl.STORE_CD, DECODE(NVL(tphsl.ORDER_CLOSE_YN, 'N'), 'Y', 1, 0) AS ORDER_FG FROM TB_PO_HQ_STORE_LOAN tphsl WHERE tphsl.HQ_OFFICE_CD = #{hqOfficeCd} GROUP BY tphsl.STORE_CD, tphsl.ORDER_CLOSE_YN ) GROUP BY STORE_CD ) suborder WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmp.STORE_CD = tms.STORE_CD AND tmp.PROD_CD = #{prodCd} AND tphsd.STORE_CD (+) = tmp.STORE_CD AND tphsd.PROD_CD (+) = tmp.PROD_CD AND tshsc.PROD_CD (+) = tmp.PROD_CD AND tsssc.STORE_CD (+) = tmp.STORE_CD AND tsssc.PROD_CD (+) = tmp.PROD_CD AND suborder.STORE_CD (+) = tms.STORE_CD
AND tms.STORE_CD IN
#{item}

