mapper.select:
/* USE : RtnOutstockConfmMapper.getReqNoConfirmCnt */

SELECT	tphso.REQ_NO_CONFIRM_CNT, tphsd.OUTSTOCK_NO_CREATE_CNT
FROM	(	SELECT  COUNT(*) AS REQ_NO_CONFIRM_CNT
            FROM    TB_MS_STORE tms
            ,       TB_PO_HQ_STORE_ORDER tphso
            WHERE   tms.HQ_OFFICE_CD    =   #{hqOfficeCd}
            AND     tphso.STORE_CD      =   tms.STORE_CD
            AND     REQ_DATE    BETWEEN #{startDate} AND #{endDate}
            AND     PROC_FG     IN      ('00','10')
        ) tphso
,		(	SELECT  COUNT(*) AS OUTSTOCK_NO_CREATE_CNT
            FROM    (   SELECT  tphso.STORE_CD
                        FROM    TB_PO_HQ_STORE_DISTRIBUTE tphso
                        WHERE   tphso.HQ_OFFICE_CD	=       #{hqOfficeCd}
                        AND     tphso.REQ_DATE      BETWEEN #{startDate} AND #{endDate}
                        AND     tphso.SLIP_FG       =       #{slipFg}
                        AND     tphso.PROC_FG       IN      ('00','10','20')
                        GROUP
                        BY      tphso.STORE_CD
                    )
        ) tphsd

/* USE : RtnOutstockConfmMapper.getRtnOutstockConfmList */
SELECT tphso.SLIP_NO, tphso.VENDR_CD, tphso.SLIP_FG, tphso.SLIP_KIND, tphso.PROC_FG, tphso.STORE_CD, tms.STORE_NM , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_AMT*tphso.SLIP_FG AS ORDER_AMT , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_VAT*tphso.SLIP_FG AS ORDER_VAT , tphso.ORDER_TOT_QTY*tphso.SLIP_FG AS ORDER_TOT_QTY, tphso.ORDER_TOT*tphso.SLIP_FG AS ORDER_TOT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_AMT *tphso.SLIP_FG AS MGR_AMT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_VAT *tphso.SLIP_FG AS MGR_VAT , tphso.MGR_TOT_QTY *tphso.SLIP_FG AS MGR_TOT_QTY , tphso.MGR_TOT *tphso.SLIP_FG AS MGR_TOT , (CASE WHEN tphso.PROC_FG < '20' THEN tphso.OUT_DATE ELSE NULL END) AS REQ_DATE , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_DATE ELSE NULL END) AS OUT_DATE , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_TOT_QTY*tphso.SLIP_FG ELSE NULL END) AS OUT_TOT_QTY , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_AMT*tphso.SLIP_FG ELSE NULL END) AS OUT_AMT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_VAT*tphso.SLIP_FG ELSE NULL END) AS OUT_VAT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_TOT*tphso.SLIP_FG ELSE NULL END) AS OUT_TOT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_DATE ELSE NULL END) AS IN_DATE , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_TOT_QTY *tphso.SLIP_FG ELSE NULL END) AS IN_TOT_QTY , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_AMT *tphso.SLIP_FG ELSE NULL END) AS IN_AMT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_VAT *tphso.SLIP_FG ELSE NULL END) AS IN_VAT , (CASE WHEN tphso.PROC_FG >= '30' THEN tphso.IN_TOT *tphso.SLIP_FG ELSE NULL END) AS IN_TOT , (CASE WHEN tphso.PROC_FG >= '20' THEN tphso.OUT_DT ELSE NULL END) AS OUT_DT , (CASE WHEN tphso.PROC_FG >= '20' THEN userInfo.EMP_NM ELSE NULL END) AS OUT_NM , tphso.REMARK, tphso.HQ_REMARK, tphso.DLVR_CD, tphdc.DLVR_NM , tphsod.PENALTY_AMT , NVL((SELECT VENDR_NM FROM TB_HQ_VENDOR WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND VENDR_CD = tphso.VENDR_CD), '본사') AS VENDR_NM FROM TB_PO_HQ_STORE_OUTSTOCK tphso , ( SELECT tphsod.HQ_OFFICE_CD, tphsod.SLIP_NO, SUM(tphsod.PENALTY_AMT) AS PENALTY_AMT FROM TB_PO_HQ_STORE_OUTSTOCK tphso , TB_PO_HQ_STORE_OUTSTOCK_DTL tphsod WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd} AND tphso.OUT_DATE BETWEEN #{startDate} AND #{endDate} AND tphsod.HQ_OFFICE_CD = tphso.HQ_OFFICE_CD AND tphsod.SLIP_NO = tphso.SLIP_NO
AND tphso.SLIP_FG = #{slipFg}
AND tphso.SLIP_KIND = #{slipKind}
AND tphso.PROC_FG = #{procFg}
AND tphso.VENDR_CD = #{vendrCd}
AND tphso.VENDR_CD IS NULL
AND tphso.DLVR_CD = #{dlvrCd}
GROUP BY tphsod.HQ_OFFICE_CD, tphsod.SLIP_NO ) tphsod , TB_MS_STORE tms , ( SELECT tme.USER_ID, tme.EMP_NM FROM TB_MS_STORE tms , TB_MS_EMPLOYEE tme WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tme.STORE_CD = tms.STORE_CD UNION SELECT the.USER_ID, the.EMP_NM FROM TB_HQ_EMPLOYEE the WHERE the.HQ_OFFICE_CD = #{hqOfficeCd} ) userInfo , TB_PO_HQ_DELIVERY_CHARGER tphdc WHERE tphso.HQ_OFFICE_CD = #{hqOfficeCd} AND tphso.OUT_DATE BETWEEN #{startDate} AND #{endDate} AND tphsod.HQ_OFFICE_CD = tphso.HQ_OFFICE_CD AND tphsod.SLIP_NO = tphso.SLIP_NO AND tms.STORE_CD = tphso.STORE_CD AND userInfo.USER_ID (+) = tphso.MOD_ID AND tphdc.HQ_OFFICE_CD (+) = tphso.HQ_OFFICE_CD AND tphdc.DLVR_CD (+) = tphso.DLVR_CD
AND tphso.SLIP_FG = #{slipFg}
AND tphso.SLIP_KIND = #{slipKind}
AND tphso.PROC_FG = #{procFg}
AND tphso.VENDR_CD = #{vendrCd}
AND tphso.VENDR_CD IS NULL
AND tphso.DLVR_CD = #{dlvrCd}
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
ORDER BY tphso.SLIP_NO

mapper.update:
/* USE : RtnOutstockConfmMapper.updateOutstockDtlConfirm */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK_DTL
SET     PROC_FG         = #{updateProcFg},
		REQ_DATE        = #{outDate},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     SLIP_NO       = #{slipNo}

/* USE : RtnOutstockConfmMapper.updateOutstockProdConfirm */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK_PROD
SET     CONFM_YN        = 'Y',
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     SLIP_NO       = #{slipNo}
AND     OCCR_FG	  	  = '12'
AND     CONFM_YN      != 'Y'

/* USE : RtnOutstockConfmMapper.updateOutstockConfirm */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK
SET     PROC_FG         = #{updateProcFg},
        OUT_DATE        = #{outDate},
        OUT_DT          = #{modDt},
        OUT_ID          = #{modId},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     SLIP_NO       = #{slipNo}

/* USE : RtnOutstockConfmMapper.updateRtnOutstockConfmDtl */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK_DTL
SET     OUT_SPLY_UPRC   = #{outSplyUprc},
        OUT_UNIT_QTY    = #{outUnitQty},
        OUT_ETC_QTY     = #{outEtcQty},
        OUT_TOT_QTY     = #{outTotQty},
        OUT_AMT         = #{outAmt},
        OUT_VAT         = #{outVat},
        OUT_TOT         = #{outTot},
        REMARK          = #{remark},
        MOD_DT          = #{modDt},
        MOD_ID          = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     SLIP_NO       = #{slipNo}
AND     SEQ           = #{seq}

/* USE : RtnOutstockConfmMapper.updateRtnOutstockConfmHd */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK
SET     (   OUT_UNIT_QTY, OUT_ETC_QTY, OUT_TOT_QTY, OUT_AMT, OUT_VAT, OUT_TOT)
    =   (   SELECT  SUM(OUT_UNIT_QTY), SUM(OUT_ETC_QTY), SUM(OUT_TOT_QTY), SUM(OUT_AMT), SUM(OUT_VAT), SUM(OUT_TOT)
            FROM    TB_PO_HQ_STORE_OUTSTOCK_DTL
            WHERE   HQ_OFFICE_CD    =   #{hqOfficeCd}
            AND     SLIP_NO         =   #{slipNo}
        ),
        REMARK      = #{hdRemark},
        HQ_REMARK   = #{hqRemark},
        DLVR_CD     = #{dlvrCd},
        MOD_DT      = #{modDt},
        MOD_ID      = #{modId}
WHERE   HQ_OFFICE_CD  =   #{hqOfficeCd}
AND     SLIP_NO       =   #{slipNo}

/* USE : RtnOutstockConfmMapper.updateOutstockAfterHd */
UPDATE  TB_PO_HQ_STORE_OUTSTOCK
SET     REMARK      = #{hdRemark},
        HQ_REMARK   = #{hqRemark},
        DLVR_CD     = #{dlvrCd},
        MOD_DT      = #{modDt},
        MOD_ID      = #{modId}
WHERE   HQ_OFFICE_CD  =   #{hqOfficeCd}
AND     SLIP_NO       =   #{slipNo}

        /* USE : RtnOutstockConfmMapper.updateRtnStoreOrderProd */
	    UPDATE  TB_PO_HQ_STORE_ORDER_PROD
        SET     (
              		 PO_UNIT_QTY
              		,ORDER_UNIT_QTY
              		,ORDER_ETC_QTY
              		,ORDER_TOT_QTY
              		,ORDER_AMT
              		,ORDER_VAT
              		,ORDER_TOT
				 )
            =   ( SELECT  (PO_UNIT_QTY)+#{poUnitQty}
            			, (ORDER_UNIT_QTY)+#{orderUnitQty}
            			, (ORDER_ETC_QTY)+#{orderEtcQty}
            			, (ORDER_TOT_QTY)+#{orderTotQty}
                        , (ORDER_AMT)+#{orderAmt}
            			, (ORDER_VAT)+#{orderVat}
            			, (ORDER_TOT)+#{orderTot}
            	  FROM TB_PO_HQ_STORE_ORDER_PROD
				  WHERE REQ_DATE = #{reqDate}
				  AND STORE_CD = #{storeCd}
				  AND SLIP_FG = #{slipFg}
				  AND PROD_CD = #{prodCd}
				  AND STORAGE_CD = #{storageCd}
                )
        ,       MOD_DT  = #{modDt}
        ,       MOD_ID  = #{modId}
		WHERE REQ_DATE = #{reqDate}
		AND STORE_CD = #{storeCd}
		AND SLIP_FG = #{slipFg}
		AND PROD_CD = #{prodCd}
		AND STORAGE_CD = #{storageCd}

mapper.delete:
/* USE : RtnOutstockConfmMapper.deleteRtnOutStockProdAll */
DELETE  TB_PO_HQ_STORE_OUTSTOCK_PROD
WHERE   HQ_OFFICE_CD  =     #{hqOfficeCd}
AND     SLIP_NO       =     #{slipNo}
AND     CONFM_YN      !=    'Y'

		/* USE : RtnDstbCloseProdMapper.deleteOrderProd */
        DELETE  TB_PO_HQ_STORE_ORDER_PROD
        WHERE   REQ_DATE    = #{reqDate}
        AND     STORE_CD    = #{storeCd}
        AND     SLIP_FG     = '-1'

		/* USE : RtnDstbCloseProdMapper.deleteOutStockProd */
        DELETE  TB_PO_HQ_STORE_OUTSTOCK_PROD
        WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
        AND     STORE_CD		= #{storeCd}
        AND     SLIP_NO    		= #{slipNo}
        AND     SLIP_FG     	= '-1'
