mapper.select:
/* USE : VendrOrderMapper.getVendrList */




    SELECT  thv.VENDR_CD, thv.VENDR_NM
    ,       thv.SHIP_FG, thv.VAT_INCLD_YN
    FROM    TB_HQ_VENDOR thv
    WHERE   thv.HQ_OFFICE_CD    =   #{hqOfficeCd}





    SELECT  tmv.VENDR_CD, tmv.VENDR_NM
    ,       tmv.SHIP_FG, tmv.VAT_INCLD_YN
    FROM    TB_MS_VENDOR tmv
    WHERE   tmv.STORE_CD  = #{storeCd}

/* USE : VendrOrderMapper.getVendrOrderList */




        SELECT  tphvo.SLIP_NO, tphvo.PROC_FG, tphvo.VENDR_CD, thv.VENDR_NM
        ,       tphvo.ORDER_TYPE, tphvo.ORDER_DATE, tphvo.ORDER_REQ_DATE
        FROM    TB_PO_HQ_VENDR_ORDER tphvo
        ,       TB_HQ_VENDOR thv
        WHERE   tphvo.HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     tphvo.ORDER_DATE    BETWEEN #{startDate} AND #{endDate}
        AND     thv.HQ_OFFICE_CD    = tphvo.HQ_OFFICE_CD
        AND     thv.VENDR_CD        = tphvo.VENDR_CD


            AND tphvo.SLIP_NO = #{slipNo}


            AND tphvo.PROC_FG = #{procFg}


            AND tphvo.VENDR_CD = #{vendrCd}


        ORDER BY tphvo.SLIP_NO DESC





        SELECT  tpsvo.SLIP_NO, tpsvo.PROC_FG, tmv.VENDR_NM
        ,       tpsvo.ORDER_TYPE, tpsvo.ORDER_DATE, tpsvo.ORDER_REQ_DATE
        FROM    TB_PO_STORE_VENDR_ORDER tpsvo
        ,       TB_MS_VENDOR tmv
        WHERE   tpsvo.STORE_CD    = #{storeCd}
        AND     tpsvo.ORDER_DATE  BETWEEN #{startDate} AND #{endDate}
        AND     tmv.STORE_CD      = tpsvo.STORE_CD
        AND     tmv.VENDR_CD      = tpsvo.VENDR_CD


            AND tpsvo.SLIP_NO = #{slipNo}


            AND tpsvo.PROC_FG = #{procFg}


            AND tpsvo.VENDR_CD = #{vendrCd}


        ORDER BY tpsvo.SLIP_NO DESC

/* USE : VendrOrderMapper.getSlipInfo */
SELECT tphvo.SLIP_NO, tphvo.PROC_FG, tcm.NMCODE_NM AS PROC_NM, tphvo.VENDR_CD, thv.VENDR_NM , tphvo.ORDER_TYPE, tphvo.ORDER_DATE, tphvo.ORDER_REQ_DATE, tphvo.DTL_CNT, tphvo.REMARK , tphvo.ORDER_REG_DT , thereg.EMP_NM AS ORDER_REG_NM , DECODE(tphvo.PROC_FG, 0, NULL, tphvo.CONFM_DT) AS CONFM_DT , DECODE(tphvo.PROC_FG, 0, NULL, theconfm.EMP_NM) AS CONFM_NM , DECODE(tphvo.PROC_FG, 5, tphvo.END_DT, NULL) AS END_DT , DECODE(tphvo.PROC_FG, 5, theend.EMP_NM, NULL) AS END_NM , tphvo.ORDER_TOT_QTY * tphvo.SLIP_FG AS ORDER_TOT_QTY , tphvo.ORDER_TOT * tphvo.SLIP_FG AS ORDER_TOT , tphvo.IN_TOT_QTY * tphvo.SLIP_FG AS IN_TOT_QTY , tphvo.IN_TOT * tphvo.SLIP_FG AS IN_TOT , CASE WHEN tphvo.PROC_FG > 3 THEN tphvo.IN_FIRST_DATE ELSE NULL END AS IN_FIRST_DATE , CASE WHEN tphvo.PROC_FG > 3 THEN tphvo.IN_LAST_DATE ELSE NULL END AS IN_LAST_DATE FROM TB_PO_HQ_VENDR_ORDER tphvo , TB_HQ_VENDOR thv , TB_HQ_EMPLOYEE thereg , TB_HQ_EMPLOYEE theconfm , TB_HQ_EMPLOYEE theend , TB_CM_NMCODE tcm WHERE tphvo.HQ_OFFICE_CD = #{hqOfficeCd} AND tphvo.SLIP_NO = #{slipNo} AND thv.HQ_OFFICE_CD = tphvo.HQ_OFFICE_CD AND thv.VENDR_CD = tphvo.VENDR_CD AND thereg.HQ_OFFICE_CD (+) = tphvo.HQ_OFFICE_CD AND thereg.USER_ID (+) = tphvo.ORDER_REG_ID AND theconfm.HQ_OFFICE_CD (+) = tphvo.HQ_OFFICE_CD AND theconfm.USER_ID (+) = tphvo.CONFM_ID AND theend.HQ_OFFICE_CD (+) = tphvo.HQ_OFFICE_CD AND theend.USER_ID (+) = tphvo.END_ID AND tcm.NMCODE_CD = tphvo.PROC_FG AND tcm.NMCODE_GRP_CD = '096'
SELECT tpsvo.SLIP_NO, tpsvo.PROC_FG, tcm.NMCODE_NM AS PROC_NM, tpsvo.VENDR_CD, tmv.VENDR_NM , tpsvo.ORDER_TYPE, tpsvo.ORDER_DATE, tpsvo.ORDER_REQ_DATE, tpsvo.DTL_CNT, tpsvo.REMARK , tpsvo.ORDER_REG_DT , tmereg.EMP_NM AS ORDER_REG_NM , DECODE(tpsvo.PROC_FG, 0, NULL, tpsvo.CONFM_DT) AS CONFM_DT , DECODE(tpsvo.PROC_FG, 0, NULL, tmeconfm.EMP_NM) AS CONFM_NM , DECODE(tpsvo.PROC_FG, 5, tpsvo.END_DT, NULL) AS END_DT , DECODE(tpsvo.PROC_FG, 5, tmeend.EMP_NM, NULL) AS END_NM , tpsvo.ORDER_TOT_QTY * tpsvo.SLIP_FG AS ORDER_TOT_QTY , tpsvo.ORDER_TOT * tpsvo.SLIP_FG AS ORDER_TOT , tpsvo.IN_TOT_QTY * tpsvo.SLIP_FG AS IN_TOT_QTY , tpsvo.IN_TOT * tpsvo.SLIP_FG AS IN_TOT , CASE WHEN tpsvo.PROC_FG > 3 THEN tpsvo.IN_FIRST_DATE ELSE NULL END AS IN_FIRST_DATE , CASE WHEN tpsvo.PROC_FG > 3 THEN tpsvo.IN_LAST_DATE ELSE NULL END AS IN_LAST_DATE FROM TB_PO_STORE_VENDR_ORDER tpsvo , TB_MS_VENDOR tmv , TB_MS_EMPLOYEE tmereg , TB_MS_EMPLOYEE tmeconfm , TB_MS_EMPLOYEE tmeend , TB_CM_NMCODE tcm WHERE tpsvo.STORE_CD = #{storeCd} AND tpsvo.SLIP_NO = #{slipNo} AND tmv.STORE_CD = tpsvo.STORE_CD AND tmv.VENDR_CD = tpsvo.VENDR_CD AND tmereg.STORE_CD (+) = tpsvo.STORE_CD AND tmereg.USER_ID (+) = tpsvo.ORDER_REG_ID AND tmeconfm.STORE_CD (+) = tpsvo.STORE_CD AND tmeconfm.USER_ID (+) = tpsvo.CONFM_ID AND tmeend.STORE_CD (+) = tpsvo.STORE_CD AND tmeend.USER_ID (+) = tpsvo.END_ID AND tcm.NMCODE_CD = tpsvo.PROC_FG AND tcm.NMCODE_GRP_CD = '096'

/* USE : VendrOrderMapper.getNewSlipNo */




        SELECT  NVL(MAX(tphvo.SLIP_NO), #{yymm}||'0000')+1 AS SLIP_NO
        FROM    TB_PO_HQ_VENDR_ORDER tphvo
        WHERE   tphvo.HQ_OFFICE_CD  =     #{hqOfficeCd}
        AND     tphvo.SLIP_NO       LIKE  #{yymm}||'%'





        SELECT  NVL(MAX(tpsvo.SLIP_NO), #{yymm}||'0000')+1 AS SLIP_NO
        FROM    TB_PO_STORE_VENDR_ORDER tpsvo
        WHERE   tpsvo.STORE_CD  =     #{storeCd}
        AND     tpsvo.SLIP_NO   LIKE  #{yymm}||'%'

/* USE : VendrOrderMapper.getVendrOrderProdList */
SELECT tphvod.PROD_CD, thp.PROD_NM, tphvod.PO_UNIT_FG, tphvod.PO_UNIT_QTY, thp.PO_UNIT_ALLOW_FG , tphvod.SLIP_FG, tphvod.COST_UPRC, thp.PO_MIN_QTY , TO_NUMBER(DECODE(tphvod.ORDER_TOT_QTY , 0, NULL, tphvod.ORDER_TOT_QTY *tphvod.SLIP_FG)) AS PREV_ORDER_TOT_QTY , TO_NUMBER(DECODE(tphvod.ORDER_UNIT_QTY, 0, NULL, tphvod.ORDER_UNIT_QTY*tphvod.SLIP_FG)) AS ORDER_UNIT_QTY , TO_NUMBER(DECODE(tphvod.ORDER_ETC_QTY , 0, NULL, tphvod.ORDER_ETC_QTY *tphvod.SLIP_FG)) AS ORDER_ETC_QTY , TO_NUMBER(DECODE(tphvod.ORDER_TOT_QTY , 0, NULL, tphvod.ORDER_TOT_QTY *tphvod.SLIP_FG)) AS ORDER_TOT_QTY , tphvod.ORDER_AMT*tphvod.SLIP_FG AS ORDER_AMT , tphvod.ORDER_VAT*tphvod.SLIP_FG AS ORDER_VAT , tphvod.ORDER_TOT*tphvod.SLIP_FG AS ORDER_TOT , DECODE(thp.VAT_FG, 'Y', 1, 0) AS VAT_FG01 , DECODE(thv.VAT_INCLD_YN, 'Y', 1, 0) AS VENDR_VAT_FG01 FROM TB_PO_HQ_VENDR_ORDER_DTL tphvod , TB_HQ_PRODUCT thp , TB_HQ_VENDOR thv WHERE tphvod.HQ_OFFICE_CD = #{hqOfficeCd} AND tphvod.SLIP_NO = #{slipNo} AND thp.HQ_OFFICE_CD = tphvod.HQ_OFFICE_CD AND thp.PROD_CD = tphvod.PROD_CD AND thv.HQ_OFFICE_CD = tphvod.HQ_OFFICE_CD AND thv.VENDR_CD = #{vendrCd} ORDER BY tphvod.PROD_CD
SELECT tpsvod.PROD_CD, tmp.PROD_NM, tpsvod.PO_UNIT_FG, tpsvod.PO_UNIT_QTY, tmp.PO_UNIT_ALLOW_FG , tpsvod.SLIP_FG, tpsvod.COST_UPRC, tmp.PO_MIN_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_TOT_QTY , 0, NULL, tpsvod.ORDER_TOT_QTY *tpsvod.SLIP_FG)) AS PREV_ORDER_TOT_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_UNIT_QTY, 0, NULL, tpsvod.ORDER_UNIT_QTY*tpsvod.SLIP_FG)) AS ORDER_UNIT_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_ETC_QTY , 0, NULL, tpsvod.ORDER_ETC_QTY *tpsvod.SLIP_FG)) AS ORDER_ETC_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_TOT_QTY , 0, NULL, tpsvod.ORDER_TOT_QTY *tpsvod.SLIP_FG)) AS ORDER_TOT_QTY , tpsvod.ORDER_AMT*tpsvod.SLIP_FG AS ORDER_AMT , tpsvod.ORDER_VAT*tpsvod.SLIP_FG AS ORDER_VAT , tpsvod.ORDER_TOT*tpsvod.SLIP_FG AS ORDER_TOT , DECODE(tmp.VAT_FG, 'Y', 1, 0) AS VAT_FG01 , DECODE(tmv.VAT_INCLD_YN, 'Y', 1, 0) AS VENDR_VAT_FG01 FROM TB_PO_STORE_VENDR_ORDER_DTL tpsvod , TB_MS_PRODUCT tmp , TB_MS_VENDOR tmv WHERE tpsvod.STORE_CD = #{storeCd} AND tpsvod.SLIP_NO = #{slipNo} AND tmp.STORE_CD = tpsvod.STORE_CD AND tmp.PROD_CD = tpsvod.PROD_CD AND tmv.STORE_CD = tpsvod.STORE_CD AND tmv.VENDR_CD = #{vendrCd} ORDER BY tpsvod.PROD_CD

/* USE : VendrOrderMapper.getProcFgCheck */




        SELECT  tphvo.PROC_FG, tcm.NMCODE_NM, tphvo.DTL_CNT
        FROM    TB_PO_HQ_VENDR_ORDER tphvo
        ,       TB_CM_NMCODE tcm
        WHERE   tphvo.HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     tphvo.SLIP_NO       = #{slipNo}
        AND     tcm.NMCODE_CD       = tphvo.PROC_FG
        AND     tcm.NMCODE_GRP_CD   = '096'





        SELECT  tpsvo.PROC_FG, tcm.NMCODE_NM, tpsvo.DTL_CNT
        FROM    TB_PO_STORE_VENDR_ORDER tpsvo
        ,       TB_CM_NMCODE tcm
        WHERE   tpsvo.STORE_CD      = #{storeCd}
        AND     tpsvo.SLIP_NO       = #{slipNo}
        AND     tcm.NMCODE_CD       = tpsvo.PROC_FG
        AND     tcm.NMCODE_GRP_CD   = '096'

/* USE : VendrOrderMapper.getVendrOrderProdRegList */
SELECT thvp.PROD_CD , thp.PROD_NM , thp.ORGPLCE_CD , DECODE(thp.VAT_FG,'Y',1,0) AS VAT_FG01 , TRUNC(DECODE(SIGN(tshsc.CURR_QTY),-1,0,tshsc.CURR_QTY) / thp.PO_UNIT_QTY) AS CURR_UNIT_QTY , MOD (DECODE(SIGN(tshsc.CURR_QTY),-1,0,tshsc.CURR_QTY) , thp.PO_UNIT_QTY) AS CURR_ETC_QTY , NVL(tphvod.COST_UPRC, thp.COST_UPRC) AS COST_UPRC , thp.LAST_COST_UPRC , thp.PO_UNIT_ALLOW_FG , thp.PO_UNIT_FG , thp.PO_UNIT_QTY , DECODE(thv.VAT_INCLD_YN, 'Y', 1, 0) AS VENDR_VAT_FG01 , TRUNC(DECODE(SIGN(thp.SAFE_STOCK_QTY),-1,0,thp.SAFE_STOCK_QTY) / thp.PO_UNIT_QTY) AS SAFE_STOCK_UNIT_QTY , MOD (DECODE(SIGN(thp.SAFE_STOCK_QTY),-1,0,thp.SAFE_STOCK_QTY) , thp.PO_UNIT_QTY) AS SAFE_STOCK_ETC_QTY , TO_NUMBER(DECODE(tphvod.ORDER_UNIT_QTY, 0, NULL, tphvod.ORDER_UNIT_QTY*tphvod.SLIP_FG)) AS PREV_ORDER_UNIT_QTY , TO_NUMBER(DECODE(tphvod.ORDER_ETC_QTY, 0, NULL, tphvod.ORDER_ETC_QTY*tphvod.SLIP_FG )) AS PREV_ORDER_ETC_QTY , TO_NUMBER(DECODE(tphvod.ORDER_TOT_QTY, 0, NULL, tphvod.ORDER_TOT_QTY*tphvod.SLIP_FG )) AS PREV_ORDER_TOT_QTY , TO_NUMBER(DECODE(tphvod.ORDER_AMT , 0, NULL, tphvod.ORDER_AMT*tphvod.SLIP_FG )) AS ORDER_AMT , TO_NUMBER(DECODE(tphvod.ORDER_VAT , 0, NULL, tphvod.ORDER_VAT*tphvod.SLIP_FG )) AS ORDER_VAT , TO_NUMBER(DECODE(tphvod.ORDER_TOT , 0, NULL, tphvod.ORDER_TOT*tphvod.SLIP_FG )) AS ORDER_TOT
FROM TB_HQ_VENDOR_PROD thvp , TB_HQ_PRODUCT thp , TB_ST_HQ_STOCK_CUR tshsc , TB_PO_HQ_VENDR_ORDER_DTL tphvod , TB_HQ_VENDOR thv WHERE thvp.HQ_OFFICE_CD = #{hqOfficeCd} AND thvp.VENDR_CD = #{vendrCd} AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = thvp.PROD_CD AND thp.USE_YN = 'Y' AND tshsc.HQ_OFFICE_CD(+) = thp.HQ_OFFICE_CD AND tshsc.PROD_CD (+) = thp.PROD_CD AND tphvod.HQ_OFFICE_CD(+)= thp.HQ_OFFICE_CD AND tphvod.SLIP_NO (+) = #{slipNo} AND tphvod.PROD_CD (+) = thp.PROD_CD AND thv.HQ_OFFICE_CD = thvp.HQ_OFFICE_CD AND thv.VENDR_CD = #{vendrCd}
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND NVL(tshsc.CURR_QTY, 0) < NVL(thp.SAFE_STOCK_QTY, 0)
ORDER BY thp.PROD_CD
SELECT tmvp.PROD_CD , tmp.PROD_NM , tmp.ORGPLCE_CD , DECODE(tmp.VAT_FG,'Y',1,0) AS VAT_FG01 , TRUNC(DECODE(SIGN(tsssc.CURR_QTY),-1,0,tsssc.CURR_QTY) / tmp.PO_UNIT_QTY) AS CURR_UNIT_QTY , MOD (DECODE(SIGN(tsssc.CURR_QTY),-1,0,tsssc.CURR_QTY) , tmp.PO_UNIT_QTY) AS CURR_ETC_QTY , NVL(tpsvod.COST_UPRC, tmp.COST_UPRC) AS COST_UPRC , tmp.LAST_COST_UPRC , tmp.PO_UNIT_ALLOW_FG , tmp.PO_UNIT_FG , tmp.PO_UNIT_QTY , DECODE(tmv.VAT_INCLD_YN, 'Y', 1, 0) AS VENDR_VAT_FG01 , TRUNC(DECODE(SIGN(tmp.SAFE_STOCK_QTY),-1,0,tmp.SAFE_STOCK_QTY) / tmp.PO_UNIT_QTY) AS SAFE_STOCK_UNIT_QTY , MOD (DECODE(SIGN(tmp.SAFE_STOCK_QTY),-1,0,tmp.SAFE_STOCK_QTY) , tmp.PO_UNIT_QTY) AS SAFE_STOCK_ETC_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_UNIT_QTY, 0, NULL, tpsvod.ORDER_UNIT_QTY*tpsvod.SLIP_FG)) AS PREV_ORDER_UNIT_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_ETC_QTY, 0, NULL, tpsvod.ORDER_ETC_QTY*tpsvod.SLIP_FG )) AS PREV_ORDER_ETC_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_TOT_QTY, 0, NULL, tpsvod.ORDER_TOT_QTY*tpsvod.SLIP_FG )) AS PREV_ORDER_TOT_QTY , TO_NUMBER(DECODE(tpsvod.ORDER_AMT , 0, NULL, tpsvod.ORDER_AMT*tpsvod.SLIP_FG )) AS ORDER_AMT , TO_NUMBER(DECODE(tpsvod.ORDER_VAT , 0, NULL, tpsvod.ORDER_VAT*tpsvod.SLIP_FG )) AS ORDER_VAT , TO_NUMBER(DECODE(tpsvod.ORDER_TOT , 0, NULL, tpsvod.ORDER_TOT*tpsvod.SLIP_FG )) AS ORDER_TOT
FROM TB_MS_VENDOR_PROD tmvp , TB_MS_PRODUCT tmp , TB_ST_STORE_STOCK_CUR tsssc , TB_PO_STORE_VENDR_ORDER_DTL tpsvod , TB_MS_VENDOR tmv WHERE tmvp.STORE_CD = #{storeCd} AND tmvp.VENDR_CD = #{vendrCd} AND tmp.STORE_CD = tmvp.STORE_CD AND tmp.PROD_CD = tmvp.PROD_CD AND tmp.USE_YN = 'Y' AND tsssc.STORE_CD (+) = tmp.STORE_CD AND tsssc.PROD_CD (+) = tmp.PROD_CD AND tpsvod.STORE_CD (+) = tmp.STORE_CD AND tpsvod.SLIP_NO (+) = #{slipNo} AND tpsvod.PROD_CD (+) = tmp.PROD_CD AND tmv.STORE_CD = tmvp.STORE_CD AND tmv.VENDR_CD = #{vendrCd}
AND tmp.PROD_CD = #{prodCd}
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND NVL(tsssc.CURR_QTY, 0) < NVL(tmp.SAFE_STOCK_QTY, 0)
ORDER BY tmp.PROD_CD

/* USE : VendrOrderMapper.getDtlProdExist */




        SELECT  DECODE(COUNT(tshvod.PROD_CD), 0, 'N', 'Y') AS IS_EXIST
        FROM    TB_PO_HQ_VENDR_ORDER_DTL tshvod
        WHERE   tshvod.HQ_OFFICE_CD = #{hqOfficeCd}
        AND     tshvod.SLIP_NO      = #{slipNo}
        AND     ROWNUM              = 1





        SELECT  DECODE(COUNT(tssvod.PROD_CD), 0, 'N', 'Y') AS IS_EXIST
        FROM    TB_PO_STORE_VENDR_ORDER_DTL tssvod
        WHERE   tssvod.STORE_CD = #{storeCd}
        AND     tssvod.SLIP_NO  = #{slipNo}
        AND     ROWNUM          = 1

/* USE : VendrOrderMapper.getVendrInstockExist */




        SELECT  DECODE(COUNT(tphvi.SLIP_NO), 0, 'N', 'Y') AS IS_EXIST
        FROM    TB_PO_HQ_VENDR_INSTOCK tphvi
        WHERE   tphvi.HQ_OFFICE_CD = #{hqOfficeCd}
        AND     tphvi.SLIP_NO      = #{slipNo}


            AND tphvi.PROC_FG = #{procFg}





        SELECT  DECODE(COUNT(tpsvi.SLIP_NO), 0, 'N', 'Y') AS IS_EXIST
        FROM    TB_PO_STORE_VENDR_INSTOCK tpsvi
        WHERE   tpsvi.STORE_CD  = #{storeCd}
        AND     tpsvi.SLIP_NO   = #{slipNo}


            AND tpsvi.PROC_FG = #{procFg}

mapper.insert:
/* USE : VendrOrderMapper.insertVendrOrderHd */
INSERT INTO TB_PO_HQ_VENDR_ORDER ( HQ_OFFICE_CD, SLIP_NO, SLIP_FG, VENDR_CD, PROC_FG, ORDER_TYPE, RTN_REASON_CD, ORDER_DATE, ORDER_REQ_DATE, DTL_CNT, ORDER_TOT_QTY, ORDER_AMT, ORDER_VAT, ORDER_TOT, IN_TOT_QTY, IN_AMT, IN_VAT, IN_TOT, REMARK, ORDER_REG_DT, ORDER_REG_ID, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{slipNo}, #{slipFg}, #{vendrCd}, #{procFg}, #{orderType}, #{rtnReasonCd}, #{orderDate}, #{orderReqDate}, 0, 0, 0, 0, 0, 0, 0, 0, 0, #{remark}, #{regDt}, #{regId}, #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_PO_STORE_VENDR_ORDER ( STORE_CD, SLIP_NO, SLIP_FG, VENDR_CD, PROC_FG, ORDER_TYPE, RTN_REASON_CD, ORDER_DATE, ORDER_REQ_DATE, DTL_CNT, ORDER_TOT_QTY, ORDER_AMT, ORDER_VAT, ORDER_TOT, IN_TOT_QTY, IN_AMT, IN_VAT, IN_TOT, REMARK, ORDER_REG_DT, ORDER_REG_ID, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{slipNo}, #{slipFg}, #{vendrCd}, #{procFg}, #{orderType}, #{rtnReasonCd}, #{orderDate}, #{orderReqDate}, 0, 0, 0, 0, 0, 0, 0, 0, 0, #{remark}, #{regDt}, #{regId}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* USE : VendrOrderMapper.updateVendrOrderHd */



        UPDATE  TB_PO_HQ_VENDR_ORDER
        SET     VENDR_CD        = #{vendrCd}
        ,       ORDER_TYPE      = #{orderType}
        ,       RTN_REASON_CD   = #{rtnReasonCd}
        ,       ORDER_DATE      = #{orderDate}
        ,       ORDER_REQ_DATE  = #{orderReqDate}
        ,       REMARK          = #{remark}
        ,       MOD_DT          = #{modDt}
        ,       MOD_ID          = #{modId}
        WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     SLIP_NO       = #{slipNo}



        UPDATE  TB_PO_STORE_VENDR_ORDER
        SET     VENDR_CD        = #{vendrCd}
        ,       ORDER_TYPE      = #{orderType}
        ,       RTN_REASON_CD   = #{rtnReasonCd}
        ,       ORDER_DATE      = #{orderDate}
        ,       ORDER_REQ_DATE  = #{orderReqDate}
        ,       REMARK          = #{remark}
        ,       MOD_DT          = #{modDt}
        ,       MOD_ID          = #{modId}
        WHERE   STORE_CD  = #{storeCd}
        AND     SLIP_NO   = #{slipNo}

/* USE : VendrOrderMapper.updateVendrOrderDtlSumHd */



        UPDATE  TB_PO_HQ_VENDR_ORDER
        SET     ( DTL_CNT, ORDER_TOT_QTY
                , ORDER_AMT, ORDER_VAT, ORDER_TOT)
            =   ( SELECT  COUNT(tphvod.PROD_CD), NVL(SUM(tphvod.ORDER_TOT_QTY),0)
                  ,       NVL(SUM(tphvod.ORDER_AMT),0), NVL(SUM(tphvod.ORDER_VAT),0), NVL(SUM(tphvod.ORDER_TOT),0)
                  FROM    TB_PO_HQ_VENDR_ORDER_DTL tphvod
                  WHERE   tphvod.HQ_OFFICE_CD  = #{hqOfficeCd}
                  AND     tphvod.SLIP_NO       = #{slipNo}
                )
        ,       MOD_DT = #{modDt}
        ,       MOD_ID = #{modId}
        WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     SLIP_NO       = #{slipNo}



        UPDATE  TB_PO_STORE_VENDR_ORDER
        SET     ( DTL_CNT, ORDER_TOT_QTY
                , ORDER_AMT, ORDER_VAT, ORDER_TOT)
            =   ( SELECT  COUNT(tpsvod.PROD_CD), NVL(SUM(tpsvod.ORDER_TOT_QTY),0)
                  ,       NVL(SUM(tpsvod.ORDER_AMT),0), NVL(SUM(tpsvod.ORDER_VAT),0), NVL(SUM(tpsvod.ORDER_TOT),0)
                  FROM    TB_PO_STORE_VENDR_ORDER_DTL tpsvod
                  WHERE   tpsvod.STORE_CD  = #{storeCd}
                  AND     tpsvod.SLIP_NO       = #{slipNo}
                )
        ,       MOD_DT = #{modDt}
        ,       MOD_ID = #{modId}
        WHERE   STORE_CD  = #{storeCd}
        AND     SLIP_NO   = #{slipNo}

mapper.delete:
/* USE : VendrOrderMapper.deleteVendrOrderHd */



        DELETE  TB_PO_HQ_VENDR_ORDER
        WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     SLIP_NO       = #{slipNo}



        DELETE  TB_PO_STORE_VENDR_ORDER
        WHERE   STORE_CD  = #{storeCd}
        AND     SLIP_NO   = #{slipNo}

mapper.update:
/* USE : VendrOrderMapper.updateProcFg */



        UPDATE  TB_PO_HQ_VENDR_ORDER
        SET     PROC_FG   = #{procFg}

            ,       CONFM_DT = #{modDt}
            ,       CONFM_ID = #{modId}


            ,       VENDR_CHK_DT = #{modDt}
            ,       VENDR_CHK_ID = #{modId}


            ,       END_DT = #{modDt}
            ,       END_ID = #{modId}

        ,       MOD_DT = #{modDt}
        ,       MOD_ID = #{modId}
        WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
        AND     SLIP_NO       = #{slipNo}



