mapper.select:
/* DayMembrMapper.getDayMembrList */ SELECT SALE_DATE, SALE_FG, MEMBR_NO, MEMBR_NM, MEMBR_CARD_NO, NVL(SUM(REAL_SALE_AMT) , 0) AS REAL_SALE_AMT, NVL(SUM(VAT_AMT) , 0) AS VAT_AMT,
SUM(PAY${item}) AS PAY${item} , NVL(SUM(VAT_AMT), 0) AS VAT_AMT, TO_CHAR(TO_DATE(MAX(REG_DT), 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS VISIT_DT FROM ( SELECT tsshp.HQ_OFFICE_CD, tsshp.HQ_BRAND_CD, tsshp.STORE_CD, tsshp.SALE_DATE, tsshp.SALE_FG, tsshm.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, tsshm.MEMBR_CARD_NO, tssh.REAL_SALE_AMT, tssh.VAT_AMT, tsshp.PAY_CD, tsshp.PAY_AMT, tsshm.REG_DT FROM TB_SL_SALE_HDR_MEMBR tsshm, TB_SL_SALE_HDR tssh, TB_SL_SALE_HDR_PAY tsshp, TB_MB_MEMBER tmm WHERE 1=1 AND tsshm.HQ_OFFICE_CD = #{hqOfficeCd} AND tsshm.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsshm.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsshm.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssh.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tssh.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tssh.STORE_CD = tsshm.STORE_CD AND tssh.SALE_DATE = tsshm.SALE_DATE AND tssh.POS_NO = tsshm.POS_NO AND tssh.BILL_NO = tsshm.BILL_NO AND tsshp.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tsshp.HQ_BRAND_CD = tssh.HQ_BRAND_CD AND tsshp.STORE_CD = tssh.STORE_CD AND tsshp.SALE_DATE = tssh.SALE_DATE AND tsshp.POS_NO = tssh.POS_NO AND tsshp.BILL_NO = tssh.BILL_NO AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmm.MEMBR_NO = tsshm.MEMBR_NO
AND tmm.MEMBR_NO LIKE '%'||#{membrNm}||'%'
) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) GROUP BY SALE_DATE, SALE_FG, MEMBR_NO, MEMBR_NM, MEMBR_CARD_NO

/* DayMembrMapper.getDayMembrPurchsList */ SELECT tssd.SALE_DATE, (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = tssd.STORE_CD AND HQ_OFFICE_CD = tssd.HQ_OFFICE_CD) AS STORE_NM, (SELECT PROD_NM FROM TB_MS_PRODUCT WHERE HQ_BRAND_CD = tssd.HQ_BRAND_CD AND STORE_CD = tssd.STORE_CD AND PROD_CD = tssd.PROD_CD) AS PROD_NM, tssd.SALE_QTY, tssd.REAL_SALE_AMT, tssd.MEMBR_SAVE_POINT, tssd.MEMBR_USE_POINT FROM TB_SL_SALE_HDR_MEMBR tsshm, TB_SL_SALE_DTL tssd WHERE 1=1 AND tsshm.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshm.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsshm.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsshm.SALE_DATE = #{saleDate} AND tsshm.MEMBR_NO = #{membrNo}
AND tsshm.MEMBR_CARD_NO = #{membrCardNo}
AND tssd.SALE_FG = #{saleFg}
AND tssd.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tssd.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tssd.STORE_CD = tsshm.STORE_CD AND tssd.SALE_DATE = tsshm.SALE_DATE AND tssd.POS_NO = tsshm.POS_NO AND tssd.BILL_NO = tsshm.BILL_NO ORDER BY tssd.SALE_DATE, tssd.STORE_CD, tssd.PROD_CD

/* DayMembrMapper.getDayMembrDetailPurchsList */ SELECT tsshm.SALE_DATE, (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = tssd.STORE_CD AND HQ_OFFICE_CD = tssd.HQ_OFFICE_CD) AS STORE_NM, (SELECT PROD_NM FROM TB_MS_PRODUCT WHERE HQ_BRAND_CD = tssd.HQ_BRAND_CD AND STORE_CD = tssd.STORE_CD AND PROD_CD = tssd.PROD_CD) AS PROD_NM, tssd.SALE_QTY, tssd.REAL_SALE_AMT, tssd.MEMBR_SAVE_POINT, tssd.MEMBR_USE_POINT FROM TB_SL_SALE_HDR_MEMBR tsshm, TB_SL_SALE_DTL tssd WHERE 1=1 AND tsshm.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshm.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsshm.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsshm.SALE_DATE = #{saleDate} AND tsshm.MEMBR_NO = #{membrNo} AND tssd.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tssd.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tssd.STORE_CD = tsshm.STORE_CD AND tssd.SALE_DATE = tsshm.SALE_DATE AND tssd.POS_NO = tsshm.POS_NO AND tssd.BILL_NO = tsshm.BILL_NO ORDER BY tsshm.SALE_DATE, tsshm.STORE_CD, tssd.PROD_CD
