mapper.select:
/* MembrProdMapper.getMembrProdList */ SELECT tsshm.MEMBR_NO, SBPENC.D ( tsshm.MEMBR_NM ) AS MEMBR_NM, tssd.PROD_CD, tmp.PROD_NM, SUM(tssd.SALE_QTY) AS SALE_QTY, SUM(tssd.SALE_AMT) AS SALE_AMT, SUM(tssd.DC_AMT) AS DC_AMT, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL tssd, TB_SL_SALE_HDR_MEMBR tsshm, TB_MS_PRODUCT tmp WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssd.STORE_CD = #{storeCd}
AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND SBPENC.D ( tsshm.MEMBR_NM ) LIKE '%'|| #{membrNm} ||'%' AND tssd.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tssd.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tssd.STORE_CD = tsshm.STORE_CD AND tssd.SALE_DATE = tsshm.SALE_DATE AND tssd.POS_NO = tsshm.POS_NO AND tssd.BILL_NO = tsshm.BILL_NO AND tmp.HQ_BRAND_CD = tssd.HQ_BRAND_CD AND tmp.STORE_CD = tssd.STORE_CD AND tmp.PROD_CD = tssd.PROD_CD GROUP BY tsshm.MEMBR_NO, tsshm.MEMBR_NM, tssd.PROD_CD, tmp.PROD_NM ORDER BY tsshm.MEMBR_NO, tsshm.MEMBR_NM, tssd.PROD_CD
