mapper.select:
/* MembrNonBilClctMapper.getMembrNonBilClctList */ SELECT tmmpb.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, SUM(tmmpb.POSTPAID_AMT) AS POSTPAID_AMT, SUM(tmmpb.POSTPAID_IN_AMT) AS POSTPAID_IN_AMT, SUM(tmmpb.POSTPAID_BAL_AMT) AS POSTPAID_BAL_AMT, SUM(tmmpbdt.POSTPAID_AMT_DT) AS POSTPAID_AMT_DT, SUM(tmmpbdt.POSTPAID_IN_AMT_DT) AS POSTPAID_IN_AMT_DT FROM TB_MB_MEMBER_PAID_BALANCE tmmpb, TB_MB_MEMBER tmm, ( SELECT MEMBR_NO, POSTPAID_AMT AS POSTPAID_AMT_DT, POSTPAID_IN_AMT AS POSTPAID_IN_AMT_DT FROM TB_MB_MEMBER_PAID_BALANCE WHERE 1=1 AND REG_DT BETWEEN #{startDate} AND #{endDate} ) tmmpbdt WHERE 1=1 AND tmmpb.HQ_OFFICE_CD = #{membrOrgnCd}
AND tmmpb.STORE_CD = #{storeCd}
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
AND tmmpb.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND tmmpb.MEMBR_NO = tmm.MEMBR_NO AND tmmpb.MEMBR_NO = tmmpbdt.MEMBR_NO AND tmmpb.HQ_OFFICE_CD = tmm.MEMBR_ORGN_CD GROUP BY tmmpb.HQ_OFFICE_CD, tmmpb.STORE_CD, tmmpb.MEMBR_NO, tmm.MEMBR_NM ORDER BY tmmpb.MEMBR_NO

/* MembrNonBilClctMapper.getMembrNonBilClctDetailList */
SELECT
TO_CHAR(TO_DATE(tmmpb.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS REG_DT,
tmmpb.POSTPAID_AMT,
tmmpb.POSTPAID_IN_AMT
FROM TB_MB_MEMBER_PAID_BALANCE tmmpb
WHERE 1=1
AND tmmpb.HQ_OFFICE_CD = #{membrOrgnCd}


        AND tmmpb.STORE_CD = #{storeCd}


AND tmmpb.MEMBR_NO = #{membrNo}
AND tmmpb.REG_DT BETWEEN #{startDate} AND #{endDate}
ORDER BY tmmpb.REG_DT
