mapper.select:
/* MembrNonBilClctMapper.getMembrNonBilClctList */ SELECT tmmpb.MEMBR_NO , SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM , NVL(tmmpb.POSTPAID_AMT,0) AS POSTPAID_AMT , NVL(tmmpb.POSTPAID_IN_AMT,0) AS POSTPAID_IN_AMT , NVL(tmmpb.POSTPAID_BAL_AMT,0) AS POSTPAID_BAL_AMT , NVL(tmmpbdt.POSTPAID_AMT_DT,0) AS POSTPAID_AMT_DT , NVL(tmmpbdt.POSTPAID_IN_AMT_DT,0) AS POSTPAID_IN_AMT_DT FROM ( SELECT A.MEMBR_NO , SUM(A.POSTPAID_AMT) AS POSTPAID_AMT , SUM(A.POSTPAID_IN_AMT) AS POSTPAID_IN_AMT , SUM(A.POSTPAID_BAL_AMT) AS POSTPAID_BAL_AMT FROM TB_MB_MEMBER_PAID_BALANCE A WHERE 1=1
AND EXISTS ( SELECT 1 FROM TB_MB_MEMBER_PAID_BALANCE B WHERE B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.STORE_CD = A.STORE_CD AND B.MEMBR_NO = A.MEMBR_NO AND B.MOD_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959' )
AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR A.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND A.MEMBR_NO LIKE '%'||#{membrNo}||'%'
GROUP BY A.MEMBR_NO , A.HQ_OFFICE_CD ) tmmpb , TB_MB_MEMBER tmm , ( SELECT MEMBR_NO , SUM(CASE WHEN POSTPAID_FG IN ('3', '4') THEN POSTPAID_AMT ELSE 0 END) AS POSTPAID_AMT_DT , SUM(CASE WHEN POSTPAID_FG IN ('1', '2') THEN POSTPAID_AMT ELSE 0 END) AS POSTPAID_IN_AMT_DT FROM TB_MB_MEMBER_POSTPAID WHERE 1=1 AND SALE_DATE BETWEEN #{startDate} AND #{endDate} AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND MEMBR_NO LIKE '%'||#{membrNo}||'%'
GROUP BY MEMBR_NO , HQ_OFFICE_CD ) tmmpbdt WHERE 1=1 AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmpb.MEMBR_NO = tmm.MEMBR_NO
AND tmmpb.MEMBR_NO = tmmpbdt.MEMBR_NO
AND tmmpb.MEMBR_NO = tmmpbdt.MEMBR_NO (+)
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
ORDER BY tmmpb.MEMBR_NO

/* MembrNonBilClctMapper.getMembrNonBilClctDetailList */
SELECT  TO_CHAR(TO_DATE(A.SALE_DATE , 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE
,       SUM(CASE WHEN POSTPAID_FG IN ('3', '4') THEN POSTPAID_AMT ELSE 0 END)   AS POSTPAID_AMT
,       SUM(CASE WHEN POSTPAID_FG IN ('1', '2') THEN POSTPAID_AMT ELSE 0 END)   AS POSTPAID_IN_AMT
FROM    TB_MB_MEMBER_POSTPAID A
WHERE   A.HQ_OFFICE_CD  =   #{hqOfficeCd}
AND     A.MEMBR_NO      =   #{membrNo}

    AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate}


    AND A.STORE_CD = #{storeCd}


    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR	A.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )

GROUP
BY      A.SALE_DATE
ORDER
BY      A.SALE_DATE
