mapper.select:
/* MembrNonBilClctMapper.getMembrNonBilClctList */ SELECT tmmpb.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, tmmpb.POSTPAID_AMT AS POSTPAID_AMT, tmmpb.POSTPAID_IN_AMT AS POSTPAID_IN_AMT, tmmpb.POSTPAID_BAL_AMT AS POSTPAID_BAL_AMT, tmmpbdt.POSTPAID_AMT_DT AS POSTPAID_AMT_DT, tmmpbdt.POSTPAID_IN_AMT_DT AS POSTPAID_IN_AMT_DT FROM ( SELECT MEMBR_NO, HQ_OFFICE_CD, SUM(POSTPAID_AMT) AS POSTPAID_AMT, SUM(POSTPAID_IN_AMT) AS POSTPAID_IN_AMT, SUM(POSTPAID_BAL_AMT) AS POSTPAID_BAL_AMT FROM TB_MB_MEMBER_PAID_BALANCE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND MEMBR_NO LIKE '%'||#{membrNo}||'%'
GROUP BY MEMBR_NO, HQ_OFFICE_CD ) tmmpb, TB_MB_MEMBER tmm, ( SELECT MEMBR_NO, SUM(POSTPAID_AMT) AS POSTPAID_AMT_DT, SUM(POSTPAID_IN_AMT) AS POSTPAID_IN_AMT_DT FROM TB_MB_MEMBER_PAID_BALANCE WHERE REG_DT BETWEEN #{startDate} AND #{endDate} AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND MEMBR_NO LIKE '%'||#{membrNo}||'%'
GROUP BY MEMBR_NO, HQ_OFFICE_CD ) tmmpbdt WHERE 1=1 AND tmmpb.MEMBR_NO = tmm.MEMBR_NO AND tmmpb.MEMBR_NO = tmmpbdt.MEMBR_NO AND tmmpb.HQ_OFFICE_CD = tmm.MEMBR_ORGN_CD
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
ORDER BY tmmpb.MEMBR_NO

/* MembrNonBilClctMapper.getMembrNonBilClctDetailList */
SELECT
    TO_CHAR(TO_DATE(tmmpb.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS REG_DT,
    tmmpb.POSTPAID_AMT,
    tmmpb.POSTPAID_IN_AMT
FROM TB_MB_MEMBER_PAID_BALANCE tmmpb
WHERE 1=1
AND tmmpb.HQ_OFFICE_CD = #{hqOfficeCd}


        AND tmmpb.STORE_CD = #{storeCd}



    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR	tmmpb.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )

AND tmmpb.MEMBR_NO = #{membrNo}
AND tmmpb.REG_DT BETWEEN #{startDate} AND #{endDate}
ORDER BY tmmpb.REG_DT
