mapper.select:
/* MembrPointMapper.getMembrPointList */ SELECT (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = tssd.STORE_CD) AS STORE_NM, TO_CHAR(TO_DATE(tssd.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS REG_DT, tsshm.MEMBR_NO, tsshm.MEMBR_NM, (SELECT MEMBR_CLASS_NM FROM TB_MB_MEMBER_CLASS WHERE MEMBR_ORGN_CD = tmm.REG_STORE_CD AND MEMBR_CLASS_CD = tmm.MEMBR_CLASS_CD) AS MEMBR_CLASS_NM, SUM(tssd.MEMBR_SAVE_POINT) AS MEMBR_SAVE_POINT, SUM(tssd.MEMBR_USE_POINT) AS MEMBR_USE_POINT, SUM(tssd.SALE_AMT) AS SALE_AMT, SUM(tssd.DC_AMT) AS DC_AMT, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_HDR_MEMBR tsshm, TB_SL_SALE_DTL tssd, TB_MB_MEMBER tmm WHERE 1=1 AND tsshm.HQ_OFFICE_CD = #{membrOrgnCd}
AND tsshm.STORE_CD = #{storeCd}
AND tssd.REG_DT BETWEEN #{startDate} AND #{endDate}
AND tssd.STORE_CD IN
#{item}
AND tsshm.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND tsshm.MEMBR_NM LIKE '%'||#{membrNm}||'%'
AND tsshm.HQ_OFFICE_CD = tssd.HQ_OFFICE_CD AND tsshm.HQ_OFFICE_CD = tmm.MEMBR_ORGN_CD AND tsshm.HQ_BRAND_CD = tssd.HQ_BRAND_CD AND tsshm.STORE_CD = tssd.STORE_CD AND tsshm.SALE_DATE = tssd.SALE_DATE AND tsshm.POS_NO = tssd.POS_NO AND tsshm.BILL_NO = tssd.BILL_NO AND tsshm.STORE_CD = tmm.REG_STORE_CD AND tsshm.MEMBR_NO = tmm.MEMBR_NO AND tsshm.MEMBR_NM = tmm.MEMBR_NM GROUP BY tssd.STORE_CD, tssd.REG_DT, tsshm.MEMBR_NO, tsshm.MEMBR_NM, tmm.REG_STORE_CD, tmm.MEMBR_CLASS_CD
ORDER BY tssd.STORE_CD, tssd.REG_DT, tsshm.MEMBR_NO
ORDER BY tssd.STORE_CD, tsshm.MEMBR_NO, tssd.REG_DT
ORDER BY tsshm.MEMBR_NO, tssd.REG_DT, tssd.STORE_CD
ORDER BY tsshm.MEMBR_NO, tssd.STORE_CD, tssd.REG_DT
ORDER BY tssd.REG_DT, tssd.STORE_CD, tsshm.MEMBR_NO
ORDER BY tssd.REG_DT, tsshm.MEMBR_NO, tssd.STORE_CD
