mapper.select:
/* PeriodMembrMapper.getPeriodMembrList */ SELECT tsshm.SALE_DATE, tsshm.MEMBR_NO, tmm.MEMBR_NM, MAX(tsshm.MEMBR_CARD_NO) AS MEMBR_CARD_NO, COUNT(tsshm.MEMBR_NO) AS SALE_COUNT, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tssd.DC_AMT) AS DC_AMT, SUM(tssd.MEMBR_SAVE_POINT) AS MEMBR_SAVE_POINT, SUM(tssd.MEMBR_USE_POINT) AS MEMBR_USE_POINT, TO_CHAR(TO_DATE(MIN(tsshm.REG_DT), 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MIN_REG_DT, TO_CHAR(TO_DATE(MAX(tsshm.REG_DT), 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MAX_REG_DT FROM TB_SL_SALE_HDR_MEMBR tsshm, TB_MB_MEMBER tmm, TB_SL_SALE_DTL tssd WHERE 1 = 1 AND tsshm.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshm.STORE_CD = #{storeCd}
AND tsshm.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tssd.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tssd.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tssd.STORE_CD = tsshm.STORE_CD AND tssd.SALE_DATE = tsshm.SALE_DATE AND tssd.POS_NO = tsshm.POS_NO AND tssd.BILL_NO = tsshm.BILL_NO AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmm.MEMBR_NO = tsshm.MEMBR_NO GROUP BY tsshm.SALE_DATE, tsshm.MEMBR_NO, tmm.MEMBR_NM ORDER BY tsshm.MEMBR_NO
