mapper.select:
/* PeriodMembrMapper.getPeriodMembrList */ SELECT tsshm.SALE_DATE, tsshm.MEMBR_NO, tmm.MEMBR_NM, tsshm.MEMBR_CARD_NO, COUNT(tsshm.MEMBR_NO) AS SALE_COUNT, SUM(tsdmp.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdmp.TOT_DC_AMT) AS DC_AMT, SUM(tsdmp.MEMBR_SAVE_POINT) AS MEMBR_SAVE_POINT, SUM(tsdmp.MEMBR_USE_POINT) AS MEMBR_USE_POINT, MIN(tsshm.MIN_REG_DT) AS MIN_REG_DT, MIN(tsshm.MAX_REG_DT) AS MAX_REG_DT FROM ( SELECT HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, SALE_DATE, MEMBR_NO, MAX(MEMBR_CARD_NO) AS MEMBR_CARD_NO, TO_CHAR(TO_DATE(MIN(REG_DT), 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MIN_REG_DT, TO_CHAR(TO_DATE(MAX(REG_DT), 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MAX_REG_DT FROM TB_SL_SALE_HDR_MEMBR WHERE 1 = 1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, SALE_DATE, MEMBR_NO ORDER BY SALE_DATE, MEMBR_NO ) tsshm, TB_MB_MEMBER tmm, TB_SL_DAILY_MEMBR_PROD tsdmp WHERE 1 = 1 AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmm.MEMBR_NO = tsshm.MEMBR_NO AND tsdmp.HQ_OFFICE_CD = tsshm.HQ_OFFICE_CD AND tsdmp.HQ_BRAND_CD = tsshm.HQ_BRAND_CD AND tsdmp.STORE_CD = tsshm.STORE_CD AND tsdmp.SALE_DATE = tsshm.SALE_DATE AND tsdmp.MEMBR_NO = tsshm.MEMBR_NO GROUP BY tsshm.SALE_DATE, tsshm.MEMBR_NO, tmm.MEMBR_NM, tsshm.MEMBR_CARD_NO ORDER BY tsshm.SALE_DATE, tsshm.MEMBR_NO
