mapper.select:
/* PostpaidMapper.getPostpaidMemberList */
SELECT tmmc.STORE_CD, tmmc.STORE_NM, tmmc.SALE_DATE, tmmc.MEMBR_NO, tmmc.MEMBR_NM, tmmc.SALE_AMT, tmmc.POSTPAID_AMT, tmmc.POSTPAID_FG, tmmc.POSTPAID_PAY_FG
FROM ( SELECT tsspc.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{membrOrgnCd} AND STORE_CD = tsspc.STORE_CD) AS STORE_NM, TO_CHAR(TO_DATE(tsspc.SALE_DATE), 'YYYY-MM-DD') AS SALE_DATE, tsspc.MEMBR_NO, (SELECT MEMBR_NM FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = #{membrOrgnCd} AND MEMBR_NO = tsspc.MEMBR_NO) AS MEMBR_NM, tsspc.SALE_AMT, 0 AS POSTPAID_AMT, '' AS POSTPAID_FG, '' AS POSTPAID_PAY_FG FROM TB_SL_SALE_PAY_CREDIT tsspc UNION SELECT tmmc.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{membrOrgnCd} AND STORE_CD = tmmc.STORE_CD) AS STORE_CD, TO_CHAR(TO_DATE(tmmc.SALE_DATE), 'YYYY-MM-DD') AS SALE_DATE, tmmc.MEMBR_NO, (SELECT MEMBR_NM FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = #{membrOrgnCd} AND MEMBR_NO = tmmc.MEMBR_NO) AS MEMBR_NM, 0 AS SALE_AMT, tmmc.POSTPAID_AMT, tmmc.POSTPAID_FG, tmmc.POSTPAID_PAY_FG FROM TB_MB_MEMBER_POSTPAID tmmc ) tmmc
AND tmmc.STORE_CD IN
#{item}
AND tmmc.MEMBR_NO = #{membrNo}
AND tmmc.MEMBR_NM = #{membrNm}
ORDER BY tmmc.STORE_CD, tmmc.SALE_DATE, tmmc.MEMBR_NO
ORDER BY tmmc.STORE_CD, tmmc.MEMBR_NO, tmmc.SALE_DATE
ORDER BY tmmc.MEMBR_NO, tmmc.SALE_DATE, tmmc.STORE_CD
ORDER BY tmmc.MEMBR_NO, tmmc.STORE_CD, tmmc.SALE_DATE
ORDER BY tmmc.SALE_DATE, tmmc.STORE_CD, tmmc.MEMBR_NO
ORDER BY tmmc.SALE_DATE, tmmc.MEMBR_NO, tmmc.STORE_CD

/* PostpaidMapper.getDepositMemberList */
SELECT *
  FROM (
  SELECT tmmcs.MEMBR_ORGN_CD,
         tmmcs.MEMBR_NO AS MEMBR_NO,
         (SELECT MEMBR_NM FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = tmmcs.MEMBR_ORGN_CD AND MEMBR_NO = tmmcs.MEMBR_NO) AS MEMBR_NM,
         tmmcs.STORE_CD,
         (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = tmmcs.MEMBR_ORGN_CD AND STORE_CD = tmmcs.STORE_CD) AS STORE_NM
    FROM TB_MB_MEMBER_POSTPAID_STORE tmmcs
   WHERE MEMBR_ORGN_CD = #{membrOrgnCd}
       ) tmmcs


    AND tmmcs.STORE_CD = #{storeCd}


    AND tmmcs.STORE_NM = #{storeNm}


    AND tmmcs.MEMBR_NO = #{membrNo}


    AND tmmcs.MEMBR_NM = #{membrNm}


    AND tmmcs.STORE_CD != #{defaultStoreCd}
