mapper.select:
/* PostpaidMapper.getPostpaidMemberList */
SELECT tmmpb.HQ_OFFICE_CD, tmmpb.STORE_CD, tms.STORE_NM, tmmpb.MEMBR_NO, tmm.MEMBR_NM, tmmpb.POSTPAID_AMT, tmmpb.POSTPAID_IN_AMT, tmmpb.POSTPAID_BAL_AMT
FROM TB_MB_MEMBER_PAID_BALANCE tmmpb, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmm.MEMBR_ORGN_CD = tmmpb.HQ_OFFICE_CD AND tmm.MEMBR_NO = tmmpb.MEMBR_NO AND tms.HQ_OFFICE_CD = tmmpb.HQ_OFFICE_CD AND tms.STORE_CD = tmmpb.STORE_CD
AND tmmpb.STORE_CD IN
#{item}
AND tmmpb.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND tmmpb.STORE_CD = #{storeCd}
AND tmm.MEMBR_NM LIKE '%'|| #{membrNm} ||'%'
ORDER BY tmmpb.MOD_DT DESC, tmmpb.STORE_CD, tmmpb.MEMBR_NO

mapper.insert:
/* PostpaidMapper.saveDeposit */
SELECT #{storeCd} AS STORE_CD FROM DUAL
SELECT REG_STORE_CD AS STORE_CD FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = #{membrOrgnCd} AND MEMBR_NO = #{membrNo}
INSERT INTO TB_MB_MEMBER_POSTPAID ( HQ_OFFICE_CD, STORE_CD, SALE_DATE, POSTPAID_NO, MEMBR_NO, POSTPAID_DT, POSTPAID_FG, POSTPAID_PAY_FG, POSTPAID_AMT, NONSALE_TYPE_APPR_NO, ORG_NONSALE_TYPE_APPR_NO, NONSALE_BILL_NO, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{membrOrgnCd}, #{storeCd}, #{saleDate}, (SELECT LPAD(NVL(MAX(POSTPAID_NO), '0')+1, 4, '0') FROM TB_MB_MEMBER_POSTPAID WHERE HQ_OFFICE_CD = #{membrOrgnCd} AND STORE_CD = #{storeCd} AND SALE_DATE = #{saleDate} ), #{membrNo}, #{postpaidDt}, #{postpaidFg, jdbcType=VARCHAR}, #{postpaidPayFg, jdbcType=VARCHAR}, #{postpaidAmt}, #{nonsaleTypeApprNo}, #{orgNonsaleTypeApprNo}, #{nonsaleBillNo}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.select:
/* PostpaidMapper.getTaxBillList */
SELECT tmmtb.BILL_DATE, tmmtb.BILL_SEQ, tmmtb.BILL_DATE || BILL_SEQ AS BILL_NO, tmmtb.MEMBR_ORGN_CD, tmmtb.MEMBR_NO, tmm.MEMBR_NM, TO_CHAR(to_date(tmmtb.REQUEST_DT, 'yyyyMMddhh24miss'), 'yyyy-MM-dd hh24:mi:ss') AS REQUEST_DT, tmmtb.REQUEST_AMT, tmmtb.STATUS_FG, tmmtb.REG_DT, tmmtb.REG_ID, tmmtb.MOD_DT, tmmtb.MOD_ID
FROM TB_MB_MEMBER_TAX_BILL tmmtb, TB_MB_MEMBER tmm WHERE tmmtb.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmtb.STATUS_FG = #{statusFg} AND tmm.MEMBR_ORGN_CD = tmmtb.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmtb.MEMBR_NO ORDER BY tmmtb.BILL_DATE ASC, tmmtb.BILL_SEQ, tmmtb.MEMBR_NO

mapper.update:
/* PostpaidMapper.saveTaxBillComplete */
UPDATE TB_MB_MEMBER_TAX_BILL tmmtb
   SET tmmtb.STATUS_FG = #{statusFg},
       tmmtb.MOD_DT = #{modDt},
       tmmtb.MOD_ID = #{modId}
 WHERE tmmtb.BILL_DATE = #{billDate}
   AND tmmtb.BILL_SEQ = #{billSeq}
   AND tmmtb.MEMBR_ORGN_CD = #{membrOrgnCd}
