mapper.select:
/* PostpaidMapper.getPostpaidMemberList */
SELECT tmmpb.HQ_OFFICE_CD, tmmpb.STORE_CD, tms.STORE_NM, tmmpb.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, tmmpb.POSTPAID_AMT, tmmpb.POSTPAID_IN_AMT, tmmpb.POSTPAID_BAL_AMT
FROM TB_MB_MEMBER_PAID_BALANCE tmmpb, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE 1=1 AND tmmpb.HQ_OFFICE_CD = #{hqOfficeCd}
AND tmmpb.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tmmpb.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tmmpb.STORE_CD = #{storeCd}
AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmm.MEMBR_NO = tmmpb.MEMBR_NO AND tms.HQ_OFFICE_CD = tmmpb.HQ_OFFICE_CD AND tms.STORE_CD = tmmpb.STORE_CD
AND tmmpb.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'
ORDER BY tmmpb.MOD_DT DESC, tmmpb.STORE_CD, tmmpb.MEMBR_NO

/* PostpaidMapper.getDepositMemberList */
SELECT tmmcs.MEMBR_ORGN_CD, tmmcs.MEMBR_NO, SBPENC.D(tmmcs.MEMBR_NM) AS MEMBR_NM, tmmcs.STORE_CD, tmmcs.STORE_NM
FROM ( SELECT tmmcs.MEMBR_ORGN_CD, tmmcs.MEMBR_NO AS MEMBR_NO, (SELECT MEMBR_NM FROM TB_MB_MEMBER WHERE MEMBR_ORGN_CD = tmmcs.MEMBR_ORGN_CD AND MEMBR_NO = tmmcs.MEMBR_NO) AS MEMBR_NM, tmmcs.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = tmmcs.MEMBR_ORGN_CD AND STORE_CD = tmmcs.STORE_CD) AS STORE_NM FROM TB_MB_MEMBER_POSTPAID_STORE tmmcs WHERE MEMBR_ORGN_CD = #{membrOrgnCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tmmcs.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tmmcs.STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND tmmcs.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND tmmcs.STORE_CD != #{defaultStoreCd}
) tmmcs WHERE 1=1
AND tmmcs.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND SBPENC.D(tmmcs.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'
ORDER BY tmmcs.STORE_CD, tmmcs.MEMBR_NO

select.if:
SELECT REG_STORE_CD AS STORE_CD
FROM TB_MB_MEMBER
WHERE MEMBR_ORGN_CD = #{membrOrgnCd}
AND MEMBR_NO = #{membrNo}

mapper.select:
/* PostpaidMapper.getTaxBillList */
SELECT tmmtb.BILL_DATE, tmmtb.BILL_SEQ, tmmtb.BILL_DATE || BILL_SEQ AS BILL_NO, tmmtb.MEMBR_ORGN_CD, tmmtb.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, TO_CHAR(to_date(tmmtb.REQUEST_DT, 'yyyyMMddhh24miss'), 'yyyy-MM-dd hh24:mi:ss') AS REQUEST_DT, tmmtb.REQUEST_AMT, tmmtb.STATUS_FG, tmmtb.REG_DT, tmmtb.REG_ID, tmmtb.MOD_DT, tmmtb.MOD_ID
FROM TB_MB_MEMBER_TAX_BILL tmmtb, TB_MB_MEMBER tmm WHERE tmmtb.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmtb.STATUS_FG = #{statusFg} AND tmm.MEMBR_ORGN_CD = tmmtb.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmtb.MEMBR_NO ORDER BY tmmtb.BILL_DATE ASC, tmmtb.BILL_SEQ, tmmtb.MEMBR_NO

mapper.update:
/* PostpaidMapper.saveTaxBillComplete */
UPDATE TB_MB_MEMBER_TAX_BILL tmmtb
   SET tmmtb.STATUS_FG = #{statusFg},
       tmmtb.MOD_DT = #{modDt},
       tmmtb.MOD_ID = #{modId}
 WHERE tmmtb.BILL_DATE = #{billDate}
   AND tmmtb.BILL_SEQ = #{billSeq}
   AND tmmtb.MEMBR_ORGN_CD = #{membrOrgnCd}
