mapper.select:
/* PrepaidMapper.getPrepaidMemberList */
SELECT b.STORE_CD, b.STORE_NM, b.MEMBR_NO, b.MEMBR_NM, a.PREPAID_AMT, a.PREPAID_USE_AMT, a.PREPAID_BAL_AMT
FROM TB_MB_MEMBER_PAID_BALANCE a, ( SELECT tmmp.STORE_CD, tms.STORE_NM, tmmp.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM FROM TB_MB_MEMBER_PREPAID_STORE tmmp, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE 1=1 AND tmmp.MEMBR_ORGN_CD = #{membrOrgnCd}
AND
tmmp.STORE_CD = #{item}
AND tmmp.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tmmp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tmm.MEMBR_ORGN_CD = tmmp.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmp.MEMBR_NO AND tms.STORE_CD = tmmp.STORE_CD
AND tmmp.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'
) b WHERE A.STORE_CD(+) = B.STORE_CD AND A.MEMBR_NO(+) = B.MEMBR_NO
AND A.PREPAID_BAL_AMT IS NOT NULL
AND A.PREPAID_BAL_AMT IS NULL
ORDER BY a.MOD_DT DESC NULLS LAST, b.STORE_CD, b.MEMBR_NO

/* PrepaidMapper.getPrepaidMemberListExcel */ SELECT b.STORE_CD, b.STORE_NM, b.MEMBR_NO, b.MEMBR_NM, a.PREPAID_AMT, a.PREPAID_USE_AMT, a.PREPAID_BAL_AMT FROM TB_MB_MEMBER_PAID_BALANCE a, ( SELECT tmmp.STORE_CD, tms.STORE_NM, tmmp.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM FROM TB_MB_MEMBER_PREPAID_STORE tmmp, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE 1=1 AND tmmp.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmp.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE MEMBR_ORGN_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tmmp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tmm.MEMBR_ORGN_CD = tmmp.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmp.MEMBR_NO AND tms.STORE_CD = tmmp.STORE_CD
AND tmm.REG_STORE_CD != #{defaultStoreCd}
AND tmm.REG_STORE_CD = #{prepaidStoreCd}
AND tms.STORE_NM LIKE '%'|| #{prepaidStoreNm} ||'%'
AND tmmp.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'
) b WHERE A.STORE_CD(+) = B.STORE_CD AND A.MEMBR_NO(+) = B.MEMBR_NO
AND A.PREPAID_BAL_AMT IS NOT NULL
AND A.PREPAID_BAL_AMT IS NULL
ORDER BY a.MOD_DT DESC NULLS LAST, b.STORE_CD, b.MEMBR_NO

/* PrepaidMapper.getChargeMemberList */ SELECT tmmp.STORE_CD AS STORE_CD, tms.STORE_NM, tmmp.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM FROM TB_MB_MEMBER_PREPAID_STORE tmmp, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE 1=1 AND tmmp.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmp.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tmmp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tmm.MEMBR_ORGN_CD = tmmp.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmp.MEMBR_NO AND tms.STORE_CD = tmmp.STORE_CD
AND tmm.REG_STORE_CD != #{defaultStoreCd}
AND tmm.REG_STORE_CD = #{prepaidStoreCd}
AND tms.STORE_NM LIKE '%'|| #{prepaidStoreNm} ||'%'
AND tmmp.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'|| #{membrNm} ||'%'
ORDER BY tmmp.STORE_CD, tmmp.MEMBR_NO
