mapper.select:
/* PrepaidMapper.getPrepaidMemberList */
SELECT tmmpb.HQ_OFFICE_CD, tmmpb.STORE_CD, tms.STORE_NM, tmmpb.MEMBR_NO, tmm.MEMBR_NM, tmmpb.PREPAID_AMT, tmmpb.PREPAID_USE_AMT, tmmpb.PREPAID_BAL_AMT
FROM TB_MB_MEMBER_PAID_BALANCE tmmpb, TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE tmm.MEMBR_ORGN_CD = #{hqOfficeCd} AND tmm.MEMBR_ORGN_CD = tmmpb.HQ_OFFICE_CD AND tmm.MEMBR_NO = tmmpb.MEMBR_NO AND tms.HQ_OFFICE_CD = tmmpb.HQ_OFFICE_CD AND tms.STORE_CD = tmmpb.STORE_CD
AND tmmpb.STORE_CD IN
#{item}
AND tmmpb.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND tmmpb.STORE_CD = #{storeCd}
AND tmm.MEMBR_NM LIKE '%'|| #{membrNm} ||'%'
ORDER BY tmmpb.MOD_DT DESC, tmmpb.STORE_CD, tmmpb.MEMBR_NO

/* PrepaidMapper.getChargeMemberList */ SELECT tmm.REG_STORE_CD AS STORE_CD, tms.STORE_NM, tmm.MEMBR_NO, tmm.MEMBR_NM FROM TB_MB_MEMBER tmm, TB_MS_STORE tms WHERE tms.STORE_CD = tmm.REG_STORE_CD AND MEMBR_ORGN_CD = #{hqOfficeCd}
AND tmm.REG_STORE_CD != #{defaultStoreCd}
AND tmm.REG_STORE_CD = #{prepaidStoreCd}
AND tms.STORE_NM LIKE '%'|| #{prepaidStoreNm} ||'%'
AND tmm.MEMBR_NO LIKE '%'|| #{membrNo} ||'%'
AND tmm.MEMBR_NM LIKE '%'|| #{membrNm} ||'%'
