mapper.select:
/* DlvrMapper.getDlvrLzoneList */ SELECT tmdl.DLVR_LZONE_CD AS VALUE, tmdl.DLVR_LZONE_NM AS NAME FROM TB_MS_DELIVERY_LZONE tmdl WHERE 1 = 1
AND tmdl.STORE_CD = #{storeCd}
ORDER BY DLVR_LZONE_CD ASC

/* DlvrMapper.getDlvrMzoneList */
SELECT tmdm.DLVR_MZONE_CD AS VALUE,
tmdm.DLVR_MZONE_NM AS NAME
FROM TB_MS_DELIVERY_MZONE tmdm
WHERE tmdm.DLVR_LZONE_CD = #{dlvrLzoneCd}

    AND tmdm.STORE_CD = #{regStoreCd}

ORDER BY DLVR_MZONE_CD ASC

/*DlvrMapper.getDlvrList*/
SELECT tmmda.MEMBR_ORGN_CD , tmmda.MEMBR_NO , SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM , tmmda.DLVR_ADDR_SEQ , tmmda.DLVR_STORE_CD , tmmda.DLVR_LZONE_CD , tmmda.DLVR_MZONE_CD , SBPENC.D(tmmda.ADDR) AS ADDR , SBPENC.D(tmmda.ADDR_DTL) AS ADDR_DTL , tmmda.LAST_DLVR_DATE , tmmda.TOT_DLVR_CNT , tmmda.USE_YN , TO_CHAR(TO_DATE(tmmda.REG_DT, 'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') AS REG_DT , tmmda.REG_ID , TO_CHAR(TO_DATE(tmmda.MOD_DT, 'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') AS MOD_DT , tmmda.MOD_ID
FROM TB_MB_MEMBER_DLVR_ADDR tmmda, TB_MB_MEMBER tmm WHERE tmmda.MEMBR_NO = tmm.MEMBR_NO AND tmmda.MEMBR_ORGN_CD = tmm.MEMBR_ORGN_CD
AND tmmda.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmda.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND SBPENC.D(tmm.MEMBR_NM ) LIKE '%'||#{membrNm}||'%'
AND tmm.MEMBR_CLASS_CD = #{membrClassCd}
AND tmmda.DLVR_LZONE_CD = #{dlvrLzoneCd}
AND tmmda.DLVR_MZONE_CD = #{dlvrMzoneCd}
AND tmmda.DLVR_STORE_CD LIKE '%'|| #{dlvrStoreCd}||'%'
AND SBPENC.D(tmmda.ADDR_DTL) LIKE '%'|| #{addrDtl}||'%'
AND tmmda.USE_YN = #{useYn}
ORDER BY tmmda.MEMBR_ORGN_CD, tmmda.MEMBR_NO

mapper.delete:
/*DlvrMapper.deleteDlvr*/
/*배달주소지 삭제*/
DELETE FROM  TB_MB_MEMBER_DLVR_ADDR tmmda
WHERE tmmda.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmda.MEMBR_NO = #{membrNo}
AND tmmda.DLVR_ADDR_SEQ = #{dlvrAddrSeq}

mapper.update:
/*DlvrMapper.updateDlvr*/
/*배달지주소 저장*/
UPDATE TB_MB_MEMBER_DLVR_ADDR tmmda
SET
tmmda.ADDR_DTL = SBPENC.E(#{addrDtl})
, tmmda.USE_YN = #{useYn}
  , tmmda.MOD_ID = #{modId}
  , tmmda.MOD_DT = #{modDt}
WHERE tmmda.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmda.MEMBR_NO = #{membrNo}
AND tmmda.DLVR_ADDR_SEQ = #{dlvrAddrSeq}

mapper.select:
/*DlvrMapper.getDlvrTelList*/ /*배달전화번호 조회*/
SELECT tmmdt.MEMBR_ORGN_CD , tmmdt.MEMBR_NO , SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM , tmmdt.DLVR_TEL_SEQ , tmmdt.DLVR_STORE_CD , SBPENC.D(tmmdt.TEL_NO) AS TEL_NO , tmmdt.SHORT_NO , tmmdt.USE_YN , TO_CHAR(TO_DATE(tmmdt.REG_DT, 'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss') AS REG_DT , tmmdt.REG_ID , TO_CHAR(TO_DATE(tmmdt.MOD_DT, 'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss') AS MOD_DT , tmmdt.MOD_ID
FROM TB_MB_MEMBER_DLVR_TEL tmmdt, TB_MB_MEMBER tmm WHERE 1=1 AND tmm.MEMBR_ORGN_CD (+)= tmmdt.MEMBR_ORGN_CD AND tmm.MEMBR_NO (+)= tmmdt.MEMBR_NO
AND tmmdt.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmdt.MEMBR_NO = #{membrNo}
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
AND tmm.MEMBR_CLASS_CD LIKE '%'|| #{membrClassNo}||'%'
AND SBPENC.D(tmmdt.TEL_NO) LIKE '%'|| #{telNo}||'%'
AND tmmdt.SHORT_NO LIKE '%'|| #{shortNo}||'%'
AND tmmdt.DLVR_STORE_CD LIKE '%'|| #{dlvrStoreCd}||'%'
AND tmmdt.USE_YN = #{useYn}
ORDER BY tmmdt.MEMBR_ORGN_CD, tmmdt.MEMBR_NO

mapper.delete:
/*DlvrMapper.deleteDlvrTel*/
/*배달전화 삭제*/
DELETE FROM TB_MB_MEMBER_DLVR_TEL tmmdt
WHERE tmmdt.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmdt.MEMBR_NO = #{membrNo}
AND tmmdt.DLVR_TEL_SEQ = #{dlvrTelSeq}

mapper.update:
 /*DlvrMapper.updateDlvrTel*/
/*배달전화 저장*/
UPDATE TB_MB_MEMBER_DLVR_TEL tmmdt
SET tmmdt.TEL_NO = SBPENC.E(#{telNo})
, tmmdt.SHORT_NO = #{shortNo}
, tmmdt.USE_YN = #{useYn}
, tmmdt.MOD_ID = #{modId}
, tmmdt.MOD_DT = #{modDt}
WHERE tmmdt.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmdt.MEMBR_NO = #{membrNo}
AND tmmdt.DLVR_TEL_SEQ = #{dlvrTelSeq}
