mapper.select:
/* ChgBatchMapper.getMemberChgBatchList */ SELECT tmm.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, tmm.MEMBR_CLASS_CD, tmmc.MEMBR_CLASS_NM, tmm.MEMBR_CARD_NO, tmm.SHORT_NO, tmm.MEMBR_ORGN_CD, tmm.REG_STORE_CD, tms.STORE_NM AS REG_STORE_NM, tmm.POST_NO, SBPENC.D(tmm.ADDR) AS ADDR, SBPENC.D(tmm.ADDR_DTL) AS ADDR_DTL, SBPENC.D(tmm.TEL_NO) AS TEL_NO, tmm.EMAIL_ADDR, tmm.EMAIL_RECV_YN, tmm.BIRTHDAY, tmm.GENDR_FG, tmm.SMS_RECV_YN, tmm.WEDDING_YN, tmm.WEDDINGDAY, tmm.LUNAR_YN, tmm.USE_YN, tmm.MEMBR_NICKNM, tmm.MEMBER_ENG_NM, tmm.REMARK, tmmp.TOT_SAVE_POINT, tmmp.TOT_USE_POINT, tmmp.AVABL_POINT, tmmp.TOT_ADJ_POINT, tmmp.FIRST_SALE_DATE, tmmp.LAST_SALE_DATE, tmm.REG_DT FROM TB_MB_MEMBER tmm, TB_MB_MEMBER_CLASS tmmc, TB_MS_STORE tms,
TB_MB_MEMBER_POINT_STORE tmmps,
TB_MB_MEMBER_POINT tmmp WHERE tmm.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmc.MEMBR_ORGN_CD (+)= #{hqOfficeCd}
AND tmmc.MEMBR_ORGN_CD (+)= #{storeCd}
AND tmmc.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmm.REG_STORE_CD AND tmmp.MEMBR_NO (+)= tmm.MEMBR_NO AND tmmp.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD
AND tmmps.MEMBR_ORGN_CD (+) = tmm.REG_STORE_CD AND tmmps.CHG_MEMBR_ORGN_CD (+)= tmm.REG_STORE_CD AND tmmps.MEMBR_NO (+)= tmm.MEMBR_NO
AND tmmps.CHG_MEMBR_ORGN_CD = #{storeCd}
AND tmmps.CHG_MEMBR_ORGN_CD IN
#{item}
AND tmm.CST_CARD_USE_FG = #{cstCardUseFg}
AND tmm.MEMBR_CARD_NO LIKE '%'||#{membrCardNo}||'%'
AND tmm.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
AND SBPENC.D(tmm.TEL_NO) LIKE '%'||#{telNo}||'%'
AND tmm.EMAIL_ADDR LIKE '%'||#{emailAddr}||'%'
AND tmm.REG_STORE_CD IN
#{item}
AND tmm.MEMBR_CLASS_CD = #{membrClassCd}
AND tmm.GENDR_FG = #{gendrFg}
AND tmm.EMAIL_RECV_YN = #{emailRecvYn}
AND tmm.SMS_RECV_YN = #{smsRecvYn}
AND tmmp.POINT_ACC_CNT BETWEEN #{startSaveSale} AND #{endSaveSale}
AND tmmp.AVABL_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmm.REG_DT BETWEEN #{periodStartDate}||'000000' AND #{periodEndDate}||'999999'
AND tmmp.LAST_SALE_DATE BETWEEN #{periodStartDate} AND #{periodEndDate}
AND tmm.BIRTHDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.WEDDINGDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.USE_YN = #{useYn}
AND tmm.SHORT_NO = #{shortNo}
AND tmm.WEDDING_YN = #{weddingYn}
AND tmm.REG_STORE_CD = #{storeCd}
ORDER BY tmm.MEMBR_NO
