mapper.select:
  /* 회원엑셀 조회 */
SELECT tmm.MEMBR_NM, tmm.MEMBER_ENG_NM, tmm.MEMBR_CLASS_CD, tmm.REG_STORE_CD, tmm.GENDR_FG, tmm.MEMBR_CARD_NO, tmm.BIRTHDAY, tmm.WEDDING_YN, tmm.WEDDINGDAY, tmm.MEMBR_NO
FROM TB_MB_MEMBER tmm

mapper.delete:
          /* 회원엑셀 삭제 */
      DELETE FROM TB_MB_MEMBER tmm
      WHERE MEMBR_NO = ''
--       WHERE MEMBR_NO = #{membrNo}
