mapper.select:
/* RegistMapper.getRegistStore */
SELECT tms.STORE_CD AS VALUE,
       tms.STORE_NM AS NAME
  FROM TB_MS_STORE tms
 WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}

/* RegistMapper.getMemberClassList */
SELECT MEMBR_CLASS_CD AS VALUE,
       MEMBR_CLASS_NM AS NAME
  FROM TB_MB_MEMBER_CLASS
 WHERE USE_YN = 'Y'
   AND MEMBR_ORGN_CD = #{membrOrgnCd}



        AND REG_STORE_CD IS NULL




            AND REG_STORE_CD = #{storeCd}


            AND REG_STORE_CD IS NULL





 ORDER BY DEFLT_YN DESC

/* RegistMapper.getMemberList */
SELECT tmm.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM, tmm.MEMBR_CLASS_CD, tmmc.MEMBR_CLASS_NM, tmm.MEMBR_CARD_NO, tmm.SHORT_NO, tmm.MEMBR_ORGN_CD, tmm.REG_STORE_CD, tms.STORE_NM AS REG_STORE_NM, tmm.POST_NO, SBPENC.D(tmm.ADDR) AS ADDR, SBPENC.D(tmm.ADDR_DTL) AS ADDR_DTL, SBPENC.D(tmm.TEL_NO) AS TEL_NO, tmm.EMAIL_ADDR, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '072' AND NMCODE_CD = tmm.EMAIL_RECV_YN) AS EMAIL_RECV_YN, tmm.BIRTHDAY, tmm.GENDR_FG, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '072' AND NMCODE_CD = tmm.SMS_RECV_YN) AS SMS_RECV_YN, tmm.WEDDING_YN, tmm.WEDDINGDAY, tmm.LUNAR_YN, tmm.USE_YN, tmm.MEMBR_NICKNM, tmm.MEMBER_ENG_NM, tmm.REMARK, tmm.CST_CARD_USE_FG, tmm.CST_CARD_ISS_CNT, tmm.ORG_CST_CARD_NO, NVL(tmmp.TOT_SAVE_POINT, 0) AS TOT_SAVE_POINT, NVL(tmmp.TOT_USE_POINT, 0) AS TOT_USE_POINT, NVL(tmmp.AVABL_POINT, 0) AS AVABL_POINT, NVL(tmmp.TOT_ADJ_POINT, 0) AS TOT_ADJ_POINT, tmmp.FIRST_SALE_DATE, tmmp.LAST_SALE_DATE, tmmp.POINT_ACC_CNT AS POINT_ACC_CNT, tmmp.TOT_SALE_AMT AS TOT_SALE_AMT,
tmmps.TOT_SAVE_POINT AS STORE_TOT_SAVE_POINT, tmmps.TOT_USE_POINT AS STORE_TOT_USE_POINT, tmmps.TOT_ADJ_POINT AS STORE_TOT_ADJ_POINT, tmmps.FIRST_SALE_DATE AS STORE_FIRST_SALE_DATE , tmmps.LAST_SALE_DATE AS STORE_LAST_SALE_DATE , tmmps.POINT_ACC_CNT AS STORE_POINT_ACC_CNT, tmmps.TOT_SALE_AMT AS STORE_TOT_SALE_AMT,
tmm.REG_DT, '후불적용매장등록' AS POSTPAID_STORE, tmmcpr.POINT_SAVE_FG, tmmcpr.PAY01 AS MEMBER_CARD, tmmcpr.PAY02 AS MEMBER_CASH
FROM TB_MB_MEMBER tmm, TB_MB_MEMBER_CLASS tmmc, TB_MS_STORE tms, ( SELECT MEMBR_ORGN_CD, MEMBR_NO, MEMBR_CLASS_CD, POINT_SAVE_FG, NVL(PAY01, 0) AS PAY01, NVL(PAY02, 0) AS PAY02 FROM ( SELECT tmm.MEMBR_ORGN_CD, tmm.MEMBR_NO, tmm.MEMBR_CLASS_CD, tmmc.POINT_SAVE_FG, tmmcpr.PAY_CD, tmmcpr.ACC_RATE FROM TB_MB_MEMBER tmm, TB_MB_MEMBER_CLASS tmmc, TB_MB_MEMBER_CLASS_PAY_RATE tmmcpr WHERE 1=1 AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmc.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmc.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmcpr.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD ORDER BY tmm.MEMBR_NO ) PIVOT ( SUM(ACC_RATE) FOR PAY_CD IN ('01' AS PAY01, '02' AS PAY02) ) ORDER BY MEMBR_NO ) tmmcpr,
TB_MB_MEMBER_POINT_STORE tmmps,
TB_MB_MEMBER_POINT tmmp WHERE tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmc.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmc.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmm.REG_STORE_CD AND tmmp.MEMBR_NO (+)= tmm.MEMBR_NO AND tmmp.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmcpr.MEMBR_NO (+)= tmm.MEMBR_NO
AND tmmps.MEMBR_ORGN_CD (+) = tmm.REG_STORE_CD AND tmmps.CHG_MEMBR_ORGN_CD (+)= tmm.REG_STORE_CD AND tmmps.MEMBR_NO (+)= tmm.MEMBR_NO
AND tmmps.CHG_MEMBR_ORGN_CD = #{storeCd}
AND tmmps.CHG_MEMBR_ORGN_CD IN
#{item}
AND tmm.CST_CARD_USE_FG = #{cstCardUseFg}
AND tmm.MEMBR_CARD_NO LIKE '%'||#{membrCardNo}||'%'
AND tmm.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
AND tmm.MEMBER_ENG_NM LIKE '%'||#{membrEngNm}||'%'
AND SBPENC.D(tmm.TEL_NO) LIKE '%'||#{telNo}||'%'
AND tmm.EMAIL_ADDR LIKE '%'||#{emailAddr}||'%'
AND tmm.REG_STORE_CD IN
#{item}
AND tmm.MEMBR_CLASS_CD = #{membrClassCd}
AND tmm.GENDR_FG = #{gendrFg}
AND tmm.EMAIL_RECV_YN = #{emailRecvYn}
AND tmm.SMS_RECV_YN = #{smsRecvYn}
AND tmm.REG_DT BETWEEN #{periodStartDate}||'000000' AND #{periodEndDate}||'999999'
AND tmmp.LAST_SALE_DATE BETWEEN #{periodStartDate} AND #{periodEndDate}
AND tmm.BIRTHDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.WEDDINGDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.USE_YN = #{useYn}
AND tmmp.POINT_ACC_CNT BETWEEN #{startSaveSale} AND #{endSaveSale}
AND tmmp.TOT_SALE_AMT BETWEEN #{startSaveSale} AND #{endSaveSale}
AND tmmp.TOT_SAVE_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmmp.TOT_USE_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmmp.TOT_ADJ_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmmp.AVABL_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmm.SHORT_NO = #{shortNo}
AND tmm.WEDDING_YN = #{weddingYn}
AND tmm.REG_DT BETWEEN TO_CHAR(SYSDATE - 30, 'yyyyMMdd')||'000000' AND TO_CHAR(SYSDATE, 'yyyyMMdd')||'999999'
AND SBPENC.D(tmm.ADDR) LIKE '%'||#{addr}||'%'
AND tmmp.LAST_SALE_DATE >= TO_CHAR(ADD_MONTHS(SYSDATE, - #{marketingSmsGubun}), 'yyyyMMdd')
AND tmm.REG_STORE_CD = #{storeCd}
ORDER BY tmm.MEMBR_NO

/* RegistMapper.getMemberListExcel */ SELECT tmm.MEMBR_NO, RPAD(SUBSTR(SBPENC.D(tmm.MEMBR_NM),0,1), LENGTHB(CONVERT(SBPENC.D(tmm.MEMBR_NM), 'KO16MSWIN949')), '*') AS MEMBR_NM, tmm.MEMBR_CLASS_CD, tmmc.MEMBR_CLASS_NM, tmm.MEMBR_CARD_NO, tmm.SHORT_NO, tmm.MEMBR_ORGN_CD, tmm.REG_STORE_CD, tms.STORE_NM AS REG_STORE_NM, tmm.POST_NO, SBPENC.D(tmm.ADDR) AS ADDR, SBPENC.D(tmm.ADDR_DTL) AS ADDR_DTL, RPAD(SUBSTR(SBPENC.D(tmm.TEL_NO),0,7),11,'*') AS TEL_NO, tmm.EMAIL_ADDR, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '072' AND NMCODE_CD = tmm.EMAIL_RECV_YN) AS EMAIL_RECV_YN, tmm.BIRTHDAY, tmm.GENDR_FG, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '072' AND NMCODE_CD = tmm.SMS_RECV_YN) AS SMS_RECV_YN, tmm.WEDDING_YN, tmm.WEDDINGDAY, tmm.LUNAR_YN, tmm.USE_YN, tmm.MEMBR_NICKNM, tmm.MEMBER_ENG_NM, tmm.REMARK, tmm.CST_CARD_USE_FG, tmm.CST_CARD_ISS_CNT, tmm.ORG_CST_CARD_NO, tmmp.TOT_SAVE_POINT, tmmp.TOT_USE_POINT, tmmp.AVABL_POINT, tmmp.TOT_ADJ_POINT, tmmp.FIRST_SALE_DATE, tmmp.LAST_SALE_DATE, tmmp.POINT_ACC_CNT AS POINT_ACC_CNT, tmmp.TOT_SALE_AMT AS TOT_SALE_AMT,
tmmps.TOT_SAVE_POINT AS STORE_TOT_SAVE_POINT, tmmps.TOT_USE_POINT AS STORE_TOT_USE_POINT, tmmps.TOT_ADJ_POINT AS STORE_TOT_ADJ_POINT, tmmps.FIRST_SALE_DATE AS STORE_FIRST_SALE_DATE , tmmps.LAST_SALE_DATE AS STORE_LAST_SALE_DATE , tmmps.POINT_ACC_CNT AS STORE_POINT_ACC_CNT, tmmps.TOT_SALE_AMT AS STORE_TOT_SALE_AMT,
tmm.REG_DT, '후불적용매장등록' AS POSTPAID_STORE, tmmcpr.POINT_SAVE_FG, tmmcpr.PAY01 AS MEMBER_CARD, tmmcpr.PAY02 AS MEMBER_CASH FROM TB_MB_MEMBER tmm, TB_MB_MEMBER_CLASS tmmc, TB_MS_STORE tms, ( SELECT MEMBR_ORGN_CD, MEMBR_NO, MEMBR_CLASS_CD, POINT_SAVE_FG, NVL(PAY01, 0) AS PAY01, NVL(PAY02, 0) AS PAY02 FROM ( SELECT tmm.MEMBR_ORGN_CD, tmm.MEMBR_NO, tmm.MEMBR_CLASS_CD, tmmc.POINT_SAVE_FG, tmmcpr.PAY_CD, tmmcpr.ACC_RATE FROM TB_MB_MEMBER tmm, TB_MB_MEMBER_CLASS tmmc, TB_MB_MEMBER_CLASS_PAY_RATE tmmcpr WHERE 1=1 AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmc.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmc.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmcpr.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD ORDER BY tmm.MEMBR_NO ) PIVOT ( SUM(ACC_RATE) FOR PAY_CD IN ('01' AS PAY01, '02' AS PAY02) ) ORDER BY MEMBR_NO ) tmmcpr,
TB_MB_MEMBER_POINT_STORE tmmps,
TB_MB_MEMBER_POINT tmmp WHERE tmm.MEMBR_ORGN_CD = #{membrOrgnCd} AND tmmc.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmc.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tmm.REG_STORE_CD AND tmmp.MEMBR_NO (+)= tmm.MEMBR_NO AND tmmp.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_ORGN_CD (+)= tmm.MEMBR_ORGN_CD AND tmmcpr.MEMBR_CLASS_CD (+)= tmm.MEMBR_CLASS_CD AND tmmcpr.MEMBR_NO (+)= tmm.MEMBR_NO
AND tmmps.MEMBR_ORGN_CD (+) = tmm.REG_STORE_CD AND tmmps.CHG_MEMBR_ORGN_CD (+)= tmm.REG_STORE_CD AND tmmps.MEMBR_NO (+)= tmm.MEMBR_NO
AND tmmps.CHG_MEMBR_ORGN_CD = #{storeCd}
AND tmmps.CHG_MEMBR_ORGN_CD IN
#{item}
AND tmm.CST_CARD_USE_FG = #{cstCardUseFg}
AND tmm.MEMBR_CARD_NO LIKE '%'||#{membrCardNo}||'%'
AND tmm.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
AND tmm.MEMBER_ENG_NM LIKE '%'||#{membrEngNm}||'%'
AND SBPENC.D(tmm.TEL_NO) LIKE '%'||#{telNo}||'%'
AND tmm.EMAIL_ADDR LIKE '%'||#{emailAddr}||'%'
AND tmm.REG_STORE_CD IN
#{item}
AND tmm.MEMBR_CLASS_CD = #{membrClassCd}
AND tmm.GENDR_FG = #{gendrFg}
AND tmm.EMAIL_RECV_YN = #{emailRecvYn}
AND tmm.SMS_RECV_YN = #{smsRecvYn}
AND tmmp.LAST_SALE_DATE BETWEEN #{periodStartDate} AND #{periodEndDate}
AND tmm.REG_DT BETWEEN #{periodStartDate}||'000000' AND #{periodEndDate}||'999999'
AND tmm.BIRTHDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.WEDDINGDAY BETWEEN #{anvStartDate} AND #{anvEndDate}
AND tmm.USE_YN = #{useYn}
AND tmmp.POINT_ACC_CNT BETWEEN #{startSaveSale} AND #{endSaveSale}
AND tmmp.TOT_SALE_AMT BETWEEN #{startSaveSale} AND #{endSaveSale}
AND tmmp.TOT_SAVE_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
AND tmmp.TOT_USE_POINT BETWEEN #{startAvablPoint} AND #{endAvablPoint}
