    (
        #{membrOrgnCd},
        #{regStoreCd},
        #{membrNo},
        #{result, mode=OUT, jdbcType=VARCHAR}
    )
}

SELECT membr_orgn_cd ,membr_card_no ,membr_no ,reg_dt ,reg_id ,mod_dt ,mod_id ,cst_card_stat_fg ,old_cst_card_no ,cst_card_iss_fg ,iss_remark ,iss_orgn_cd ,iss_date ,(SELECT nmcode_nm FROM tb_cm_nmcode WHERE nmcode_grp_cd = '300' AND nmcode_cd = tmmc.cst_card_stat_fg) AS stat_fg_nm ,(SELECT nmcode_nm FROM tb_cm_nmcode WHERE nmcode_grp_cd = '301' AND nmcode_cd = tmmc.cst_card_iss_fg ) AS iss_fg_nm FROM TB_MB_MEMBER_CARD tmmc
AND MEMBR_NO = #{membrNo}
AND membr_orgn_cd = #{membrOrgnCd}
ORDER BY REG_DT DESC

mapper.insert:
/* RegistMapper.insertMembrDlvr */
INSERT INTO TB_MB_MEMBER_DLVR_ADDR tmmda (
    tmmda.MEMBR_ORGN_CD
    , tmmda.MEMBR_NO
    , tmmda.DLVR_ADDR_SEQ
    , tmmda.DLVR_STORE_CD
    , tmmda.DLVR_LZONE_CD
    , tmmda.DLVR_MZONE_CD
    , tmmda.ADDR
    , tmmda.ADDR_DTL
    , tmmda.LAST_DLVR_DATE
    , tmmda.TOT_DLVR_CNT
    , tmmda.USE_YN
    , tmmda.REG_DT
    , tmmda.REG_ID
    , tmmda.MOD_DT
    , tmmda.MOD_ID
    )
VALUES (
    #{membrOrgnCd}
    , #{membrNo}
    , (SELECT NVL(MAX(DLVR_ADDR_SEQ),0)+1 FROM TB_MB_MEMBER_DLVR_ADDR WHERE MEMBR_ORGN_CD = #{membrOrgnCd} AND MEMBR_NO = #{membrNo})
    , #{regStoreCd}
    , #{dlvrLzoneCd}
    , #{dlvrMzoneCd}
    , SBPENC.E((SELECT A.DLVR_LZONE_NM|| ' ' ||B.DLVR_MZONE_NM FROM TB_MS_DELIVERY_LZONE A,  TB_MS_DELIVERY_MZONE B WHERE A.DLVR_LZONE_CD = B.DLVR_LZONE_CD AND A.STORE_CD = B.STORE_CD AND A.STORE_CD = #{regStoreCd} AND B.DLVR_LZONE_CD = #{dlvrLzoneCd} AND B.DLVR_MZONE_CD = #{dlvrMzoneCd}))
    , SBPENC.E(#{addrDtl})
    , #{lastDlvrDate}
    , 0
    , #{useYn}
    , #{regDt}
    , #{regId}
    , #{modDt}
    , #{modId}
     )

mapper.update:
