DELETE FROM TB_MB_MEMBER_DLVR_ADDR
WHERE MEMBR_NO = #{membrNo}
AND MEMBR_ORGN_CD = #{membrOrgnCd}
AND DLVR_ADDR_SEQ = #{dlvrAddrSeq}

mapper.select:
/* RegistMapper.getMemberInfoBuyList */ SELECT tsdmp.SALE_DATE, tms.STORE_NM, tmp.PROD_NM, tsdmp.TOT_SALE_QTY, tsdmp.TOT_SALE_AMT, tsdmp.MEMBR_SAVE_POINT, tsdmp.MEMBR_USE_POINT FROM TB_SL_DAILY_MEMBR_PROD tsdmp, TB_MS_STORE tms, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdmp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tmp.STORE_CD = #{storeCd}
AND tms.HQ_OFFICE_CD (+)= tsdmp.HQ_OFFICE_CD AND tms.HQ_BRAND_CD (+)= tsdmp.HQ_BRAND_CD AND tms.STORE_CD (+)= tsdmp.STORE_CD AND tmp.HQ_BRAND_CD (+)= tsdmp.HQ_BRAND_CD AND tmp.STORE_CD (+)= tsdmp.STORE_CD AND tmp.PROD_CD (+)= tsdmp.PROD_CD

/* RegistMapper.getSearchMemberPointList */ SELECT tmmp.MEMBR_NO, tmm.MEMBR_NM, tmmp.AVABL_POINT FROM TB_MB_MEMBER_POINT tmmp, TB_MB_MEMBER tmm WHERE 1=1 AND tmmp.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmmp.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND tmm.MEMBR_NM LIKE '%'||#{membrNm}||'%'
AND tmm.MEMBR_ORGN_CD = tmmp.MEMBR_ORGN_CD AND tmm.MEMBR_NO = tmmp.MEMBR_NO ORDER BY tmmp.MEMBR_NO
