mapper.select:
/* MobileResveInfoMapper.getResveList */
SELECT tpri.HQ_OFFICE_CD, tpri.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tpri.SYS_DATE||tpri.SYS_DT, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AS REG_DT, tpri.RESVE_IN_FG, tpri.POS_NO, tpri.RESVE_NO, TO_CHAR(TO_DATE(tpri.RESVE_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS RESVE_DATE, TO_CHAR(TO_DATE(tpri.RESVE_TIME,'HH24:MI'),'HH24:MI') AS RESVE_TIME, tpri.RESVE_GUEST_NM, tpri.RESVE_GUEST_TEL_NO, tpri.RESVE_GUEST_CNT, tpri.RESVE_MEMO
FROM TB_PS_RESVE_INFO tpri, TB_MS_STORE tms WHERE tpri.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.HQ_OFFICE_CD = tpri.HQ_OFFICE_CD AND tms.STORE_CD = tpri.STORE_CD
AND tms.STORE_CD IN
#{item}
AND tpri.SYS_DATE BETWEEN #{startDate} AND #{endDate}
AND tpri.RESVE_DATE BETWEEN #{resveStartDate} AND #{resveEndDate}
AND tpri.RESVE_NO = #{resveNo}
AND tpri.RESVE_GUEST_NM = #{resveGuestNm}
ORDER BY RESVE_DATE DESC, RESVE_SEQ ASC
SELECT tpri.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tpri.SYS_DATE||tpri.SYS_DT, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AS REG_DT, tpri.RESVE_IN_FG, tpri.POS_NO, tpri.RESVE_NO, TO_CHAR(TO_DATE(tpri.RESVE_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS RESVE_DATE, TO_CHAR(TO_DATE(tpri.RESVE_TIME,'HH24:MI'),'HH24:MI') AS RESVE_TIME, tpri.RESVE_GUEST_NM, tpri.RESVE_GUEST_TEL_NO, tpri.RESVE_GUEST_CNT, tpri.RESVE_MEMO
FROM TB_PS_RESVE_INFO tpri, TB_MS_STORE tms WHERE tpri.HQ_OFFICE_CD = #{hqOfficeCd} AND tpri.STORE_CD IN
#{item}
AND tms.HQ_OFFICE_CD = tpri.HQ_OFFICE_CD AND tms.STORE_CD = tpri.STORE_CD
AND tpri.SYS_DATE BETWEEN #{startDate} AND #{endDate}
AND tpri.RESVE_DATE BETWEEN #{resveStartDate} AND #{resveEndDate}
AND tpri.RESVE_NO = #{resveNo}
AND tpri.RESVE_GUEST_NM = #{resveGuestNm}
ORDER BY RESVE_DATE DESC, RESVE_SEQ ASC
