/* MobileContentMapper.getMobileWeekSaleList */ SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'mm-dd') AS SALE_DATE, SUM(STORE_CNT) AS STORE_CNT, SUM(POS_CNT) AS POS_CNT FROM ( SELECT * FROM ( SELECT tsdt.SALE_DATE, tsdt.STORE_CNT, tmp.POS_CNT FROM ( SELECT SALE_DATE, COUNT(STORE_CD) AS STORE_CNT FROM TB_SL_DAILY_TOTAL GROUP BY SALE_DATE ) tsdt, ( SELECT SALE_DATE, COUNT(POS_NO) AS POS_CNT FROM TB_SL_DAILY_POS GROUP BY SALE_DATE ) tmp WHERE 1=1 AND tsdt.SALE_DATE BETWEEN TO_CHAR(SYSDATE-7, 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') AND tmp.SALE_DATE = tsdt.SALE_DATE ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level+1, 'YYYYMMDD') AS SALE_DATE, 0 AS STORE_CNT, 0 AS POS_CNT FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileContentMapper.getMobileWeekSaleAgencyList */ SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'mm-dd') AS SALE_DATE, SUM(STORE_CNT) AS STORE_CNT, SUM(POS_CNT) AS POS_CNT FROM ( SELECT * FROM ( SELECT tsdt.SALE_DATE, tsdt.STORE_CNT, tmp.POS_CNT FROM ( SELECT tsdt.SALE_DATE, COUNT(tsdt.STORE_CD) AS STORE_CNT FROM TB_SL_DAILY_TOTAL tsdt, TB_MS_STORE tms WHERE 1=1 AND tms.AGENCY_CD = #{orgnCd} AND tms.HQ_OFFICE_CD = tsdt.HQ_OFFICE_CD AND tms.STORE_CD = tsdt.STORE_CD GROUP BY tsdt.SALE_DATE ) tsdt, ( SELECT tmp.SALE_DATE, COUNT(tmp.POS_NO) AS POS_CNT FROM TB_SL_DAILY_POS tmp, TB_MS_STORE tms WHERE 1=1 AND tms.AGENCY_CD = #{orgnCd} AND tms.HQ_OFFICE_CD = tmp.HQ_OFFICE_CD AND tms.STORE_CD = tmp.STORE_CD GROUP BY SALE_DATE ) tmp WHERE 1=1 AND tsdt.SALE_DATE BETWEEN TO_CHAR(SYSDATE-7, 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') AND tmp.SALE_DATE = tsdt.SALE_DATE ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level+1, 'YYYYMMDD') AS SALE_DATE, 0 AS STORE_CNT, 0 AS POS_CNT FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileContentMapper.getMobileNoticeList */ SELECT twbi.BOARD_CD, twbi.BOARD_SEQ_NO, twbi.TITLE, TO_CHAR(TO_DATE(twbi.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS NOTICE_DATE FROM TB_WB_BOARD_INFO twbi, TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1 AND twbi.BOARD_CD = '01' AND twbi.USE_YN = 'Y' AND twbi.NOTICE_YN = 'Y' AND twbi.APPR_FG = '2'
AND twbi.START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND twbi.END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND twuidv01.ORGN_FG IN ('M','A') AND twbi.BOARD_SEQ_NO NOT IN ( SELECT BOARD_SEQ_NO FROM TB_WB_BOARD_READNG_HIST WHERE 1=1 AND BOARD_CD = '01' AND USER_ID = #{userId} GROUP BY BOARD_SEQ_NO ) AND twuidv01.USER_ID (+)= twbi.USER_ID ORDER BY twbi.REG_DT DESC

/* MobileContentMapper.getMobileNoticeAgencyList */ SELECT twbi.BOARD_CD, twbi.BOARD_SEQ_NO, twbi.TITLE, TO_CHAR(TO_DATE(twbi.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS NOTICE_DATE FROM TB_WB_BOARD_INFO twbi, TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1 AND twbi.BOARD_CD = '01' AND twbi.USE_YN = 'Y' AND twbi.NOTICE_YN = 'Y' AND twbi.APPR_FG = '2'
AND twbi.START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND twbi.END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND twuidv01.ORGN_FG IN ('M','A') AND twbi.BOARD_SEQ_NO NOT IN ( SELECT BOARD_SEQ_NO FROM TB_WB_BOARD_READNG_HIST WHERE 1=1 AND BOARD_CD = '01' AND USER_ID = #{userId} GROUP BY BOARD_SEQ_NO ) AND twuidv01.USER_ID (+)= twbi.USER_ID ORDER BY twbi.REG_DT DESC

/* MobileContentMapper.getMobileNoticeHqList */ SELECT twbi.BOARD_CD, twbi.BOARD_SEQ_NO, twbi.TITLE, TO_CHAR(TO_DATE(twbi.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS NOTICE_DATE FROM TB_WB_BOARD_INFO twbi, TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1 AND twbi.BOARD_CD = '01' AND twbi.USE_YN = 'Y' AND twbi.NOTICE_YN = 'Y' AND twbi.APPR_FG = '2'
AND twbi.START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND twbi.END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND twuidv01.ORGN_FG IN ('H') AND twuidv01.HQ_OFFICE_CD = #{hqOfficeCd} AND twbi.BOARD_SEQ_NO NOT IN ( SELECT BOARD_SEQ_NO FROM TB_WB_BOARD_READNG_HIST WHERE 1=1 AND BOARD_CD = '01' AND USER_ID = #{userId} GROUP BY BOARD_SEQ_NO ) AND twuidv01.USER_ID (+)= twbi.USER_ID ORDER BY twbi.REG_DT DESC

/* MobileContentMapper.getMobileNoticeStoreList */ SELECT twbi.BOARD_CD, twbi.BOARD_SEQ_NO, twbi.TITLE, TO_CHAR(TO_DATE(twbi.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS NOTICE_DATE FROM TB_WB_BOARD_INFO twbi, TB_WB_USER_INFO_DTL_V01 twuidv01 WHERE 1=1 AND twbi.BOARD_CD = '01' AND twbi.USE_YN = 'Y' AND twbi.NOTICE_YN = 'Y' AND twbi.APPR_FG = '2' AND ( ( twuidv01.ORGN_FG = 'H' AND twuidv01.HQ_OFFICE_CD = #{hqOfficeCd} AND ( ( (twbi.TARGET_FG = '2') AND (twbi.BOARD_SEQ_NO IN (SELECT BOARD_SEQ_NO FROM TB_WB_BOARD_PART_STORE WHERE BOARD_CD = '01' AND PART_ORGN_CD = #{storeCd} AND USE_YN = 'Y')) ) OR (twbi.TARGET_FG = '1') ) ) OR ( twuidv01.ORGN_FG = 'S' AND twuidv01.STORE_CD = #{storeCd} ) )
AND twbi.START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND twbi.END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND twbi.BOARD_SEQ_NO NOT IN ( SELECT BOARD_SEQ_NO FROM TB_WB_BOARD_READNG_HIST WHERE 1=1 AND BOARD_CD = '01' AND USER_ID = #{userId} GROUP BY BOARD_SEQ_NO ) AND twuidv01.USER_ID (+)= twbi.USER_ID ORDER BY twbi.REG_DT DESC

/* MobileContentMapper.getMobileWeekPosInstList */ SELECT TO_CHAR(TO_DATE(INST_INS_DT, 'YYYYMMDD'), 'mm-dd') AS INST_INS_DT, SUM(INST_STORE_CNT_NEW) AS INST_STORE_CNT_NEW, SUM(INST_STORE_CNT_RE) AS INST_STORE_CNT_RE FROM ( SELECT * FROM ( SELECT INST_INS_DT, SUM(INST_STORE_CNT_NEW) AS INST_STORE_CNT_NEW, SUM(INST_STORE_CNT_RE) AS INST_STORE_CNT_RE FROM ( SELECT TO_CHAR(TO_DATE(tci.MOD_DT, 'YYYYMMDDHH24MISS'), 'yyyyMMdd') AS INST_INS_DT, (CASE WHEN tci.INST_FG = '1' THEN 1 ELSE 0 END) AS INST_STORE_CNT_NEW, (CASE WHEN tci.INST_FG = '2' THEN 1 ELSE 0 END) AS INST_STORE_CNT_RE FROM TB_CM_INSTLL tci ) WHERE 1=1 AND INST_INS_DT BETWEEN TO_CHAR(SYSDATE-7, 'yyyyMMdd')AND TO_CHAR(SYSDATE, 'yyyyMMdd') GROUP BY INST_INS_DT ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level+1, 'YYYYMMDD') AS INST_INS_DT, 0 AS INST_STORE_CNT_NEW, 0 AS INST_STORE_CNT_RE FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY INST_INS_DT ORDER BY INST_INS_DT

/* MobileContentMapper.getMobileWeekPosInstAgencyList */ SELECT TO_CHAR(TO_DATE(INST_INS_DT, 'YYYYMMDD'), 'mm-dd') AS INST_INS_DT, SUM(INST_STORE_CNT_NEW) AS INST_STORE_CNT_NEW, SUM(INST_STORE_CNT_RE) AS INST_STORE_CNT_RE FROM ( SELECT * FROM ( SELECT INST_INS_DT, SUM(INST_STORE_CNT_NEW) AS INST_STORE_CNT_NEW, SUM(INST_STORE_CNT_RE) AS INST_STORE_CNT_RE FROM ( SELECT TO_CHAR(TO_DATE(tci.MOD_DT, 'YYYYMMDDHH24MISS'), 'yyyyMMdd') AS INST_INS_DT, (CASE WHEN tci.INST_FG = '1' THEN 1 ELSE 0 END) AS INST_STORE_CNT_NEW, (CASE WHEN tci.INST_FG = '2' THEN 1 ELSE 0 END) AS INST_STORE_CNT_RE FROM TB_CM_INSTLL tci WHERE 1=1 AND tci.AGENCY_CD = #{orgnCd} ) WHERE 1=1 AND INST_INS_DT BETWEEN TO_CHAR(SYSDATE-7, 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') GROUP BY INST_INS_DT ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level+1, 'YYYYMMDD') AS INST_INS_DT, 0 AS INST_STORE_CNT_NEW, 0 AS INST_STORE_CNT_RE FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY INST_INS_DT ORDER BY INST_INS_DT

/* MobileContentMapper.getMobileSaleWeekList */ SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'mm-dd') AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsdt.SALE_DATE, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}) AND tsdt.SALE_DATE BETWEEN TO_CHAR(SYSDATE-7, 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd') GROUP BY tsdt.SALE_DATE ) UNION ALL ( SELECT TO_CHAR(SYSDATE-level+1, 'YYYYMMDD') AS SALE_DATE, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= 7
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

