/* MobilePayInfoMapper.getMobileTemporaryList */ SELECT tsspt.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND STORE_CD = tsspt.STORE_CD) AS STORE_NM, tsspt.TEMPORARY_PAY_CD, tcn.NMCODE_NM AS TEMPORARY_PAY_NM, SUM(tsspt.SALE_AMT) AS SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt, TB_CM_NMCODE tcn WHERE 1=1 AND tsspt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND
tsspt.STORE_CD = #{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsspt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tcn.NMCODE_GRP_CD IN ('111', '802') AND tcn.NMCODE_CD = tsspt.TEMPORARY_PAY_CD GROUP BY tsspt.STORE_CD, tsspt.TEMPORARY_PAY_CD, tcn.NMCODE_NM ORDER BY tsspt.STORE_CD, tsspt.TEMPORARY_PAY_CD
