/* MobileDayOfWeekSale.getMobileDayOfWeekSaleList */ SELECT YOIL, YOIL2, COUNT(SALE_DATE) AS SALE_DATE, SUM(BILL_CNT) AS SALE_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, TRUNC(SUM(REAL_SALE_AMT) / COUNT(SALE_DATE)) AS DAY_AVR_SALE FROM ( SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYY-MM-DD'),'DY') AS YOIL, TO_CHAR(TO_DATE(SALE_DATE, 'YYYY-MM-DD'),'d') AS YOIL2, SALE_DATE, REAL_SALE_CNT AS BILL_CNT, REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} ) GROUP BY YOIL, YOIL2 ORDER BY YOIL2

/* MobileDayOfWeekSale.getMobileDayOfWeekSaleChartList */
SELECT  YOIL,
        TRUNC(SUM(REAL_SALE_AMT) / COUNT(SALE_DATE)) AS DAY_AVR_SALE
FROM
    (
    SELECT 	TO_CHAR(TO_DATE(SALE_DATE, 'YYYY-MM-DD'),'DY') AS YOIL,
            SALE_DATE,
            SALE_CNT,
            REAL_SALE_AMT
    FROM 	TB_SL_DAILY_TOTAL tsdt
    WHERE   HQ_OFFICE_CD = #{hqOfficeCd}


        AND STORE_CD IN

            #{item}


    AND     SALE_DATE BETWEEN #{startDate} AND #{endDate}
    )
GROUP BY YOIL
