/* MobileDaySaleMapper.getMobileDaySaleTotalList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(BILL_CNT) AS BILL_CNT, SUM(SALE_CNT) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, SUM(CARD_AMT) AS CARD_AMT, SUM(CASH_AMT) AS CASH_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(BILL_UPRC) AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, NVL(A.REAL_SALE_CNT, 0) AS REAL_SALE_CNT, NVL(A.RTN_SALE_CNT, 0) AS RTN_SALE_CNT, NVL(A.BILL_CNT, 0) AS BILL_CNT, NVL(A.SALE_CNT, 0) AS SALE_CNT, NVL(A.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(A.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(A.REAL_SALE_AMT, 0) AS REAL_SALE_AMT, ROUND(A.TOT_SALE_AMT / A.SALE_CNT) AS DAY_AVR_SALE, NVL(B.CARD_AMT, 0) AS CARD_AMT, NVL(B.CASH_AMT, 0) AS CASH_AMT, NVL(B.ETC_AMT, 0) AS ETC_AMT, ROUND(A.TOT_SALE_AMT / A.BILL_CNT) AS BILL_UPRC FROM ( SELECT tsdt.HQ_OFFICE_CD, SUM(tsdt.REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(tsdt.RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(tsdt.REAL_SALE_CNT) - SUM(tsdt.RTN_SALE_CNT) AS BILL_CNT, COUNT(tsdt.SALE_DATE) AS SALE_CNT, SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.HQ_OFFICE_CD ) A, ( SELECT tsdp.HQ_OFFICE_CD, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_CNT, 0 AS RTN_SALE_CNT, 0 AS BILL_CNT, 0 AS SALE_CNT, 0 AS TOT_SALE_AMT, 0 AS TOT_DC_AMT, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE, 0 AS CARD_AMT, 0 AS CASH_AMT, 0 AS ETC_AMT, 0 AS BILL_UPRC FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileDaySaleMapper.getMobileDaySalePayList */ SELECT A.PAY_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = A.PAY_CD) AS NM, A.TOT_SALE_QTY, A.REAL_SALE_AMT, NVL(ROUND(A.REAL_SALE_AMT / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT tsdp.PAY_CD, NVL(SUM(tsdp.TOT_SALE_QTY), 0) AS TOT_SALE_QTY, NVL(SUM(tsdp.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.PAY_CD ) A, ( SELECT NVL(SUM(tsdp.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.PAY_CD

/* MobileDaySaleMapper.getMobileDaySaleDcList */ SELECT A.DC_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = A.DC_CD) AS NM, A.TOT_SALE_QTY, A.REAL_SALE_AMT, NVL(ROUND(A.REAL_SALE_AMT / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT tsdd.DC_CD, NVL(SUM(tsdd.TOT_SALE_QTY), 0) AS TOT_SALE_QTY, NVL(SUM(tsdd.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdd.DC_CD ) A, ( SELECT NVL(SUM(tsdd.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.DC_CD

/* MobileDaySaleMapper.getMobileDaySaleShopList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC, '집계필요' AS TBL_AVR, '집계필요' AS TBL_CNT, '집계필요' AS TBL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS DAY_AVR_SALE FROM ( SELECT tsdp.HQ_OFFICE_CD, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsdp.SALE_DATE) AS SALE_CNT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdp.DLVR_PACK_FG = '1' GROUP BY tsdp.HQ_OFFICE_CD ) A, ( SELECT tsdp.HQ_OFFICE_CD, SUM(tsdp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdp.DLVR_PACK_FG = '1' GROUP BY tsdp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileDaySaleMapper.getMobileDaySaleDlvrList */ SELECT DLVR_PACK_FG, (CASE WHEN DLVR_PACK_FG = '1' THEN '내점' WHEN DLVR_PACK_FG = '2' THEN '배달' WHEN DLVR_PACK_FG = '3' THEN '포장' ELSE null END ) AS NM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, '집계필요' AS BILL_CNT, '집계필요' AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.DLVR_PACK_FG, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS DAY_AVR_SALE FROM ( SELECT tsdp.DLVR_PACK_FG, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsdp.SALE_DATE) AS SALE_CNT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.DLVR_PACK_FG ) A, ( SELECT tsdp.DLVR_PACK_FG, SUM(tsdp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.DLVR_PACK_FG ) B WHERE 1=1 AND B.DLVR_PACK_FG = A.DLVR_PACK_FG ) UNION ALL ( SELECT LPAD(LEVEL, 1, 0) AS DLVR_PACK_FG, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE FROM dual
CONNECT BY LEVEL <= 3
) ) GROUP BY DLVR_PACK_FG ORDER BY DLVR_PACK_FG

/* MobileDaySaleMapper.getMobileDaySaleDlvrChartList */ SELECT SALE_DATE, SUM(SHOP_REAL_SALE_AMT) AS SHOP_REAL_SALE_AMT, SUM(DLVR_REAL_SALE_AMT) AS DLVR_REAL_SALE_AMT, SUM(PACK_REAL_SALE_AMT) AS PACK_REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsdp.SALE_DATE, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '1' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '2' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '3' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.SALE_DATE, tsdp.DLVR_PACK_FG ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startDate}, 'YYYYMMDD')+level-1, 'YYYYMMDD') AS SALE_DATE, 0 AS SHOP_REAL_SALE_AMT, 0 AS DLVR_REAL_SALE_AMT, 0 AS PACK_REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') + 1
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileDaySaleMapper.getMobileDaySaleDlvrChart2List */ SELECT SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tsdp.SALE_DATE, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdp.DLVR_PACK_FG = #{gubun} GROUP BY tsdp.SALE_DATE ) GROUP BY SALE_DATE ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startDate}, 'YYYYMMDD')+level-1, 'YYYYMMDD') AS SALE_DATE, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') + 1
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileDaySaleMapper.getMobileDaySaleDtlList */ SELECT A.SALE_DATE, NVL(SUM(A.TOT_SALE_AMT), 0) AS TOT_SALE_AMT, NVL(SUM(A.TOT_DC_AMT), 0) AS TOT_DC_AMT, NVL(SUM(A.TOT_REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT, NVL(SUM(B.DLVR_REAL_SALE_AMT), 0) AS DLVR_REAL_SALE_AMT, '집계필요' AS DLVR_BILL_CNT, '집계필요' AS DLVR_BILL_UPRC, NVL(SUM(B.PACK_REAL_SALE_AMT), 0) AS PACK_REAL_SALE_AMT, '집계필요' AS PACK_BILL_CNT, '집계필요' AS PACK_BILL_UPRC, NVL(SUM(B.SHOP_REAL_SALE_AMT), 0) AS SHOP_REAL_SALE_AMT, '집계필요' AS SHOP_BILL_CNT, '집계필요' AS SHOP_BILL_UPRC, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC, NVL(SUM(C.CARD_AMT), 0) AS CARD_AMT, NVL(SUM(C.CASH_AMT), 0) AS CASH_AMT, NVL(SUM(C.ETC_AMT), 0) AS ETC_AMT, NVL(SUM(D.DC_AMT), 0) AS DC_AMT, NVL(SUM(D.COUPN_DC_AMT), 0) AS COUPN_DC_AMT, NVL(SUM(D.ETC_DC_AMT), 0) AS ETC_DC_AMT FROM ( SELECT tsdt.SALE_DATE, SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdt.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.SALE_DATE ) A, ( SELECT tsdp.SALE_DATE, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '2' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '3' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT, SUM(CASE WHEN tsdp.DLVR_PACK_FG = '1' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.SALE_DATE ) B, ( SELECT tsdp.SALE_DATE, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.SALE_DATE ) C, ( SELECT tsdd.SALE_DATE, SUM(CASE WHEN tsdd.DC_CD = '01' THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS DC_AMT, SUM(CASE WHEN tsdd.DC_CD = '02' THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS COUPN_DC_AMT, SUM(CASE WHEN tsdd.DC_CD NOT IN('01','02') THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS ETC_DC_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdd.SALE_DATE ) D WHERE 1=1 AND B.SALE_DATE (+)= A.SALE_DATE AND C.SALE_DATE (+)= A.SALE_DATE AND D.SALE_DATE (+)= A.SALE_DATE GROUP BY A.SALE_DATE ORDER BY A.SALE_DATE
