mapper.select:
/* MobileDaySaleMapper.getMobileDaySaleTotalList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(BILL_CNT) AS BILL_CNT, SUM(SALE_CNT) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, SUM(CARD_AMT) AS CARD_AMT, SUM(CASH_AMT) AS CASH_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(BILL_UPRC) AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, NVL(A.REAL_SALE_CNT, 0) AS REAL_SALE_CNT, NVL(A.RTN_SALE_CNT, 0) AS RTN_SALE_CNT, NVL(A.BILL_CNT, 0) AS BILL_CNT, NVL(A.SALE_CNT, 0) AS SALE_CNT, NVL(A.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(A.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(A.REAL_SALE_AMT, 0) AS REAL_SALE_AMT, DECODE(A.SALE_CNT, 0, 0, ROUND(A.REAL_SALE_AMT / A.SALE_CNT)) AS DAY_AVR_SALE, NVL(B.CARD_AMT, 0) AS CARD_AMT, NVL(B.CASH_AMT, 0) AS CASH_AMT, NVL(B.ETC_AMT, 0) AS ETC_AMT, DECODE(A.BILL_CNT, 0, 0, ROUND(A.REAL_SALE_AMT / A.BILL_CNT)) AS BILL_UPRC FROM ( SELECT tsdt.HQ_OFFICE_CD, SUM(tsdt.SALE_CNT) AS REAL_SALE_CNT, SUM(tsdt.RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(tsdt.REAL_SALE_CNT) AS BILL_CNT, COUNT(DISTINCT tsdt.SALE_DATE) AS SALE_CNT, SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.HQ_OFFICE_CD ) A, ( SELECT tsdp.HQ_OFFICE_CD, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_CNT, 0 AS RTN_SALE_CNT, 0 AS BILL_CNT, 0 AS SALE_CNT, 0 AS TOT_SALE_AMT, 0 AS TOT_DC_AMT, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE, 0 AS CARD_AMT, 0 AS CASH_AMT, 0 AS ETC_AMT, 0 AS BILL_UPRC FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileDaySaleMapper.getMobileDaySalePayList */ SELECT A.PAY_CD, C.PAY_NM AS NM, A.REAL_SALE_AMT, DECODE(B.TOT_REAL_SALE_AMT, 0, 0, NVL(ROUND(A.REAL_SALE_AMT / B.TOT_REAL_SALE_AMT * 100, 2), 0)) AS AMT_RATE, C.PAY_METHOD FROM ( SELECT CASE WHEN COL_NM = 'PAY_AMT_021' THEN SUBSTR(COL_NM, -3) ELSE SUBSTR(COL_NM, -2) END AS PAY_CD, COL_VAL AS REAL_SALE_AMT FROM ( /* 쿠폰은 결제수단이 아닌 할인내역으로 들어가므로 하드코딩으로 결제수단에 나오지 않도록 함. (05, 12) */ SELECT SUM(tsdt.PAY_AMT_01) AS PAY_AMT_01, SUM(tsdt.NON_CASH_AMT) AS PAY_AMT_02, SUM(tsdt.PAY_AMT_03) AS PAY_AMT_03, SUM(tsdt.PAY_AMT_04) AS PAY_AMT_04, /*SUM(tsdt.PAY_AMT_05) AS PAY_AMT_05,*/ SUM(tsdt.PAY_AMT_06) AS PAY_AMT_06, SUM(tsdt.PAY_AMT_07) AS PAY_AMT_07, SUM(tsdt.PAY_AMT_08) AS PAY_AMT_08, SUM(tsdt.PAY_AMT_09) AS PAY_AMT_09, SUM(tsdt.PAY_AMT_10) AS PAY_AMT_10, SUM(tsdt.PAY_AMT_11) AS PAY_AMT_11, /*SUM(tsdt.PAY_AMT_12) AS PAY_AMT_12,*/ SUM(tsdt.PAY_AMT_13) AS PAY_AMT_13, SUM(tsdt.PAY_AMT_14) AS PAY_AMT_14, SUM(tsdt.PAY_AMT_15) AS PAY_AMT_15, SUM(tsdt.PAY_AMT_16) AS PAY_AMT_16, SUM(tsdt.PAY_AMT_17) AS PAY_AMT_17, SUM(tsdt.PAY_AMT_18) AS PAY_AMT_18, SUM(tsdt.PAY_AMT_19) AS PAY_AMT_19, SUM(tsdt.APPR_CASH_AMT) AS PAY_AMT_021 FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) UNPIVOT ( COL_VAL FOR COL_NM IN ( PAY_AMT_01, PAY_AMT_02, PAY_AMT_03, PAY_AMT_04,/* PAY_AMT_05,*/ PAY_AMT_06, PAY_AMT_07, PAY_AMT_08, PAY_AMT_09, PAY_AMT_10, PAY_AMT_11, /*PAY_AMT_12, */PAY_AMT_13, PAY_AMT_14, PAY_AMT_15, PAY_AMT_16, PAY_AMT_17, PAY_AMT_18, PAY_AMT_19, PAY_AMT_021 ) ) ) A, ( SELECT NVL(SUM(tsdt.TOT_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B, ( SELECT tcn.NMCODE_CD AS PAY_CD, tcn.NMCODE_NM AS PAY_NM, tcn.NMCODE_ITEM_1 AS PAY_METHOD FROM TB_CM_NMCODE tcn WHERE 1=1 AND tcn.NMCODE_GRP_CD = '024' AND tcn.NMCODE_CD NOT IN ('05', '12') UNION ALL ( SELECT '021' AS PAY_CD, '현금영수증' AS PAY_NM, 'CASH_BILL' AS PAY_METHOD FROM dual ) ) C WHERE 1=1 AND C.PAY_CD (+)= A.PAY_CD ORDER BY A.PAY_CD

/* MobileDaySaleMapper.getMobileDaySaleDcList */ SELECT A.DC_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '041' AND NMCODE_CD = A.DC_CD) AS NM, A.REAL_SALE_AMT, DECODE(B.TOT_REAL_SALE_AMT, 0, 0, NVL(ROUND(A.REAL_SALE_AMT / B.TOT_REAL_SALE_AMT * 100, 2), 0)) AS AMT_RATE FROM ( SELECT tsdd.DC_CD, NVL(SUM(tsdd.TOT_DC_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdd.DC_CD ) A, ( SELECT NVL(SUM(tsdd.TOT_DC_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.DC_CD

/* MobileDaySaleMapper.getMobileDaySaleShopList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, SUM(TOT_GUEST_CNT) AS TOT_GUEST_CNT, SUM(GUEST_UPRC) AS GUEST_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, A.REAL_SALE_AMT, DECODE(A.SALE_CNT, 0, 0, ROUND(B.TOT_REAL_SALE_AMT / A.SALE_CNT)) AS DAY_AVR_SALE, C.TOT_GUEST_CNT, DECODE(C.TOT_GUEST_CNT, 0, 0, ROUND(A.REAL_SALE_AMT / C.TOT_GUEST_CNT)) AS GUEST_UPRC FROM ( SELECT tsdtd.HQ_OFFICE_CD, SUM(tsdtd.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(DISTINCT tsdtd.SALE_DATE) AS SALE_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdtd.DLVR_ORDER_FG = '1' GROUP BY tsdtd.HQ_OFFICE_CD ) A, ( SELECT tsdtd.HQ_OFFICE_CD, SUM(tsdtd.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdtd.DLVR_ORDER_FG = '1' GROUP BY tsdtd.HQ_OFFICE_CD ) B, ( SELECT tsdtd.HQ_OFFICE_CD, SUM(tsdtd.TOT_GUEST_CNT) AS TOT_GUEST_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdtd.HQ_OFFICE_CD ) C WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND C.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE, 0 AS TOT_GUEST_CNT, 0 AS GUEST_UPRC FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileDaySaleMapper.getMobileDaySaleDlvrList */ SELECT DLVR_ORDER_FG, (CASE WHEN DLVR_ORDER_FG = '1' THEN '내점' WHEN DLVR_ORDER_FG = '2' THEN '배달' WHEN DLVR_ORDER_FG = '3' THEN '포장' ELSE null END ) AS NM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(DAY_AVR_SALE) AS DAY_AVR_SALE, SUM(BILL_CNT) AS BILL_CNT, SUM(BILL_UPRC) AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.DLVR_ORDER_FG, A.REAL_SALE_AMT, DECODE(A.SALE_CNT, 0, 0, NVL(ROUND(B.TOT_REAL_SALE_AMT / A.SALE_CNT), 0)) AS DAY_AVR_SALE, A.BILL_CNT, DECODE(A.BILL_CNT, 0, 0, ROUND(A.REAL_SALE_AMT / A.BILL_CNT)) AS BILL_UPRC FROM ( SELECT DECODE(tsdtd.DLVR_ORDER_FG, '4', '3', tsdtd.DLVR_ORDER_FG) AS DLVR_ORDER_FG, SUM(tsdtd.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(DISTINCT tsdtd.SALE_DATE) AS SALE_CNT, SUM(tsdtd.REAL_SALE_CNT) AS BILL_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY DECODE(tsdtd.DLVR_ORDER_FG, '4', '3', tsdtd.DLVR_ORDER_FG) ) A, ( SELECT DECODE(tsdtd.DLVR_ORDER_FG, '4', '3', tsdtd.DLVR_ORDER_FG) AS DLVR_ORDER_FG, SUM(tsdtd.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY DECODE(tsdtd.DLVR_ORDER_FG, '4', '3', tsdtd.DLVR_ORDER_FG) ) B WHERE 1=1 AND B.DLVR_ORDER_FG = A.DLVR_ORDER_FG ) UNION ALL ( SELECT LPAD(LEVEL, 1, 0) AS DLVR_ORDER_FG, 0 AS REAL_SALE_AMT, 0 AS DAY_AVR_SALE, 0 AS BILL_CNT, 0 AS BILL_UPRC FROM dual
CONNECT BY LEVEL <= 3
) ) GROUP BY DLVR_ORDER_FG ORDER BY DLVR_ORDER_FG

/* MobileDaySaleMapper.getMobileDaySaleDlvrChartList */ SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE, SUM(SHOP_REAL_SALE_AMT) AS SHOP_REAL_SALE_AMT, SUM(DLVR_REAL_SALE_AMT) AS DLVR_REAL_SALE_AMT, SUM(PACK_REAL_SALE_AMT) AS PACK_REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsdtd.SALE_DATE, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '3' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdtd.SALE_DATE, tsdtd.DLVR_ORDER_FG ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startDate}, 'YYYYMMDD')+level-1, 'YYYYMMDD') AS SALE_DATE, 0 AS SHOP_REAL_SALE_AMT, 0 AS DLVR_REAL_SALE_AMT, 0 AS PACK_REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') + 1
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileDaySaleMapper.getMobileDaySaleDlvrChart2List */ SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT SALE_DATE, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tsdtd.SALE_DATE, SUM(tsdtd.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdtd.DLVR_ORDER_FG = #{gubun}
AND tsdtd.DLVR_ORDER_FG IN ('3', '4')
GROUP BY tsdtd.SALE_DATE ) GROUP BY SALE_DATE ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startDate}, 'YYYYMMDD')+level-1, 'YYYYMMDD') AS SALE_DATE, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endDate}, 'YYYYMMDD') - TO_DATE(#{startDate}, 'YYYYMMDD') + 1
) ) GROUP BY SALE_DATE ORDER BY SALE_DATE

/* MobileDaySaleMapper.getMobileDaySaleDtlList */ SELECT TO_CHAR(TO_DATE(A.SALE_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE, NVL(A.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(A.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(A.TOT_REAL_SALE_AMT, 0) AS TOT_REAL_SALE_AMT, NVL(B.SHOP_REAL_SALE_AMT, 0) AS SHOP_REAL_SALE_AMT, NVL(B.SHOP_BILL_CNT, 0) AS SHOP_BILL_CNT, DECODE(B.SHOP_BILL_CNT, 0, 0, NVL(ROUND(B.SHOP_REAL_SALE_AMT / B.SHOP_BILL_CNT), 0)) AS SHOP_BILL_UPRC, NVL(B.DLVR_REAL_SALE_AMT, 0) AS DLVR_REAL_SALE_AMT, NVL(B.DLVR_BILL_CNT, 0) AS DLVR_BILL_CNT, DECODE(B.DLVR_BILL_CNT, 0, 0, NVL(ROUND(B.DLVR_REAL_SALE_AMT / B.DLVR_BILL_CNT), 0)) AS DLVR_BILL_UPRC, NVL(B.PACK_REAL_SALE_AMT, 0) AS PACK_REAL_SALE_AMT, NVL(B.PACK_BILL_CNT, 0) AS PACK_BILL_CNT, DECODE(B.PACK_BILL_CNT, 0, 0, NVL(ROUND(B.PACK_REAL_SALE_AMT / B.PACK_BILL_CNT), 0)) AS PACK_BILL_UPRC, NVL(E.TOT_GUEST_CNT, 0) AS TOT_GUEST_CNT, DECODE(E.TOT_GUEST_CNT, 0, 0, NVL(ROUND(A.TOT_REAL_SALE_AMT / E.TOT_GUEST_CNT), 0)) AS GUEST_UPRC, NVL(C.CARD_AMT, 0) AS CARD_AMT, NVL(C.CASH_AMT, 0) AS CASH_AMT, NVL(C.ETC_AMT, 0) AS ETC_AMT, NVL(D.DC_AMT, 0) AS DC_AMT, NVL(D.COUPN_DC_AMT, 0) AS COUPN_DC_AMT, NVL(D.ETC_DC_AMT, 0) AS ETC_DC_AMT FROM ( SELECT tsdt.SALE_DATE, SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdt.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.SALE_DATE ) A, ( SELECT tsdtd.SALE_DATE, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '3' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS SHOP_BILL_CNT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS DLVR_BILL_CNT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '3' THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS PACK_BILL_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdtd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdtd.SALE_DATE ) B, ( SELECT tsdp.SALE_DATE, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.TOT_PAY_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdp.SALE_DATE ) C, ( SELECT tsdd.SALE_DATE, SUM(CASE WHEN tsdd.DC_CD = '01' THEN tsdd.TOT_DC_AMT ELSE 0 END) AS DC_AMT, SUM(CASE WHEN tsdd.DC_CD = '02' THEN tsdd.TOT_DC_AMT ELSE 0 END) AS COUPN_DC_AMT, SUM(CASE WHEN tsdd.DC_CD NOT IN('01','02') THEN tsdd.TOT_DC_AMT ELSE 0 END) AS ETC_DC_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdd.SALE_DATE ) D, ( SELECT tssh.SALE_DATE, SUM(tssh.TOT_GUEST_CNT) AS TOT_GUEST_CNT FROM TB_SL_SALE_HDR tssh WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssh.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tssh.SALE_DATE ) E WHERE 1=1 AND B.SALE_DATE (+)= A.SALE_DATE AND C.SALE_DATE (+)= A.SALE_DATE AND D.SALE_DATE (+)= A.SALE_DATE AND E.SALE_DATE (+)= A.SALE_DATE ORDER BY A.SALE_DATE
