/* MobileMonthSaleMapper.getMobileMonthSaleTotalList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(BILL_CNT) AS BILL_CNT, SUM(SALE_CNT) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, SUM(CARD_AMT) AS CARD_AMT, SUM(CASH_AMT) AS CASH_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(BILL_UPRC) AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, NVL(A.REAL_SALE_CNT, 0) AS REAL_SALE_CNT, NVL(A.RTN_SALE_CNT, 0) AS RTN_SALE_CNT, NVL(A.BILL_CNT, 0) AS BILL_CNT, NVL(A.SALE_CNT, 0) AS SALE_CNT, NVL(A.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(A.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(A.REAL_SALE_AMT, 0) AS REAL_SALE_AMT, ROUND(A.TOT_SALE_AMT / A.SALE_CNT) AS MONTH_AVR_SALE, NVL(B.CARD_AMT, 0) AS CARD_AMT, NVL(B.CASH_AMT, 0) AS CASH_AMT, NVL(B.ETC_AMT, 0) AS ETC_AMT, ROUND(A.TOT_SALE_AMT / A.BILL_CNT) AS BILL_UPRC FROM ( SELECT tsmt.HQ_OFFICE_CD, SUM(tsmt.REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(tsmt.RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(tsmt.REAL_SALE_CNT) - SUM(tsmt.RTN_SALE_CNT) AS BILL_CNT, COUNT(tsmt.SALE_YM) AS SALE_CNT, SUM(tsmt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsmt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmt.HQ_OFFICE_CD ) A, ( SELECT tsdp.HQ_OFFICE_CD, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' GROUP BY tsdp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_CNT, 0 AS RTN_SALE_CNT, 0 AS BILL_CNT, 0 AS SALE_CNT, 0 AS TOT_SALE_AMT, 0 AS TOT_DC_AMT, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE, 0 AS CARD_AMT, 0 AS CASH_AMT, 0 AS ETC_AMT, 0 AS BILL_UPRC FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileMonthSaleMapper.getMobileMonthSalePayList */ SELECT A.PAY_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = A.PAY_CD) AS NM, A.TOT_SALE_QTY, A.REAL_SALE_AMT, NVL(ROUND(A.REAL_SALE_AMT / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT tsdp.PAY_CD, NVL(SUM(tsdp.TOT_SALE_QTY), 0) AS TOT_SALE_QTY, NVL(SUM(tsdp.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' GROUP BY tsdp.PAY_CD ) A, ( SELECT NVL(SUM(tsdp.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' ) B ORDER BY A.PAY_CD

/* MobileMonthSaleMapper.getMobileMonthSaleDcList */ SELECT A.DC_CD, (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = A.DC_CD) AS NM, A.TOT_SALE_QTY, A.REAL_SALE_AMT, NVL(ROUND(A.REAL_SALE_AMT / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT tsdd.DC_CD, NVL(SUM(tsdd.TOT_SALE_QTY), 0) AS TOT_SALE_QTY, NVL(SUM(tsdd.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' GROUP BY tsdd.DC_CD ) A, ( SELECT NVL(SUM(tsdd.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' ) B ORDER BY A.DC_CD

/* MobileMonthSaleMapper.getMobileMonthSaleShopList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC, '집계필요' AS TBL_AVR, '집계필요' AS TBL_CNT, '집계필요' AS TBL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS MONTH_AVR_SALE FROM ( SELECT tsmp.HQ_OFFICE_CD, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsmp.SALE_YM) AS SALE_CNT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = '1' GROUP BY tsmp.HQ_OFFICE_CD ) A, ( SELECT tsmp.HQ_OFFICE_CD, SUM(tsmp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = '1' GROUP BY tsmp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrList */ SELECT DLVR_PACK_FG, (CASE WHEN DLVR_PACK_FG = '1' THEN '내점' WHEN DLVR_PACK_FG = '2' THEN '배달' WHEN DLVR_PACK_FG = '3' THEN '포장' ELSE null END ) AS NM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, '집계필요' AS BILL_CNT, '집계필요' AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.DLVR_PACK_FG, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS MONTH_AVR_SALE FROM ( SELECT tsmp.DLVR_PACK_FG, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsmp.SALE_YM) AS SALE_CNT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.DLVR_PACK_FG ) A, ( SELECT tsmp.DLVR_PACK_FG, SUM(tsmp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.DLVR_PACK_FG ) B WHERE 1=1 AND B.DLVR_PACK_FG = A.DLVR_PACK_FG ) UNION ALL ( SELECT LPAD(LEVEL, 1, 0) AS DLVR_PACK_FG, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE FROM dual
CONNECT BY LEVEL <= 3
) ) GROUP BY DLVR_PACK_FG ORDER BY DLVR_PACK_FG

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrChartList */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, SUM(SHOP_REAL_SALE_AMT) AS SHOP_REAL_SALE_AMT, SUM(DLVR_REAL_SALE_AMT) AS DLVR_REAL_SALE_AMT, SUM(PACK_REAL_SALE_AMT) AS PACK_REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsmp.SALE_YM, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '1' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '2' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '3' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.SALE_YM, tsmp.DLVR_PACK_FG ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM')+level-1, 'YYYYMM') AS SALE_YM, 0 AS SHOP_REAL_SALE_AMT, 0 AS DLVR_REAL_SALE_AMT, 0 AS PACK_REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endMonth}, 'YYYYMM') - TO_DATE(#{startMonth}, 'YYYYMM') + 1
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrChart2List */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT SALE_YM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tsmp.SALE_YM, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = #{gubun} GROUP BY tsmp.SALE_YM ) GROUP BY SALE_YM ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM')+level-1, 'YYYYMM') AS SALE_YM, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endMonth}, 'YYYYMM') - TO_DATE(#{startMonth}, 'YYYYMM') + 1
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* MobileMonthSaleMapper.getMobileMonthSaleDtlList */ SELECT TO_CHAR(TO_DATE(A.SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, NVL(SUM(A.TOT_SALE_AMT), 0) AS TOT_SALE_AMT, NVL(SUM(A.TOT_DC_AMT), 0) AS TOT_DC_AMT, NVL(SUM(A.TOT_REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT, NVL(SUM(B.DLVR_REAL_SALE_AMT), 0) AS DLVR_REAL_SALE_AMT, '집계필요' AS DLVR_BILL_CNT, '집계필요' AS DLVR_BILL_UPRC, NVL(SUM(B.PACK_REAL_SALE_AMT), 0) AS PACK_REAL_SALE_AMT, '집계필요' AS PACK_BILL_CNT, '집계필요' AS PACK_BILL_UPRC, NVL(SUM(B.SHOP_REAL_SALE_AMT), 0) AS SHOP_REAL_SALE_AMT, '집계필요' AS SHOP_BILL_CNT, '집계필요' AS SHOP_BILL_UPRC, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC, NVL(SUM(C.CARD_AMT), 0) AS CARD_AMT, NVL(SUM(C.CASH_AMT), 0) AS CASH_AMT, NVL(SUM(C.ETC_AMT), 0) AS ETC_AMT, NVL(SUM(D.DC_AMT), 0) AS DC_AMT, NVL(SUM(D.COUPN_DC_AMT), 0) AS COUPN_DC_AMT, NVL(SUM(D.ETC_DC_AMT), 0) AS ETC_DC_AMT FROM ( SELECT tsmt.SALE_YM, SUM(tsmt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsmt.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmt.SALE_YM ) A, ( SELECT tsmp.SALE_YM, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '2' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '3' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '1' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.SALE_YM ) B, ( SELECT SALE_YM, SUM(CARD_AMT) AS CARD_AMT, SUM(CASH_AMT) AS CASH_AMT, SUM(ETC_AMT) AS ETC_AMT FROM ( SELECT TO_CHAR(TO_DATE(tsdp.SALE_DATE, 'YYYYMMDD'), 'YYYYMM') SALE_YM, SUM(CASE WHEN tsdp.PAY_CD = '01' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CARD_AMT, SUM(CASE WHEN tsdp.PAY_CD = '02' THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN tsdp.PAY_CD NOT IN('01' , '02') THEN tsdp.REAL_SALE_AMT ELSE 0 END) AS ETC_AMT FROM TB_SL_DAILY_PAY tsdp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' GROUP BY tsdp.SALE_DATE ) GROUP BY SALE_YM ) C, ( SELECT SALE_YM, SUM(DC_AMT) AS DC_AMT, SUM(COUPN_DC_AMT) AS COUPN_DC_AMT, SUM(ETC_DC_AMT) AS ETC_DC_AMT FROM ( SELECT TO_CHAR(TO_DATE(tsdd.SALE_DATE, 'YYYYMMDD'), 'YYYYMM') SALE_YM, SUM(CASE WHEN tsdd.DC_CD = '01' THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS DC_AMT, SUM(CASE WHEN tsdd.DC_CD = '02' THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS COUPN_DC_AMT, SUM(CASE WHEN tsdd.DC_CD NOT IN('01','02') THEN tsdd.REAL_SALE_AMT ELSE 0 END) AS ETC_DC_AMT FROM TB_SL_DAILY_DC tsdd WHERE 1=1 AND tsdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdd.STORE_CD IN
#{item}
AND tsdd.SALE_DATE BETWEEN #{startMonth} || '01' AND #{endMonth} || '31' GROUP BY tsdd.SALE_DATE ) GROUP BY SALE_YM ) D WHERE 1=1 AND B.SALE_YM (+)= A.SALE_YM AND C.SALE_YM (+)= A.SALE_YM AND D.SALE_YM (+)= A.SALE_YM GROUP BY A.SALE_YM ORDER BY A.SALE_YM
