mapper.select:
/* MobileMonthSaleMapper.getMobileMonthSaleTotalList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(BILL_CNT) AS BILL_CNT, SUM(SALE_CNT) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, SUM(CARD_AMT) AS CARD_AMT, SUM(CASH_AMT) AS CASH_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(BILL_UPRC) AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, NVL(A.REAL_SALE_CNT, 0) AS REAL_SALE_CNT, NVL(A.RTN_SALE_CNT, 0) AS RTN_SALE_CNT, NVL(A.BILL_CNT, 0) AS BILL_CNT, NVL(A.SALE_CNT, 0) AS SALE_CNT, NVL(A.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(A.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(A.REAL_SALE_AMT, 0) AS REAL_SALE_AMT, ROUND(A.TOT_SALE_AMT / A.SALE_CNT) AS MONTH_AVR_SALE, NVL(A.CARD_AMT, 0) AS CARD_AMT, NVL(A.CASH_AMT, 0) AS CASH_AMT, NVL(A.ETC_AMT, 0) AS ETC_AMT, ROUND(A.TOT_SALE_AMT / A.BILL_CNT) AS BILL_UPRC FROM ( SELECT tsmt.HQ_OFFICE_CD, SUM(tsmt.REAL_SALE_CNT) AS REAL_SALE_CNT, SUM(tsmt.RTN_SALE_CNT) AS RTN_SALE_CNT, SUM(tsmt.REAL_SALE_CNT) - SUM(tsmt.RTN_SALE_CNT) AS BILL_CNT, COUNT(tsmt.SALE_YM) AS SALE_CNT, SUM(tsmt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsmt.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsmt.PAY_AMT_01) AS CARD_AMT, SUM(tsmt.PAY_AMT_02) AS CASH_AMT, SUM(tsmt.PAY_AMT_03) + SUM(tsmt.PAY_AMT_04) + SUM(tsmt.PAY_AMT_05) + SUM(tsmt.PAY_AMT_06) + SUM(tsmt.PAY_AMT_07) + SUM(tsmt.PAY_AMT_08) + SUM(tsmt.PAY_AMT_09) + SUM(tsmt.PAY_AMT_10) + SUM(tsmt.PAY_AMT_11) + SUM(tsmt.PAY_AMT_12) + SUM(tsmt.PAY_AMT_13) + SUM(tsmt.PAY_AMT_14) + SUM(tsmt.PAY_AMT_15) + SUM(tsmt.PAY_AMT_16) + SUM(tsmt.PAY_AMT_17) + SUM(tsmt.PAY_AMT_18) + SUM(tsmt.PAY_AMT_19) AS ETC_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmt.HQ_OFFICE_CD ) A ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_CNT, 0 AS RTN_SALE_CNT, 0 AS BILL_CNT, 0 AS SALE_CNT, 0 AS TOT_SALE_AMT, 0 AS TOT_DC_AMT, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE, 0 AS CARD_AMT, 0 AS CASH_AMT, 0 AS ETC_AMT, 0 AS BILL_UPRC FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileMonthSaleMapper.getMobileMonthSalePayList */ SELECT (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = PAY_CD) AS NM, REAL_SALE_AMT, AMT_RATE FROM ( SELECT SUBSTR(A.COL_NM, -2) AS PAY_CD, A.COL_VAL AS REAL_SALE_AMT, NVL(ROUND(A.COL_VAL / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT COL_NM, COL_VAL FROM ( SELECT SUM(tsmt.PAY_AMT_01) AS PAY_AMT_01, SUM(tsmt.PAY_AMT_02) AS PAY_AMT_02, SUM(tsmt.PAY_AMT_03) AS PAY_AMT_03, SUM(tsmt.PAY_AMT_04) AS PAY_AMT_04, SUM(tsmt.PAY_AMT_05) AS PAY_AMT_05, SUM(tsmt.PAY_AMT_06) AS PAY_AMT_06, SUM(tsmt.PAY_AMT_07) AS PAY_AMT_07, SUM(tsmt.PAY_AMT_08) AS PAY_AMT_08, SUM(tsmt.PAY_AMT_09) AS PAY_AMT_09, SUM(tsmt.PAY_AMT_10) AS PAY_AMT_10, SUM(tsmt.PAY_AMT_11) AS PAY_AMT_11, SUM(tsmt.PAY_AMT_12) AS PAY_AMT_12, SUM(tsmt.PAY_AMT_13) AS PAY_AMT_13, SUM(tsmt.PAY_AMT_14) AS PAY_AMT_14, SUM(tsmt.PAY_AMT_15) AS PAY_AMT_15, SUM(tsmt.PAY_AMT_16) AS PAY_AMT_16, SUM(tsmt.PAY_AMT_17) AS PAY_AMT_17, SUM(tsmt.PAY_AMT_18) AS PAY_AMT_18, SUM(tsmt.PAY_AMT_19) AS PAY_AMT_19 FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} ) UNPIVOT ( COL_VAL FOR COL_NM IN ( PAY_AMT_01, PAY_AMT_02, PAY_AMT_03, PAY_AMT_04, PAY_AMT_05, PAY_AMT_06, PAY_AMT_07, PAY_AMT_08, PAY_AMT_09, PAY_AMT_10, PAY_AMT_11, PAY_AMT_12, PAY_AMT_13, PAY_AMT_14, PAY_AMT_15, PAY_AMT_16, PAY_AMT_17, PAY_AMT_18, PAY_AMT_19 ) ) ) A, ( SELECT SUM(tsmt.PAY_AMT_01) + SUM(tsmt.PAY_AMT_02) + SUM(tsmt.PAY_AMT_03) + SUM(tsmt.PAY_AMT_04) + SUM(tsmt.PAY_AMT_05) + SUM(tsmt.PAY_AMT_06) + SUM(tsmt.PAY_AMT_07) + SUM(tsmt.PAY_AMT_08) + SUM(tsmt.PAY_AMT_09) + SUM(tsmt.PAY_AMT_10) + SUM(tsmt.PAY_AMT_11) + SUM(tsmt.PAY_AMT_12) + SUM(tsmt.PAY_AMT_13) + SUM(tsmt.PAY_AMT_14) + SUM(tsmt.PAY_AMT_15) + SUM(tsmt.PAY_AMT_16) + SUM(tsmt.PAY_AMT_17) + SUM(tsmt.PAY_AMT_18) + SUM(tsmt.PAY_AMT_19) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} ) B ) ORDER BY PAY_CD

/* MobileMonthSaleMapper.getMobileMonthSaleDcList */ SELECT (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '041' AND NMCODE_CD = DC_CD) AS NM, REAL_SALE_AMT, AMT_RATE FROM ( SELECT SUBSTR(A.COL_NM, -2) AS DC_CD, A.COL_VAL AS REAL_SALE_AMT, NVL(ROUND(A.COL_VAL / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT COL_NM, COL_VAL FROM ( SELECT SUM(tsmt.DC_AMT_01) AS DC_AMT_01, SUM(tsmt.DC_AMT_02) AS DC_AMT_02, SUM(tsmt.DC_AMT_03) AS DC_AMT_03, SUM(tsmt.DC_AMT_04) AS DC_AMT_04, SUM(tsmt.DC_AMT_05) AS DC_AMT_05, SUM(tsmt.DC_AMT_06) AS DC_AMT_06, SUM(tsmt.DC_AMT_07) AS DC_AMT_07, SUM(tsmt.DC_AMT_08) AS DC_AMT_08, SUM(tsmt.DC_AMT_09) AS DC_AMT_09, SUM(tsmt.DC_AMT_10) AS DC_AMT_10 FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} ) UNPIVOT ( COL_VAL FOR COL_NM IN ( DC_AMT_01, DC_AMT_02, DC_AMT_03, DC_AMT_04, DC_AMT_05, DC_AMT_06, DC_AMT_07, DC_AMT_08, DC_AMT_09, DC_AMT_10 ) ) ) A, ( SELECT SUM(tsmt.DC_AMT_01) + SUM(tsmt.DC_AMT_02) + SUM(tsmt.DC_AMT_03) + SUM(tsmt.DC_AMT_04) + SUM(tsmt.DC_AMT_05) + SUM(tsmt.DC_AMT_06) + SUM(tsmt.DC_AMT_07) + SUM(tsmt.DC_AMT_08) + SUM(tsmt.DC_AMT_09) + SUM(tsmt.DC_AMT_10) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} ) B ) ORDER BY DC_CD

/* MobileMonthSaleMapper.getMobileMonthSaleShopList */ SELECT HQ_OFFICE_CD, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC FROM ( SELECT * FROM ( SELECT A.HQ_OFFICE_CD, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS MONTH_AVR_SALE FROM ( SELECT tsmp.HQ_OFFICE_CD, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsmp.SALE_YM) AS SALE_CNT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = '1' GROUP BY tsmp.HQ_OFFICE_CD ) A, ( SELECT tsmp.HQ_OFFICE_CD, SUM(tsmp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = '1' GROUP BY tsmp.HQ_OFFICE_CD ) B WHERE 1=1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD ) UNION ALL ( SELECT #{hqOfficeCd} AS HQ_OFFICE_CD, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE FROM dual ) ) GROUP BY HQ_OFFICE_CD

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrList */ SELECT DLVR_PACK_FG, (CASE WHEN DLVR_PACK_FG = '1' THEN '내점' WHEN DLVR_PACK_FG = '2' THEN '배달' WHEN DLVR_PACK_FG = '3' THEN '포장' ELSE null END ) AS NM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(MONTH_AVR_SALE) AS MONTH_AVR_SALE, '집계필요' AS BILL_CNT, '집계필요' AS BILL_UPRC FROM ( SELECT * FROM ( SELECT A.DLVR_PACK_FG, A.REAL_SALE_AMT, NVL(ROUND(DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) / A.SALE_CNT), 0) AS MONTH_AVR_SALE FROM ( SELECT tsmp.DLVR_PACK_FG, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT, COUNT(tsmp.SALE_YM) AS SALE_CNT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.DLVR_PACK_FG ) A, ( SELECT tsmp.DLVR_PACK_FG, SUM(tsmp.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.DLVR_PACK_FG ) B WHERE 1=1 AND B.DLVR_PACK_FG = A.DLVR_PACK_FG ) UNION ALL ( SELECT LPAD(LEVEL, 1, 0) AS DLVR_PACK_FG, 0 AS REAL_SALE_AMT, 0 AS MONTH_AVR_SALE FROM dual
CONNECT BY LEVEL <= 3
) ) GROUP BY DLVR_PACK_FG ORDER BY DLVR_PACK_FG

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrChartList */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, SUM(SHOP_REAL_SALE_AMT) AS SHOP_REAL_SALE_AMT, SUM(DLVR_REAL_SALE_AMT) AS DLVR_REAL_SALE_AMT, SUM(PACK_REAL_SALE_AMT) AS PACK_REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT tsmp.SALE_YM, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '1' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '2' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '3' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.SALE_YM, tsmp.DLVR_PACK_FG ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM')+level-1, 'YYYYMM') AS SALE_YM, 0 AS SHOP_REAL_SALE_AMT, 0 AS DLVR_REAL_SALE_AMT, 0 AS PACK_REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endMonth}, 'YYYYMM') - TO_DATE(#{startMonth}, 'YYYYMM') + 1
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* MobileMonthSaleMapper.getMobileMonthSaleDlvrChart2List */ SELECT TO_CHAR(TO_DATE(SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT * FROM ( SELECT SALE_YM, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tsmp.SALE_YM, SUM(tsmp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmp.DLVR_PACK_FG = #{gubun} GROUP BY tsmp.SALE_YM ) GROUP BY SALE_YM ) UNION ALL ( SELECT TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM')+level-1, 'YYYYMM') AS SALE_YM, 0 AS REAL_SALE_AMT FROM dual
CONNECT BY LEVEL <= TO_DATE(#{endMonth}, 'YYYYMM') - TO_DATE(#{startMonth}, 'YYYYMM') + 1
) ) GROUP BY SALE_YM ORDER BY SALE_YM

/* MobileMonthSaleMapper.getMobileMonthSaleDtlList */ SELECT TO_CHAR(TO_DATE(A.SALE_YM, 'YYYYMM'), 'YYYY-MM') SALE_YM, NVL(SUM(A.TOT_SALE_AMT), 0) AS TOT_SALE_AMT, NVL(SUM(A.TOT_DC_AMT), 0) AS TOT_DC_AMT, NVL(SUM(A.TOT_REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT, NVL(SUM(B.DLVR_REAL_SALE_AMT), 0) AS DLVR_REAL_SALE_AMT, '집계필요' AS DLVR_BILL_CNT, '집계필요' AS DLVR_BILL_UPRC, NVL(SUM(B.PACK_REAL_SALE_AMT), 0) AS PACK_REAL_SALE_AMT, '집계필요' AS PACK_BILL_CNT, '집계필요' AS PACK_BILL_UPRC, NVL(SUM(B.SHOP_REAL_SALE_AMT), 0) AS SHOP_REAL_SALE_AMT, '집계필요' AS SHOP_BILL_CNT, '집계필요' AS SHOP_BILL_UPRC, '집계필요' AS TOT_GUEST_CNT, '집계필요' AS GUEST_UPRC, NVL(SUM(A.CARD_AMT), 0) AS CARD_AMT, NVL(SUM(A.CASH_AMT), 0) AS CASH_AMT, NVL(SUM(A.ETC_AMT), 0) AS ETC_AMT, NVL(SUM(A.DC_AMT), 0) AS DC_AMT, NVL(SUM(A.COUPN_DC_AMT), 0) AS COUPN_DC_AMT, NVL(SUM(A.ETC_DC_AMT), 0) AS ETC_DC_AMT FROM ( SELECT tsmt.SALE_YM, SUM(tsmt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsmt.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT, SUM(tsmt.PAY_AMT_01) AS CARD_AMT, SUM(tsmt.PAY_AMT_02) AS CASH_AMT, SUM(tsmt.PAY_AMT_03) + SUM(tsmt.PAY_AMT_04) + SUM(tsmt.PAY_AMT_05) + SUM(tsmt.PAY_AMT_06) + SUM(tsmt.PAY_AMT_07) + SUM(tsmt.PAY_AMT_08) + SUM(tsmt.PAY_AMT_09) + SUM(tsmt.PAY_AMT_10) + SUM(tsmt.PAY_AMT_11) + SUM(tsmt.PAY_AMT_12) + SUM(tsmt.PAY_AMT_13) + SUM(tsmt.PAY_AMT_14) + SUM(tsmt.PAY_AMT_15) + SUM(tsmt.PAY_AMT_16) + SUM(tsmt.PAY_AMT_17) + SUM(tsmt.PAY_AMT_18) + SUM(tsmt.PAY_AMT_19) AS ETC_AMT, SUM(tsmt.DC_AMT_01) AS DC_AMT, SUM(tsmt.DC_AMT_02) AS COUPN_DC_AMT, SUM(tsmt.DC_AMT_03) + SUM(tsmt.DC_AMT_04) + SUM(tsmt.DC_AMT_05) + SUM(tsmt.DC_AMT_06) + SUM(tsmt.DC_AMT_07) + SUM(tsmt.DC_AMT_08) + SUM(tsmt.DC_AMT_09) + SUM(tsmt.DC_AMT_10) AS ETC_DC_AMT FROM TB_SL_MONTHLY_TOTAL tsmt WHERE 1=1 AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.STORE_CD IN
#{item}
AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmt.SALE_YM ) A, ( SELECT tsmp.SALE_YM, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '2' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '3' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT, SUM(CASE WHEN tsmp.DLVR_PACK_FG = '1' THEN tsmp.REAL_SALE_AMT ELSE 0 END) AS SHOP_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp WHERE 1=1 AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.STORE_CD IN
#{item}
AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsmp.SALE_YM ) B WHERE 1=1 AND B.SALE_YM (+)= A.SALE_YM GROUP BY A.SALE_YM ORDER BY A.SALE_YM
