mapper.select:
/* USE : MobileOrderChannelSaleMapper.getOrderChannelSalePayList */ SELECT * FROM ( SELECT tsdto.DLVR_IN_FG, tcn.NMCODE_NM AS DLVR_IN_FG_NM, SUM(tsdto.REAL_SALE_AMT) AS REAL_SALE_AMT, ROUND(RATIO_TO_REPORT(SUM(tsdto.REAL_SALE_AMT)) OVER (), 4) * 100 AS REAL_SALE_PER FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto, (
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '112'
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT '1' AS NMCODE_CD, 'CID' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '113'
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT '1' AS NMCODE_CD, 'CID' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE STORE_CD = #{storeCd} AND NMCODE_GRP_CD = '113'
) tcn WHERE 1=1 AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdto.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdto.DLVR_IN_FG = tcn.NMCODE_CD GROUP BY tsdto.DLVR_IN_FG, tcn.NMCODE_NM ) B
ORDER BY TO_NUMBER(DLVR_IN_FG)
ORDER BY REAL_SALE_AMT

/* USE : MobileOrderChannelSaleMapper.getOrderChannelSaleDtlList */
     SELECT tsdto.SALE_DATE,
             SUM(tsdto.BILL_CNT) AS TOT_BILL_CNT,
             SUM(tsdto.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT,

                SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.BILL_CNT ELSE 0 END) AS BILL_CNT_${item},
                SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_${item}

       FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto
      WHERE 1=1
        AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd}

          AND tsdto.STORE_CD IN

              #{item}



          AND (
                  'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
              OR	tsdto.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
              )

        AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate}
      GROUP
         BY tsdto.SALE_DATE
