mapper.select:
/* USE : MobileProdSaleMapper.getProdSaleList */ SELECT B.* FROM ( SELECT A.PROD_CD, A.PROD_NM, A.TOT_SALE_QTY, A.REAL_SALE_AMT, ROUND(A.REAL_SALE_PER, 4) * 100 AS REAL_SALE_PER, ROW_NUMBER() OVER (ORDER BY A.REAL_SALE_AMT DESC) AS RNUM FROM ( SELECT tsdp.PROD_CD, tmp.PROD_NM, SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT, RATIO_TO_REPORT(SUM(tsdp.REAL_SALE_AMT)) OVER () AS REAL_SALE_PER FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp WHERE tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdp.SALE_DATE BETWEEN TO_CHAR(SYSDATE, 'yyyyMMdd') AND TO_CHAR(SYSDATE, 'yyyyMMdd')
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD GROUP BY tsdp.PROD_CD, tmp.PROD_NM ) A ORDER BY A.REAL_SALE_AMT DESC ) B WHERE 1=1
AND 4 > B.RNUM

/* USE : MobileProdSaleMapper.getStoreList */
SELECT  STORE_CD,
        STORE_NM
FROM    TB_MS_STORE
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND (
        'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
    OR	STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
    )
