mapper.select:
/* MobileRtnStatusMapper.getMobileRtnStatusList */
SELECT  SALE_DATE,
        SUM(RTN_SALE_CNT) AS RTN_SALE_CNT,
        SUM(RTN_REAL_SALE_AMT) AS RTN_REAL_SALE_AMT
FROM 	TB_SL_DAILY_TOTAL
WHERE	HQ_OFFICE_CD = #{hqOfficeCd}

    AND STORE_CD IN

        #{item}


AND		SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND 	RTN_SALE_CNT > 0 OR RTN_SALE_AMT > 0
GROUP BY SALE_DATE
ORDER BY SALE_DATE

/* MobileRtnStatusMapper.getMobileRtnStatusDtlList */ SELECT tssh.STORE_CD, tms.STORE_NM, tssh.POS_NO, tssh.BILL_NO, tme.EMP_NM, TO_CHAR(TO_DATE(tssh.BILL_DT,'yyyy/mm/dd hh24:mi:ss'),'mm-dd hh24:mi') AS BILL_DT, tssh.REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh, TB_MS_EMPLOYEE tme, TB_MS_STORE tms WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE = #{startDate} AND tssh.SALE_YN = 'N' AND tme.STORE_CD = tssh.STORE_CD AND tme.EMP_NO = tssh.EMP_NO AND tms.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tms.STORE_CD = tssh.STORE_CD ORDER BY tssh.STORE_CD, tssh.POS_NO, tssh.BILL_NO
